--
-- Cấu trúc bảng cho bảng `tbl_product`
--

CREATE TABLE `tbl_product` (
  `product_id` int(11) UNSIGNED NOT NULL,
  `category_id` int(11) UNSIGNED NOT NULL,
  `brand_id` int(11) UNSIGNED NOT NULL,
  `product_name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_quantity` varchar(50) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_sold` int(11) NOT NULL,
  `product_desc` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_content` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_price` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `price_cost` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_image` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_tags` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `product_status` int(11) NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_product`
--

INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_quantity`, `product_sold`, `product_desc`, `product_content`, `product_price`, `price_cost`, `product_image`, `product_tags`, `product_status`, `created_at`, `updated_at`) VALUES
(1, 1, 1, 'PS4 Pro 1TB Limited', '34', 16, '<p>Ngon bổ rẻ</p>', '<p>Ngon bổ rẻ</p>', '9000000', '8000000', '574bd8f21b82eeb3bdc7641944c652ea5139.jpeg', 'ps4,may-choi-game', 1, NULL, NULL),
(3, 2, 2, 'Xbox Pro AAA', '47', 3, '<p>Xbox One</p>', '<p><strong>M&aacute;y Xbox one x chất lượng cao</strong></p>\r\n\r\n<p><strong>Xbox One X m&aacute;y chơi game hiện đại, chất lượng tốt nhất tr&ecirc;n thị trường hiện nay.</strong></p>\r\n\r\n<p>L&agrave; d&ograve;ng m&aacute;y chơi game mạnh mẽ nhất hiện nay với cấu h&igrave;nh cao, chơi game cấu h&igrave;nh 4K. Bạn l&agrave; người y&ecirc;u th&iacute;ch m&ocirc;n game, l&agrave; game thủ đang t&igrave;m kiếm c&aacute;c sản phẩm m&aacute;y chơi game chất lượng cao? H&atilde;y c&ugrave;ng tham khảo b&agrave;i viết dưới đ&acirc;y để biết th&ecirc;m th&ocirc;ng tin về m&aacute;y Xbox One X.</p>\r\n\r\n<p><img alt=\"may-xbox-one-x-01.jpg\" src=\"https://muagame.vn/cdn/images/20171229/may-xbox-one-x-01.jpg\" title=\"may-xbox-one-x-01.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>M&aacute;y chơi game Xbox One X</em></p>\r\n\r\n<p><strong>Th&ocirc;ng tin cơ bản về m&aacute;y Xbox One X.</strong></p>\r\n\r\n<p>L&agrave; d&ograve;ng m&aacute;y chơi game mang thương hiệu Microsoft, l&agrave; sản phẩm m&aacute;y chơi game c&oacute; cấu h&igrave;nh mạnh mẽ nhất hiện nay, sản phẩm n&agrave;y sẽ mang đến cho c&aacute;c bạn những trải nghiệm tuyệt vời nhất với những b&agrave;i game y&ecirc;u th&iacute;ch. Ch&iacute;nh thức được tung ra thị trường v&agrave;o th&aacute;ng 11/2017 được nhiều người sử dụng đ&aacute;nh gi&aacute; rất tốt về sản phẩm n&agrave;y. Xbox One X l&agrave; d&ograve;ng sản phẩm c&oacute; khả năng cạnh tranh cao tr&ecirc;n thị trường m&aacute;y chơi game.</p>\r\n\r\n<p>Chi tiết cấu h&igrave;nh cơ bản của m&aacute;y.</p>\r\n\r\n<p>- H&atilde;ng sản xuất: Microsoft.</p>\r\n\r\n<p>- CPU: 8 nh&acirc;n t&ugrave;y chỉnh 2.3 GHZ.</p>\r\n\r\n<p>- GPU: 6 teraflop xung nhịp 1.17 GHz.</p>\r\n\r\n<p>- Ram GDDR5 12 GB.</p>\r\n\r\n<p>- C&ocirc;ng nghệ &acirc;m thanh Dolby Atmos.</p>\r\n\r\n<p>- Ổ đĩa Blu-Ray 4K.</p>\r\n\r\n<p>- Độ ph&acirc;n giải 4k@ 60fps.</p>\r\n\r\n<p>- Ổ cứng 500 GB/1 TB/2 TB 2,5&rdquo;.</p>\r\n\r\n<p><img alt=\"may-xbox-one-x-02.jpg\" src=\"https://muagame.vn/cdn/images/20171229/may-xbox-one-x-02.jpg\" title=\"may-xbox-one-x-02.jpg\" /></p>\r\n\r\n<p><em>Xbox One X được đ&aacute;nh gi&aacute; c&oacute; cấu h&igrave;nh mạnh mẽ nhất thế giới hiện nay</em></p>\r\n\r\n<p><strong>Đặc điểm nổi bật của Xbox One X.</strong></p>\r\n\r\n<p>- K&iacute;ch thước v&agrave; m&agrave;u sắc v&ocirc; c&ugrave;ng hầm hố, c&oacute; thể xử l&yacute; mượt m&agrave; c&aacute;c nội dung 4K/HDR c&ugrave;ng c&ocirc;ng nghệ &acirc;m thanh Dolby Atmos.</p>\r\n\r\n<p>- Thay v&igrave; sử dụng quạt tản nhiệt như c&aacute;c c&aacute;c d&ograve;ng sản phẩm m&aacute;y chơi game kh&aacute;c, Xbox One X được trang bị buồng tản nhiệt hơi nước, gi&uacute;p m&aacute;y kh&ocirc;ng bị qu&aacute; nhiệt khi phải xử l&yacute; c&aacute;c h&igrave;nh ảnh phức tạp. Đ&acirc;y l&agrave; một thiết kế nổi bật bởi việc sử dụng buồng tản nhiệt hơi nước trước đ&acirc;y chỉ c&oacute; ở c&aacute;c cỗ m&aacute;y PC cao cấp.</p>\r\n\r\n<p>- Bộ vi xử l&yacute; mạnh mẽ gi&uacute;p cho người chơi c&oacute; thể chơi mượt m&agrave; c&aacute;c tr&ograve; chơi trong 4K si&ecirc;u HD, chất lượng h&igrave;nh ảnh ti&ecirc;n tiến nhất.</p>\r\n\r\n<p>- C&oacute; thể kiểm tra lưu lượng sử dụng, đ&acirc;y l&agrave; t&iacute;nh năng đặc biệt chỉ c&oacute; ở tr&ecirc;n Xbox One X rất hữu &iacute;ch gi&uacute;p bạn c&oacute; thể kiểm tra được lưu lượng trong th&aacute;ng.</p>\r\n\r\n<p>- N&uacute;t Menu sử dụng dễ d&agrave;ng t&ugrave;y chỉnh nhanh c&aacute;c chức năng của hệ thống hoặc phần mềm ứng dụng, c&ugrave;ng với điều khiển thuận tiện.</p>\r\n\r\n<p><img alt=\"may-xbox-one-x-03.jpg\" src=\"https://muagame.vn/cdn/images/20171229/may-xbox-one-x-03.jpg\" title=\"may-xbox-one-x-03.jpg\" /></p>\r\n\r\n<p><em>Xbox One X với nhiều t&iacute;nh năng vượt trội</em></p>\r\n\r\n<p><strong>Một số lưu &yacute; khi sử dụng m&aacute;y chơi game Xbox One X.</strong></p>\r\n\r\n<p>C&acirc;n chỉnh m&agrave;n h&igrave;nh, m&agrave;n h&igrave;nh cần được c&acirc;n chỉnh để mang lại h&igrave;nh ảnh vừa mắt với bạn nhất, gi&uacute;p bạn c&oacute; thể chơi game trong thời gian d&agrave;i m&agrave; kh&ocirc;ng bị mỏi mắt. L&agrave;m tươi m&agrave;n h&igrave;nh khi nhận thấy dashboard hoạt động chậm hoặc bị đơ.</p>\r\n\r\n<p><img alt=\"may-xbox-one-x-04.jpg\" src=\"https://muagame.vn/cdn/images/20171229/may-xbox-one-x-04.jpg\" title=\"may-xbox-one-x-04.jpg\" /></p>\r\n\r\n<p><em>Cỗ m&aacute;y chơi game hiện đại</em></p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; một số th&ocirc;ng tin cơ bản về m&aacute;y Xbox One X, hy vọng mang đến cho c&aacute;c bạn những th&ocirc;ng tin tham khảo bổ &iacute;ch.</p>\r\n\r\n<p>Nếu bạn đang c&oacute; nhu cầu t&igrave;m hiểu hay t&igrave;m mua c&aacute;c sản phẩm m&aacute;y chơi game chất lượng tốt vui l&ograve;ng truy cập địa chỉ&nbsp;<a href=\"https://muagame.vn/\" target=\"_self\">https://muagame.vn/</a>&nbsp;để được t&igrave;m hiểu th&ecirc;m v&agrave; được tư vấn chi tiết, cụ thể về d&ograve;ng sản phẩm n&agrave;y, ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng.</p>', '10000000', '8000000', '51-qFScFCLL9051.jpg', 'xbox,may-choi-game', 1, NULL, NULL),
(4, 1, 1, 'Máy PlayStation Classic (PS One)', '100', 0, 'PlayStation là hệ máy đầu tiên trong dòng console và thiết bị chơi game cầm tay PlayStation.', '<p>PlayStation Classic 2018 gi&uacute;p người chơi c&oacute; lại những trải nghiệm tr&ecirc;n hệ m&aacute;y PlayStation 1.</p>\r\n\r\n<p dir=\"ltr\">Tiếp&nbsp;bước Nintendo,&nbsp;Sony&nbsp;giới thiệu phi&ecirc;n bản remake của PlayStation 1. Tương tự NES v&agrave; SNES Classic của Nintendo, PlayStation Classic sẽ được c&agrave;i sẵn 20 tựa game đủ mọi thể loại, bao gồm Final Fantasy VII, Tekken 3 v&agrave; Ridge Racer Type 4. Thiết bị sẽ được b&agrave;y b&aacute;n to&agrave;n cầu v&agrave;o ng&agrave;y 3/12 tới với gi&aacute;&nbsp;2.990.000 tại Việt Nam.</p>\r\n\r\n<p><img src=\"https://file.hstatic.net/1000154920/file/playstation_classic_5_1024x1024.jpg\" /></p>\r\n\r\n<p dir=\"ltr\">Ngo&agrave;i PlayStation Classic, kh&aacute;ch h&agrave;ng cũng sẽ nhận được hai bộ điều khiển PS1 classic. Sony c&oacute; vẻ nhỉnh hơn một ch&uacute;t so với Nintendo bởi NES v&agrave; SNES Classic hiện kh&aacute; hiếm linh kiện thay thế. Đ&acirc;y c&oacute; lẽ l&agrave; tin tốt đối với những ai khao kh&aacute;t t&igrave;m lại cảm gi&aacute;c tuổi thơ, khi một lần nữa c&oacute; thể trải nghiệm PlayStation cổ điển, chơi lại những tựa game lừng lẫy một thời.</p>\r\n\r\n<p dir=\"ltr\">Trọn bộ sản phẩm sẽ bao gồm:</p>\r\n\r\n<ul>\r\n	<li dir=\"ltr\">1 m&aacute;y PlayStation One Classic</li>\r\n	<li dir=\"ltr\">2 tay cầm PS1 Classic</li>\r\n	<li dir=\"ltr\">1 d&acirc;y c&aacute;p HDMI</li>\r\n	<li dir=\"ltr\">1 d&acirc;y c&aacute;p USB</li>\r\n</ul>\r\n\r\n<p><img src=\"https://file.hstatic.net/1000154920/file/playstation_classic_8_5879a0579d904cf3acfad3a11a77cf25_1024x1024.jpg\" /></p>\r\n\r\n<p dir=\"ltr\">Thời gian bảo h&agrave;nh: 12 th&aacute;ng tại c&aacute;c trung t&acirc;m bảo h&agrave;nh của SONY.</p>', '450000', '400000', '85ec5682903a4ba0e1660683a7a63d8894.jpg', 'play-station,ps1,sony,may-choi-game', 1, NULL, NULL),
(5, 1, 1, 'MÁY PS2 SET 5 – TÍCH HỢP HDD 500GB', '97', 3, 'Ra mắt lần đầu tại Nhật vào 4/3/2000. PlayStation 2 đã trở thành máy chơi game bán chạy nhất thế giới cho tới ngày nay với hơn 155 triệu đơn vị.', '<p>Năm 2020, một năm m&agrave; tất cả cộng đồng game thủ đều đang rất h&aacute;o hức với thế hệ console tiếp theo từ Sony. C&oacute; lẽ hầu hết game thủ đều đ&atilde; để sẵn cho m&igrave;nh một khoản tiền để c&oacute; thể mua được chiếc&nbsp;<a href=\"https://motgame.vn/playstation-5.tag\"><strong>PlayStation 5</strong></a>&nbsp;mới ngay khi n&oacute; ra mắt. PlayStation đ&atilde; trải qua một qu&atilde;ng thời gian d&agrave;i ph&aacute;t triển qua c&aacute;c thế hệ m&aacute;y chơi game, v&agrave; PS5 hứa hẹn sẽ l&agrave; chiếc m&aacute;y đem tới sự ho&agrave;n hảo hơn cả chiếc PS4.</p>\r\n\r\n<p><img alt=\"\" height=\"479\" src=\"https://upload.motgame.vn/wp-content/uploads/2020/03/Jarvis/playstation-2-he-may-chua-bao-gio-loi-thoi-01.jpg\" width=\"640\" /></p>\r\n\r\n<p>Tuy nhi&ecirc;n, ngay cả khi PS5 chuẩn bị ra mắt, đối với nhiều người th&igrave; thế hệ console để lại nhiều ấn tượng nhất v&agrave; th&agrave;nh c&ocirc;ng nhất chỉ c&oacute; PlayStation 2. Đặc biệt l&agrave; với những người sinh v&agrave;o thời kỳ 8x hay đầu 9x, chiếc PS2 đ&atilde; ghi lại rất nhiều kỷ niệm đẹp với họ. Chỉ cần nhắc tới th&ocirc;i l&agrave; một bầu trời k&yacute; ức hạnh ph&uacute;c hiện về trong t&acirc;m tr&iacute; họ.</p>\r\n\r\n<p>T&ocirc;i thấy ở xung quanh m&igrave;nh, d&ugrave; cả thế giới đang tập trung sự ch&uacute; &yacute; v&agrave;o PS4 hay PS5 th&igrave; vẫn c&oacute; nhiều người lựa chọn mua PlayStation 2 cũ sang tay. D&ugrave; đ&atilde; trải qua 20 năm, chiếc m&aacute;y chơi game thuộc thế hệ thứ 6 n&agrave;y vẫn l&agrave; lựa chọn tuyệt vời cho game thủ.</p>\r\n\r\n<h2><strong>Gi&aacute; th&agrave;nh rẻ cho nhu cầu chơi game &iacute;t</strong></h2>\r\n\r\n<p>Trong số tất cả những người được gọi l&agrave; c&oacute; sở th&iacute;ch hay đam m&ecirc; chơi game, kh&ocirc;ng phải ai cũng bắt kịp thị trường game hiện tại như n&acirc;ng cấp console, n&acirc;ng cấp m&agrave;n h&igrave;nh,&hellip; Đặc biệt l&agrave; những người ở độ tuổi đ&atilde; c&oacute; gia đ&igrave;nh, c&ocirc;ng việc, cuộc sống ổn định. Dường như họ đ&atilde; d&agrave;nh hết thời gian cuộc sống h&agrave;ng ng&agrave;y để kiếm tiền, chứ kh&ocirc;ng hề theo d&otilde;i hay bắt kịp xu hướng của ng&agrave;nh game hiện tại. Do đ&oacute;, để đ&aacute;p ứng nhu cầu chơi game kh&ocirc;ng qu&aacute; nhiều của m&igrave;nh, họ lựa chọn một chiếc PlayStation 2 cũ thay v&igrave; những console đời mới hơn.</p>\r\n\r\n<p><img alt=\"\" height=\"410\" src=\"https://upload.motgame.vn/wp-content/uploads/2020/03/Jarvis/playstation-2-he-may-chua-bao-gio-loi-thoi-02.png\" width=\"640\" /></p>\r\n\r\n<p>Hiện nay, những chiếc PS2 cũ c&oacute; gi&aacute; rất rẻ l&agrave; lựa chọn của đa số những người chạc tuổi 30 tới 40. Ở độ tuổi n&agrave;y, họ c&oacute; suy nghĩ chỉ cần mua một m&oacute;n đồ đ&aacute;p ứng đủ nhu cầu của m&igrave;nh thay v&igrave; cứ chạy theo xu thế. Những chiếc PS2 đ&atilde; gắn liền thời trai trẻ n&ecirc;n họ quen sử dụng ch&uacute;ng hơn l&agrave; chuyển sang PS3 hay PS4. B&ecirc;n cạnh đ&oacute;, hầu hết những người lựa chọn mua PS2 bởi họ chỉ chơi PES hoặc game đối kh&aacute;ng, chứ &iacute;t khi muốn trải nghiệm c&aacute;c tr&ograve; chơi thể loại h&agrave;nh động, phi&ecirc;u lưu, RPG,&hellip;</p>\r\n\r\n<p>Với PlayStation 2, bạn chẳng cần phải lo về việc download game hay mua game, thường cửa h&agrave;ng b&aacute;n sẽ free lu&ocirc;n cho bạn đĩa. Bạn cũng chẳng cần phải qu&aacute; quan t&acirc;m tới tốc độ mạng, độ ph&acirc;n giải m&agrave;n h&igrave;nh hay 30/60fps,&hellip; Nếu chỉ muốn chơi PES hay chơi một game đối kh&aacute;ng n&agrave;o đ&oacute; với bạn b&egrave;, người th&acirc;n của m&igrave;nh cho vui, PS2 l&agrave; lựa chọn tương đối ổn. Bạn chỉ cần cắm d&acirc;y v&agrave;o v&agrave; chơi l&agrave; xong.</p>\r\n\r\n<h2><strong>Ho&agrave;i niệm về một qu&aacute; khứ huy ho&agrave;ng</strong></h2>\r\n\r\n<p>Như t&ocirc;i đ&atilde; từng n&oacute;i, khi được cảm nhận hạnh ph&uacute;c từ sự ho&agrave;i niệm, con người c&oacute; thể sẵn s&agrave;ng mua m&oacute;n đồ đ&oacute; m&agrave; kh&ocirc;ng cần biết n&oacute; c&oacute; hợp xu thế hay kh&ocirc;ng. T&ocirc;i tin rằng bất cứ game thủ tầm tuổi 25 tới tr&ecirc;n 30 đều đ&atilde; được trải qua một khoảng thời gian hạnh ph&uacute;c với chiếc PS2. Đ&oacute; c&oacute; thể l&agrave; những ng&agrave;y trốn học, c&ugrave;ng anh em tụ tập đ&aacute; v&agrave;i trận PES, hay những lần trốn bố mẹ đi chơi V&otilde; Đ&agrave;i Đẫm M&aacute;u,&hellip; Kh&ocirc;ng một game thủ n&agrave;o c&oacute; thể qu&ecirc;n được những kỷ niệm đẹp trong cuộc đời chơi game của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" height=\"480\" src=\"https://upload.motgame.vn/wp-content/uploads/2020/03/Jarvis/playstation-2-he-may-chua-bao-gio-loi-thoi-03.jpg\" width=\"640\" /></p>\r\n\r\n<p>Do đ&oacute;, nhiều game thủ lựa chọn mua PlayStation 2 như một c&aacute;ch để họ lưu giữ những kỷ niệm đẹp, hay như một th&uacute; vui sưu tầm. Cũng đ&atilde; c&oacute; trường hợp kh&ocirc;ng &iacute;t người sẵn s&agrave;ng bỏ cả đống tiền chỉ để sở hữu một phi&ecirc;n bản PS2 qu&yacute; hiếm tr&ecirc;n thế giới n&agrave;y. T&ocirc;i thấy kể cả những người đ&atilde; sở hữu d&agrave;n PC khủng lẫn PS4 hay Xbox One, họ vẫn muốn c&oacute; th&ecirc;m chiếc PlayStation 2 với c&aacute;c loại đĩa game tr&ecirc;n kệ của m&igrave;nh.</p>\r\n\r\n<p>Đ&ocirc;i khi nh&igrave;n lại những chiếc m&aacute;y chơi game đời cũ đ&oacute;, ch&uacute;ng ta mới thấy thời trẻ của m&igrave;nh đ&atilde; từng vui vẻ v&agrave; hạnh ph&uacute;c thế n&agrave;o. Việc rất nhiều người mua PS2 chỉ l&agrave; thi thoảng, họ muốn được sống lại một trong những k&yacute; ức đẹp nhất của cuộc đời m&agrave; th&ocirc;i.</p>\r\n\r\n<h2><strong>PlayStation 2 &ndash; Thế hệ console chưa bao giờ hết thời</strong></h2>\r\n\r\n<p>PlayStation 2, hệ m&aacute;y ban đầu được coi l&agrave; kh&ocirc;ng mấy ấn tượng so với đối th&uacute; Dreamcast. Tuy nhi&ecirc;n, nhờ v&agrave;o những c&aacute;i đầu nhạy b&eacute;n ở Sony, chiếc m&aacute;y n&agrave;y đ&atilde; nhanh ch&oacute;ng vươn l&ecirc;n vị tr&iacute; số một. N&oacute; l&agrave; thế hệ m&aacute;y đầu ti&ecirc;n c&oacute; t&iacute;nh năng tương th&iacute;ch ngược, c&oacute; những tr&ograve; chơi hấp dẫn d&agrave;nh cho gia đ&igrave;nh v&agrave;o dịp nghỉ lễ. C&oacute; lẽ đ&oacute; ch&iacute;nh l&agrave; l&yacute; do v&igrave; sao những game thủ lu&ocirc;n c&oacute; kỷ niệm đẹp với chiếc m&aacute;y n&agrave;y, bởi những tr&ograve; chơi gia đ&igrave;nh đ&oacute; gi&uacute;p họ c&oacute; sự gắn kết với bố mẹ hay bạn b&egrave; của m&igrave;nh khi chơi.</p>\r\n\r\n<p><img alt=\"\" height=\"478\" src=\"https://upload.motgame.vn/wp-content/uploads/2020/03/Jarvis/playstation-2-he-may-chua-bao-gio-loi-thoi-04.jpg\" width=\"640\" /></p>\r\n\r\n<p>Giờ đ&acirc;y, t&ocirc;i vẫn nhớ như in cảm gi&aacute;c cho&aacute;ng ngợp khi lần đầu ti&ecirc;n được chi&ecirc;m ngưỡng đồ họa 3D hay những giờ ph&uacute;t chơi game c&ugrave;ng bạn b&egrave; m&igrave;nh. Ch&uacute;ng ta, những người đ&atilde; chứng kiến biết bao sự ph&aacute;t triển rồi suy t&agrave;n của c&aacute;c th&eacute; hệ m&aacute;y chơi game, nhưng c&oacute; lẽ PlayStation 2 l&agrave; vẫn sống m&atilde;i trong l&ograve;ng cộng đồng game thủ.</p>\r\n\r\n<p><a href=\"https://motgame.vn/playstation-5-va-ke-sach-an-binh-bat-dong-loi-hai-cua-sony.game\" target=\"_blank\"><img alt=\"PlayStation 5 và kế sách “án binh bất động” lợi hại của Sony\" src=\"https://i0.wp.com/upload.motgame.vn/wp-content/uploads/2020/02/Jarvis/playstation-5-va-ke-sach-an-binh-bat-dong-cua-sony.jpg?resize=260%2C150&amp;quality=100&amp;strip=all\" /></a></p>\r\n\r\n<p>Tới giờ, cộng đồng game thủ đ&atilde; qu&aacute; sốt ruột khi Sony &eacute;m h&agrave;ng PlayStation 5 qu&aacute; kỹ. Lượng th&ocirc;ng tin h&atilde;ng cung cấp cho b&aacute;o ch&iacute; v&agrave; giới truyền th&ocirc;ng rất &iacute;t, đa số những g&igrave; người h&acirc;m mộ b&agrave;n t&aacute;n chỉ l&agrave; những tin đồn. Điều quan trọng nhất m&agrave; giới game thủ...</p>\r\n\r\n<p>C&oacute; thể l&uacute;c đ&oacute; nhiều năm sau nữa, PS2 sẽ trở th&agrave;nh một m&oacute;n đồ qu&yacute; hiếm m&agrave; bất cứ ai cũng muốn sở hữu, hay cũng c&oacute; thể n&oacute; sẽ ho&agrave;n to&agrave;n biến mất tr&ecirc;n thế giới n&agrave;y. Nhưng t&ocirc;i tin rằng huyền thoại về thế hệ console thứ 6 n&agrave;y sẽ kh&ocirc;ng bao giờ kết th&uacute;c. T&ocirc;i thấy m&igrave;nh thật may mắn v&igrave; được sinh ra v&agrave; gắn b&oacute;, c&oacute; nhiều kỷ niệm đẹp với chiếc console n&agrave;y. Tuổi thơ của t&ocirc;i đ&atilde; qu&aacute; trọn vẹn với n&oacute; rồi.</p>', '4000000', '3700000', '1-61-150.png', 'play-station,may choi game,ps2', 1, NULL, NULL),
(6, 1, 1, 'Play Staion 3 (PS3)', '89', 11, 'Ra mắt ngày 11/11/2006 tại Nhật, PlayStation 3 đã bán được 80 triệu bản trên toàn thế giới và cạnh tranh trực tiếp với Xbox 360 và Nintendo Wii.', '<p>Th&ocirc;ng b&aacute;o</p>\r\n\r\n<p><img alt=\"8001375055_0be12360f1_z.jpg\" src=\"https://s3.cloud.cmctelecom.vn/tinhte1/2012/09/3196884_8001375055_0be12360f1_z.jpg\" />​</p>\r\n\r\n<p><br />\r\nBạn kh&ocirc;ng n&ecirc;n mong chờ PS4 ở thời điểm hiện tại, thay v&agrave;o đ&oacute; h&atilde;y nghĩ về một phi&ecirc;n bản n&acirc;ng cấp từ&nbsp;<a href=\"https://tinhte.vn/tags/ps3/\">PS3</a>&nbsp;v&agrave;&nbsp;<a href=\"https://tinhte.vn/tags/sony/\">Sony</a>&nbsp;h&ocirc;m nay đ&atilde; biến điều đ&oacute; th&agrave;nh hiện thực.&nbsp;<a href=\"https://tinhte.vn/tags/tokyo-game-show/\">Tokyo Game Show</a>&nbsp;l&agrave; sự kiện m&agrave; Sony đ&atilde; tr&igrave;nh l&agrave;ng chiếc m&aacute;y chơi game console PS3 phi&ecirc;n bản 2012 với thiết kế mới. Theo blog ch&iacute;nh thức của Sony, PS3 của năm 2012 nhỏ hơn 20% v&agrave; nhẹ hơn 25% so với bản cũ, n&oacute; đi c&ugrave;ng với một số thay đổi nhỏ trong thiết kế tổng thể của sản phẩm. B&ecirc;n cạnh m&agrave;u đen, Sony cũng sẽ b&aacute;n ra PS3 2012 m&agrave;u trắng nhưng sẽ xuất hiện muộn hơn bản m&agrave;u đen.<br />\r\n<br />\r\nPS3 mới sẽ gồm hai phi&ecirc;n bản về dung lượng bộ nhớ trong gồm 250GB v&agrave; 500GB. Tại Mỹ th&igrave; PS3 mới bản 250GB sẽ được b&aacute;n ra từ 25/9 tới với gi&aacute; 269USD, đi k&egrave;m với n&oacute; l&agrave; game Uncharted 3: Drake&#39;s Deception. Trong khi đ&oacute;, bản m&agrave;u trắng sẽ b&aacute;n ra từ 22/11 tại Nhật. Ri&ecirc;ng bản 500GB của PS3, Sony sẽ chỉ cung cấp t&ugrave;y chọn m&agrave;u đen, n&oacute; được b&aacute;n ra từ 30/10 với game Assassin&#39;s Creed III v&agrave; gi&aacute; l&agrave; 299USD.</p>\r\n\r\n<p><img alt=\"8001374977_d3a2866bc4_z.jpg\" src=\"https://s3.cloud.cmctelecom.vn/tinhte1/2012/09/3196883_8001374977_d3a2866bc4_z.jpg\" />​</p>\r\n\r\n<p>​</p>\r\n\r\n<p><img alt=\"8000836491_f267140f72_z.jpg\" src=\"https://s3.cloud.cmctelecom.vn/tinhte1/2012/09/3196882_8000836491_f267140f72_z.jpg\" /></p>', '3000000', '2600000', '1200px-Sony-PlayStation-3-2001A-wController-L76.jpg', 'may-choi-game,play -station,ps3', 1, NULL, NULL),
(7, 1, 1, 'PlayStation 3 Super Slim', '94', 6, 'PlayStation 3 Super Slim vào 9/2012. Đánh dấu lần đầu tiên Sony thay đổi thiết kế cho máy Slim.', '<p>Trong l&uacute;c chờ ph&aacute;t triển ho&agrave;n thiện chiếc&nbsp;<a href=\"https://tinhte.vn/tags/playstation/\">PlayStation</a>&nbsp;4,&nbsp;<a href=\"https://tinhte.vn/tags/sony/\">Sony</a>&nbsp;tiếp tục l&agrave;m mới thế hệ&nbsp;<a href=\"https://tinhte.vn/tags/console/\">console</a>&nbsp;<a href=\"https://tinhte.vn/tags/ps3/\">PS3</a>&nbsp;đ&atilde; 6 năm tuổi của m&igrave;nh với phi&ecirc;n bản&nbsp;<a href=\"https://tinhte.vn/tags/super-slim/\">Super Slim</a>, được giới thiệu hồi cuối th&aacute;ng 9 vừa qua v&agrave; bắt đầu b&aacute;n ra trong đầu th&aacute;ng 10 n&agrave;y.&nbsp;<a href=\"https://tinhte.vn/tags/ps3-super-slim/\">PS3 Super Slim</a>&nbsp;kh&ocirc;ng c&oacute; điểm mới về cấu h&igrave;nh so với PS3 đời đầu v&agrave;&nbsp;<a href=\"https://tinhte.vn/tags/ps3-slim/\">PS3 slim</a>, n&oacute; vẫn sử dụng BXL Cell 3,2GHz của IBM, chip đồ họa 550MHz của nVIDIA c&ugrave;ng bộ nhớ RAM 256MB.<br />\r\n[prebreak][/prebreak]<br />\r\nKh&ocirc;ng c&oacute; sự n&acirc;ng cấp về cấu h&igrave;nh, do đ&oacute; sự thay đổi của Super Slim so với c&aacute;c m&aacute;y PS3 đời cũ ch&iacute;nh l&agrave; ở thiết kế. Giờ đ&acirc;y PS3 mới đ&atilde; nhỏ gọn v&agrave; nhẹ hơn rất nhiều, với k&iacute;ch thước nhỏ hơn PS3 slim đến 25% c&ugrave;ng trọng lượng chỉ 2,1kg (so với 5kg của PS3 Slim). Một điểm nữa l&agrave; Sony cũng đ&atilde; thiết kế lại ổ đĩa Bluray cho Super Slim, n&oacute; kh&ocirc;ng c&ograve;n sử dụng ổ đĩa dạy slot load nữa m&agrave; d&ugrave;ng một cơ chế trượt để mở mới, c&oacute; thể đ&oacute;ng mở kh&ocirc;ng cần bật nguồn, kiểu ổ đĩa n&agrave;y tương tự với ổ đĩa tr&ecirc;n m&aacute;y PS2 slim trước đ&acirc;y. V&agrave; một điểm đặc biệt cuối c&ugrave;ng l&agrave; giờ đ&acirc;y PS3 Super Slim chỉ c&oacute; c&ocirc;ng suất ti&ecirc;u thụ l&agrave; 190W, so với 250W của bản Slim trước đ&acirc;y.<br />\r\n<br />\r\nHiện tại m&aacute;y PS3 Super Slim đ&atilde; c&oacute; b&aacute;n tại c&aacute;c cửa h&agrave;ng chuy&ecirc;n&nbsp;<a href=\"https://tinhte.vn/tags/may-choi-game/\">m&aacute;y chơi game</a>&nbsp;console ở Việt Nam. Bản d&ugrave;ng ổ cứng 250GB c&oacute; gi&aacute; 7.680.000 đồng, bản 500GB gi&aacute; 7.980.000 đồng, trong khi bản Slim cũ c&oacute; gi&aacute; 6.880.000 đồng cho bản ổ cứng 320GB.<br />\r\n<br />\r\n<em>C&aacute;m ơn Halo shop đ&atilde; hỗ trợ sản phẩm cho b&agrave;i viết n&agrave;y.</em><br />\r\n<br />\r\n<strong><em>T&oacute;m tắt cầu h&igrave;nh PS3 Super Slim:</em></strong></p>\r\n\r\n<ul>\r\n	<li>BXL IBM Cell (7 nh&acirc;n), xung nhịp 3,2GHz</li>\r\n	<li>GPU nVIDIA xung nhịp 550MHz</li>\r\n	<li>Video RAM 256MB</li>\r\n	<li>Bộ nhớ RAM 256MB</li>\r\n	<li>Sử dụng ổ cứng 2.5&quot;</li>\r\n	<li>Kết nối: WiFi b/g, 2 USB 2.0, HDMI 1.3a, LAN, composite...</li>\r\n	<li>Định dạng video: AVI, DivX, h.264 / AVC, Motion JPEG, MPEG-1, MPEG-2, MPEG-4, VC-1, WMV, XviD (480i/p đến 1080i/p)</li>\r\n	<li>Định dạng ảnh hỗ trợ: BMP, GIF, JPEG, PNG, TIFF</li>\r\n	<li>Chuẩn &acirc;m thanh hỗ trợ (7.1 k&ecirc;nh): Dolby Digital, Dolby Digital Plus, Dolby TrueHD, DTS, DTS-HD High Resolution, DTS-HD Master Audio, PCM</li>\r\n	<li>Phụ kiện đi k&egrave;m: c&aacute;p A/V, c&aacute;p nguồn, tay cầm Dual Shock.</li>\r\n</ul>', '6600000', '6000000', '1732155may_ps3_16.jpg', 'play-station,may-choi-game,ps3-super-slim', 1, NULL, NULL),
(8, 1, 1, 'Play Station 4 Slim', '100', 0, 'Ra mắt PS4 15/11/2013 tại Bắc Mỹ và bán được 1 triệu bản chỉ trong / ngày. Trở thành chiếc console bán chạy nhất trong 24h.', '<p><a href=\"javascript: void(0)\"><img alt=\"\" height=\"493\" sizes=\"(max-width: 877px) 100vw, 877px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/5b44d4908a632806d6304b2480d1a3b0.jpg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/5b44d4908a632806d6304b2480d1a3b0.jpg 877w, https://umbala.vn/wp-content/uploads/2019/03/5b44d4908a632806d6304b2480d1a3b0-300x169.jpg 300w, https://umbala.vn/wp-content/uploads/2019/03/5b44d4908a632806d6304b2480d1a3b0-768x432.jpg 768w, https://umbala.vn/wp-content/uploads/2019/03/5b44d4908a632806d6304b2480d1a3b0-550x309.jpg 550w\" width=\"877\" /></a></p>\r\n\r\n<p>11MAR</p>\r\n\r\n<p>Chiếc m&aacute;y PlayStation 4 mở ra một c&aacute;nh cửa cho việc giải tr&iacute; v&agrave; trải nghiệm những c&ocirc;ng nghệ tuyệt đỉnh, những chiều s&acirc;u trong việc giao tiếp tr&ecirc;n mạng d&agrave;nh cho game thủ. Với hơn 180 games đẳng cấp đang được ph&aacute;t triển. PS4 tập trung v&agrave;o những điều tuyệt vời, những tựa game bom tấn v&agrave; những s&aacute;ng tạo nổi bật từ cảm hứng của c&aacute;c nh&agrave; ph&aacute;t triển &ndash; Gamer Focus.</p>\r\n\r\n<p><strong>Thiết kế v&agrave; điều khiển</strong></p>\r\n\r\n<p>PS4 Slim sở hữu thiết kế h&igrave;nh b&igrave;nh h&agrave;nh, ph&iacute;a trước sau được cắt v&aacute;t mang phong c&aacute;ch &ldquo;phi thuyền kh&ocirc;ng gian&rdquo; tương tự PS4, nhưng mỏng, gọn v&agrave; mềm mại hơn. Theo đ&oacute;, c&aacute;c g&oacute;c được bo tr&ograve;n thay v&igrave; sắc cạnh. Chiều ngang PS4 Slim đ&atilde; được cắt ngắn lại v&agrave; độ d&agrave;y m&aacute;y chỉ c&ograve;n 3/4 so với PS4.&nbsp;</p>\r\n\r\n<p>To&agrave;n bộ th&acirc;n m&aacute;y l&agrave; nhựa nh&aacute;m chống b&aacute;m v&acirc;n tay thay v&igrave; nh&aacute;m b&oacute;ng kết hợp tr&ecirc;n PS4. Mặt trước liền mạch kh&ocirc;ng c&oacute; r&atilde;nh chia. Kh&ocirc;ng sử dụng ph&iacute;m cảm ứng, n&uacute;t nguồn (Power) v&agrave; lấy đĩa (Eject) tr&ecirc;n PS4 Slim quay trở lại với phong c&aacute;ch ph&iacute;m bấm vật l&yacute; t&iacute;ch hợp đ&egrave;n LED. Hai cổng USB 3.1 được bố tr&iacute; c&aacute;ch xa nhau &ndash; một chi tiết nhỏ nhưng đ&aacute;ng gi&aacute; bởi điều đ&oacute; gi&uacute;p việc cắm c&aacute;p tay cầm hay USB tiện lợi hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://umbala.vn/wp-content/uploads/2019/03/ps4_slim_main_0.jpg\"><img alt=\"\" height=\"540\" sizes=\"(max-width: 960px) 100vw, 960px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/ps4_slim_main_0.jpg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/ps4_slim_main_0.jpg 960w, https://umbala.vn/wp-content/uploads/2019/03/ps4_slim_main_0-300x169.jpg 300w, https://umbala.vn/wp-content/uploads/2019/03/ps4_slim_main_0-768x432.jpg 768w, https://umbala.vn/wp-content/uploads/2019/03/ps4_slim_main_0-550x309.jpg 550w\" width=\"960\" /></a></p>\r\n\r\n<p>Mặt sau m&aacute;y hỗ trợ c&aacute;c cổng kết nối quen thuộc gồm HDMI OUT, LAN, AUX được bố tr&iacute; lại v&agrave; kh&ocirc;ng c&ograve;n cổng opical. Một chi tiết kh&aacute;c so với bản tiền nhiệm l&agrave; người d&ugrave;ng c&oacute; thể thay thế ổ cứng dễ d&agrave;ng, đơn giản hơn th&ocirc;ng qua khe c&oacute; nắp đậy ở cạnh h&ocirc;ng v&agrave; ph&iacute;a sau m&aacute;y m&agrave; kh&ocirc;ng cần phải th&aacute;o cả nắp tr&ecirc;n. Việc giảm k&iacute;ch thước gi&uacute;p PS4 Slim tr&ocirc;ng gọn, khoảng c&aacute;ch giữa mặt tr&ecirc;n v&agrave; dưới hẹp cũng khiến bụi kh&oacute; lọt v&agrave;o m&aacute;y hơn (sẽ kh&ocirc;ng bị ồn sau một v&agrave;i th&aacute;ng d&ugrave;ng) nhưng hệ thống tản nhiệt mặt sau đ&atilde; phải bố tr&iacute; lại ở dưới nắp m&aacute;y (vẫn ở mặt sau). Với cấu tr&uacute;c&nbsp;mới, người d&ugrave;ng n&ecirc;n đặt PS4 Slim ở chỗ tho&aacute;ng để kh&ocirc;ng kh&iacute; lưu th&ocirc;ng tốt hơn gi&uacute;p tuổi thọ m&aacute;y cao hơn.</p>\r\n\r\n<p><a href=\"https://umbala.vn/wp-content/uploads/2019/03/arstechnica_ps4-slim-video-review-hands-on.jpg\"><img alt=\"\" height=\"450\" sizes=\"(max-width: 800px) 100vw, 800px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/arstechnica_ps4-slim-video-review-hands-on.jpg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/arstechnica_ps4-slim-video-review-hands-on.jpg 800w, https://umbala.vn/wp-content/uploads/2019/03/arstechnica_ps4-slim-video-review-hands-on-300x169.jpg 300w, https://umbala.vn/wp-content/uploads/2019/03/arstechnica_ps4-slim-video-review-hands-on-768x432.jpg 768w, https://umbala.vn/wp-content/uploads/2019/03/arstechnica_ps4-slim-video-review-hands-on-550x309.jpg 550w\" width=\"800\" /></a></p>\r\n\r\n<p>Ở mặt dưới m&aacute;y vốn dĩ thường kh&ocirc;ng c&oacute; g&igrave; đặc biệt (nhất l&agrave; với d&ograve;ng m&aacute;y chơi game) tuy nhi&ecirc;n Sony lại tạo ra điểm nhấn cho PS4 Slim th&ocirc;ng qua c&aacute;c đế cao su (ch&acirc;n m&aacute;y). 8 ch&acirc;n đế được l&agrave;m theo h&igrave;nh dạng l&ocirc;-g&ocirc; c&aacute;c ph&iacute;m bấm &ldquo;thần th&aacute;nh&rdquo; vu&ocirc;ng, tr&ograve;n, X, tam gi&aacute;c bố tr&iacute; đối xứng qua t&acirc;m chữ PS. Thiết kế n&agrave;y tạo sự mới mẻ v&agrave; gi&uacute;p PS4 Slim tr&ocirc;ng &ldquo;chất&rdquo; hơn, nhưng người d&ugrave;ng phải để m&aacute;y dạng dựng đứng tương tự như kiểu Xbox (cần c&oacute; th&ecirc;m dock) mới khoe được mặt n&agrave;y.</p>\r\n\r\n<p><a href=\"https://umbala.vn/wp-content/uploads/2019/03/playstation-ps4-pro-slim-blog_2018-12-06-11-26-16_cache.jpg\"><img alt=\"\" height=\"550\" sizes=\"(max-width: 880px) 100vw, 880px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/playstation-ps4-pro-slim-blog_2018-12-06-11-26-16_cache.jpg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/playstation-ps4-pro-slim-blog_2018-12-06-11-26-16_cache.jpg 880w, https://umbala.vn/wp-content/uploads/2019/03/playstation-ps4-pro-slim-blog_2018-12-06-11-26-16_cache-300x188.jpg 300w, https://umbala.vn/wp-content/uploads/2019/03/playstation-ps4-pro-slim-blog_2018-12-06-11-26-16_cache-768x480.jpg 768w, https://umbala.vn/wp-content/uploads/2019/03/playstation-ps4-pro-slim-blog_2018-12-06-11-26-16_cache-550x344.jpg 550w\" width=\"880\" /></a></p>\r\n\r\n<p>Đi k&egrave;m với m&aacute;y c&oacute; một tay cầm, d&acirc;y HDMI, c&aacute;p microUSB sạc v&agrave; một cặp tai nghe. Tay cầm DualShock 4 của PS4 Slim c&oacute; thiết kế hiện đại, bắt mắt. Cảm nhận khi mới cầm DualShock 4 kh&aacute; đầm tay v&agrave; dễ chịu. C&aacute;c n&uacute;t bấm c&oacute; độ nảy tốt, c&aacute;c ph&iacute;m L1/R1, L2/R2 l&agrave;m dạng nh&aacute;m tạo độ b&aacute;m, khi bấm hay b&oacute;p c&ograve; thấy &ldquo;đ&atilde;&rdquo; hơn tay cầm cũ. Hai n&uacute;t Start v&agrave; Select nay đ&atilde; được thay thế bằng Share d&ugrave;ng để chia sẻ h&igrave;nh ảnh trong game v&agrave; Option c&oacute; vai tr&ograve; như n&uacute;t Start.&nbsp;</p>\r\n\r\n<p><a href=\"https://umbala.vn/wp-content/uploads/2019/03/DS4.0.jpeg\"><img alt=\"\" height=\"480\" sizes=\"(max-width: 1200px) 100vw, 1200px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/DS4.0.jpeg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/DS4.0.jpeg 1200w, https://umbala.vn/wp-content/uploads/2019/03/DS4.0-300x120.jpeg 300w, https://umbala.vn/wp-content/uploads/2019/03/DS4.0-768x307.jpeg 768w, https://umbala.vn/wp-content/uploads/2019/03/DS4.0-1024x410.jpeg 1024w, https://umbala.vn/wp-content/uploads/2019/03/DS4.0-550x220.jpeg 550w\" width=\"1200\" /></a></p>\r\n\r\n<p><strong>Th&ocirc;ng số kỹ thuật, hiệu suất</strong></p>\r\n\r\n<p>Về phần cứng, PS4 Slim gần như kh&ocirc;ng c&oacute; thay đổi n&agrave;o so với PS4. M&aacute;y trang bị CPU 1.6GHz 8 l&otilde;i AMD Jaguar, GPU 1.84 TFLOP AMD Radeon (18CU, 800MHz), RAM 8GB GDDR5, ổ cứng 500GB/1TB (5.400rpm).</p>\r\n\r\n<p><a href=\"https://umbala.vn/wp-content/uploads/2019/03/PS4-Price-Drop-JP-Ann_09-15-15.jpg\"><img alt=\"\" height=\"338\" sizes=\"(max-width: 600px) 100vw, 600px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/PS4-Price-Drop-JP-Ann_09-15-15.jpg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/PS4-Price-Drop-JP-Ann_09-15-15.jpg 600w, https://umbala.vn/wp-content/uploads/2019/03/PS4-Price-Drop-JP-Ann_09-15-15-300x169.jpg 300w, https://umbala.vn/wp-content/uploads/2019/03/PS4-Price-Drop-JP-Ann_09-15-15-550x310.jpg 550w\" width=\"600\" /></a></p>\r\n\r\n<p>Giao diện PlayStation Dynamic Menu mới với c&aacute;c biểu tượng h&igrave;nh khối dễ nh&igrave;n v&agrave; tương t&aacute;c, mang hơi hướng như một đầu HD Box với ứng dụng như Netflix, Hulu v&agrave; Amazon. Khả năng li&ecirc;n kết mạng x&atilde; hội gi&uacute;p người chơi c&oacute; thể kết nối nhắn tin v&agrave; chia sẽ c&aacute;c đoạn video dễ d&agrave;ng. Tuy nhi&ecirc;n, cũng c&oacute; kh&ocirc;ng &iacute;t game thủ thấy giao diện n&agrave;y kh&aacute; phiền phức.</p>\r\n\r\n<p>V&iacute; dụ như một số game đang chơi sẽ bị gi&aacute;n đoạn bởi c&oacute; một tin nhắn từ người chơi kh&aacute;c gửi tới. D&ugrave; kh&ocirc;ng bị tho&aacute;t game, người chơi vẫn phải quay ra m&agrave;n h&igrave;nh Homescreen để đọc tin nhắn. Phần lướt web tr&ecirc;n PS4 Slim cũng kh&aacute; chậm, tuy nhi&ecirc;n điều n&agrave;y kh&ocirc;ng phải vấn đề qu&aacute; lớn v&igrave; đ&acirc;y chỉ l&agrave; t&iacute;nh năng phụ tr&ecirc;n một console.</p>\r\n\r\n<p><img alt=\"Đánh giá máy chơi game Sony PlayStation 4 Slim ảnh 6\" src=\"http://image.nghenhinvietnam.vn/w1024/Uploaded/2019/divxpwvo/2016_11_08/sony/nghenhinvietnam_vn_sonyplaystation4slim_12_ywah.jpg\" /></p>\r\n\r\n<p>PS4 Slim kh&ocirc;ng t&iacute;ch hợp qu&aacute; nhiều nội dung,&nbsp;nhưng với những tựa game hấp dẫn, đồ hoạ đỉnh cao với đủ thể loại từ tốc độ Need for Speed, nhập vai God of War, Assassin&rsquo;s Creed IV: Black Flag cho đến h&agrave;nh động với Call of Duty: Ghosts hay Battlefield 4, thể thao PES 2017&hellip; l&agrave; đ&atilde; qu&aacute; đủ cho PS4 Slim. Đặc biệt, khi kết hợp với Playstation VR, PS4 Slim cũng sẽ c&oacute; th&ecirc;m tới h&agrave;ng chục tựa game thực tế ảo. Đ&acirc;y l&agrave; một hướng đi mới m&agrave; Sony sẽ mang đến cho d&ograve;ng m&aacute;y console để tiếp tục thống trị thị trường chơi game tay cầm.</p>\r\n\r\n<p><a href=\"https://umbala.vn/wp-content/uploads/2019/03/PS4_Bundle1.jpg\"><img alt=\"\" height=\"430\" sizes=\"(max-width: 768px) 100vw, 768px\" src=\"https://umbala.vn/wp-content/uploads/2019/03/PS4_Bundle1.jpg\" srcset=\"https://umbala.vn/wp-content/uploads/2019/03/PS4_Bundle1.jpg 768w, https://umbala.vn/wp-content/uploads/2019/03/PS4_Bundle1-300x168.jpg 300w, https://umbala.vn/wp-content/uploads/2019/03/PS4_Bundle1-550x308.jpg 550w\" width=\"768\" /></a></p>\r\n\r\n<p><strong>Đ&aacute;nh gi&aacute; chung&nbsp;</strong></p>\r\n\r\n<p>Kh&ocirc;ng phải thiết kế mới ho&agrave;n to&agrave;n nhưng những thay đổi tr&ecirc;n PS4 Slim l&agrave; cần thiết. Một ch&uacute;t mới mẻ, gọn g&agrave;ng, hợp l&yacute; hơn cả về hiệu năng trong khi trải nghiệm được kế thừa trọn vẹn từ bản tiền nhiệm. Với những người đang sở hữu PS4, th&igrave; bản Slim sẽ kh&ocirc;ng phải l&agrave; lựa chọn thay thế hợp l&yacute;, m&agrave; họ cần PS4 Pro mạnh mẽ về cấu h&igrave;nh. Tuy nhi&ecirc;n, với những game thủ đang d&ugrave;ng PS3 hoặc Xbox, PS4 Slim l&agrave; sự lựa chọn n&acirc;ng cấp ph&ugrave; hợp.</p>\r\n\r\n<p>Đặc biệt M&aacute;y chơi game Playstation 4 Slim 500GB CUH-2218A B01 hiện đang c&oacute; gi&aacute; rất tốt tại Umbala lại tặng th&ecirc;m một tạy cầm v&agrave; hai đĩa game HOT, c&ograve;n chờ g&igrave; nữa m&agrave; kh&ocirc;ng mua ngay th&ocirc;i !!!</p>', '7000000', '6500000', 'aqqqps483.jpg', 'play-station,may-choi-game,ps4-slim', 1, NULL, NULL),
(9, 1, 1, 'Play Staion 5 (PS5)', '200', 0, 'Ra mắt sau 7 năm, PS5 không chỉ mang thiết kế mới hiện đại hơn mà còn sở hữu sức mạnh phần cứng đáng nể.', '<p><img alt=\"Cover_PS5.jpg\" src=\"https://photo2.tinhte.vn/data/attachment-files/2020/11/5212679_Cover_PS5.jpg\" /><br />\r\n<br />\r\nChỉ một ng&agrave;y sau khi c&aacute;c trang web c&ocirc;ng nghệ lớn l&ecirc;n b&agrave;i&nbsp;<a href=\"https://tinhte.vn/tags/danh-gia/\">đ&aacute;nh gi&aacute;</a>&nbsp;chi tiết chiếc m&aacute;y Xbox Series X của Microsoft, th&igrave; đến lượt&nbsp;<a href=\"https://tinhte.vn/tags/sony/\">Sony</a>&nbsp;phản ph&aacute;o khi hạn NDA cho b&agrave;i viết đ&aacute;nh gi&aacute; chi tiết phần cứng m&aacute;y PlayStation 5 cũng được gỡ bỏ. Xu hướng v&agrave; cảm nhận chung của những nh&agrave; ph&ecirc; b&igrave;nh thiết bị c&ocirc;ng nghệ cũng như gamer l&agrave;, PlayStation 5 đem lại qu&aacute; nhiều đột ph&aacute; so với PS4, cả về chất lẫn về lượng, nhưng chiếc m&aacute;y ho&agrave;n to&agrave;n kh&ocirc;ng ho&agrave;n hảo.<br />\r\n<br />\r\n<img alt=\"Tinhte_PS2.jpg\" src=\"https://photo2.tinhte.vn/data/attachment-files/2020/11/5212691_Tinhte_PS2.jpg\" /><br />\r\n<br />\r\nD&agrave;nh tặng cho&nbsp;<a href=\"https://tinhte.vn/tags/ps5/\">PS5</a>&nbsp;những c&acirc;u từ đanh th&eacute;p nhất về thiết kế chiếc m&aacute;y c&oacute; lẽ ch&iacute;nh l&agrave; Devindra Hardawar của Engadget:<em>&nbsp;&ldquo;Chiếc m&aacute;y to tới mức n&oacute; cho&aacute;n ngợp hết những chiếc&nbsp;<a href=\"https://tinhte.vn/tags/console/\">console</a>&nbsp;kh&aacute;c dưới c&aacute;i TV của t&ocirc;i. N&oacute; cao hơn cả Xbox Series X, m&agrave; m&aacute;y Xbox mới cũng kh&ocirc;ng b&eacute; ch&uacute;t n&agrave;o. Nhưng bản chất k&iacute;ch thước của PS5 như vậy v&igrave; n&oacute; được thiết kế theo kiểu truyền thống: Phần trung t&acirc;m chiếc m&aacute;y chứa tất cả c&aacute;c linh kiện, v&agrave; ốp hai miếng nhựa hai b&ecirc;n. Cả hệ thống giống như Sony cho PS3 &ldquo;tiến h&oacute;a&rdquo; vậy. Tr&ocirc;ng thiết kế n&oacute; khiến t&ocirc;i li&ecirc;n tưởng tới phong c&aacute;ch sến s&uacute;a như trong phim Back To The Future 2.</em><br />\r\n<br />\r\n<em>L&agrave; một người gần như cả đời y&ecirc;u mến thiết kế mỹ thuật c&ocirc;ng nghiệp của Sony, t&ocirc;i kh&ocirc;ng ng&aacute;n thiết kế của m&aacute;y lắm. Nhưng t&ocirc;i bị bất ngờ v&igrave; chiếc m&aacute;y PS5 lần đầu cầm v&agrave;o cảm gi&aacute;c mỏng manh v&agrave; bấp b&ecirc;nh như thế n&agrave;y. N&oacute; đi ngược lại ho&agrave;n to&agrave;n tất cả những g&igrave; t&ocirc;i kỳ vọng ở một chiếc m&aacute;y console 500 USD. T&ocirc;i nghĩ vấn đề chủ yếu l&agrave; v&igrave; hai tấm nhựa ốp hai b&ecirc;n. T&ocirc;i lu&ocirc;n lo n&oacute; sẽ rơi khi cầm m&aacute;y l&ecirc;n, v&agrave; t&ocirc;i kh&ocirc;ng kỳ vọng m&aacute;y chịu nổi một c&uacute; rơi. Xbox Series X nh&igrave;n nh&agrave;m ch&aacute;n, nhưng ch&iacute; &iacute;t tr&ocirc;ng n&oacute; cứng c&aacute;p v&agrave; bền.&rdquo;</em><br />\r\n<br />\r\n<img alt=\"Tinhte_PS1.png\" src=\"https://photo2.tinhte.vn/data/attachment-files/2020/11/5212692_Tinhte_PS1.png\" /><br />\r\n<br />\r\nC&oacute; lẽ đ&oacute; cũng l&agrave; &yacute; kiến chung của nhiều reviewer kh&aacute;c. M&aacute;y đẹp, lạ, nhưng hơi dị v&agrave; thiết kế kh&ocirc;ng ở mức xuất sắc về mặt c&ocirc;ng năng.<br />\r\n<br />\r\nTuy nhi&ecirc;n về mặt hiệu năng v&agrave; trải nghiệm, gần như tất cả c&aacute;c trang web lớn đều đ&aacute;nh gi&aacute; rất cao cỗ m&aacute;y của Sony. Polygon viết:&nbsp;<em>&ldquo;PlayStation 5 ho&agrave;n to&agrave;n đạt được kỳ vọng về thế hệ phần cứng next gen. Cảm x&uacute;c trải nghiệm đầu ti&ecirc;n với tốc độ khung h&igrave;nh cao hơn v&agrave; ray tracing thật sự tuyệt vời, c&ograve;n tốc độ tải game th&igrave; đ&aacute;ng kinh ngạc. Những t&iacute;nh năng giao diện mới như Activities v&agrave; Control Centre cũng gi&uacute;p những t&iacute;nh năng hay sử dụng h&agrave;ng ng&agrave;y dễ tiếp cận hơn v&agrave; đem lại nhiều cơ hội mới. Vẫn c&oacute; nhiều kh&ocirc;ng gian để Sony tiến bộ, như t&iacute;nh năng lưu file save l&ecirc;n đ&aacute;m m&acirc;y hoặc lựa chọn lưu trữ, hy vọng sẽ được cải thiện trong tương lai.&rdquo;</em><br />\r\n<br />\r\n<img alt=\"Tinhte_PS3.jpg\" src=\"https://photo2.tinhte.vn/data/attachment-files/2020/11/5212693_Tinhte_PS3.jpg\" /><br />\r\n<br />\r\nVentureBeat cho rằng:&nbsp;<em>&ldquo;PlayStation 5 l&agrave; một khoản đầu tư lớn, nhưng những người bỏ tiền mua m&aacute;y sớm chắc chắn sẽ kh&ocirc;ng phải hối hận về lựa chọn của họ. Ngay cả trong thời kỳ lợi nhuận giảm dần, PS5 vẫn đủ sức khiến mọi người cảm thấy h&aacute;o hức. PS5 sẽ kh&ocirc;ng thay đổi ho&agrave;n to&agrave;n c&aacute;ch mọi người&nbsp;<a href=\"https://tinhte.vn/tags/choi-game/\">chơi game</a>. Nhưng n&oacute; sẽ đem lại trải nghiệm tốt hơn rất nhiều, ở mọi mặt.&rdquo;</em><br />\r\n<br />\r\nTheSixthAsis khen ngợi chiếc tay cầm&nbsp;<a href=\"https://tinhte.vn/tags/dualsense/\">DualSense</a>:&nbsp;<em>&ldquo;PS5 tạo ra cảm nhận đầu ti&ecirc;n một c&aacute;ch phi thường, với sự cuốn h&uacute;t đem lại nhờ chiếc tay cầm DualSense, c&ocirc;ng nghệ &acirc;m thanh Tempest 3D, v&agrave; tiềm năng đồ họa tuyệt đẹp tr&ecirc;n những tr&ograve; chơi, tất cả những trải nghiệm đ&oacute; kết nối với nhau hơn bao giờ hết. Vẫn c&oacute; v&agrave;i vấn đề nho nhỏ với phần mềm hệ thống, v&agrave; đặt m&aacute;y v&agrave;o trung t&acirc;m giải tr&iacute; gia đ&igrave;nh c&oacute; thể sẽ hơi kh&oacute; khăn, nhưng đ&acirc;y l&agrave; bước nhảy đủ sức gi&uacute;p việc chơi game trở n&ecirc;n kh&aacute;c biệt.&rdquo;</em><br />\r\n<br />\r\nĐiều quan trọng khiến PS5 c&oacute; thể th&agrave;nh c&ocirc;ng hơn Xbox Series X ngay khi cả hai cỗ m&aacute;y được b&aacute;n ra thị trường v&agrave;o tuần n&agrave;y, đ&oacute; l&agrave; PS5 c&oacute; game mới, được ph&aacute;t triển ri&ecirc;ng để tận dụng tối đa mọi t&iacute;nh năng mới của chiếc m&aacute;y mới. Đ&oacute; l&agrave;&nbsp;<a href=\"https://tinhte.vn/tags/spider-man-2/\">Spider-Man</a>: Miles Morales, l&agrave; Sackboy: A Big Adventure, l&agrave; Demon&rsquo;s Souls Remake.<br />\r\n<br />\r\n<img alt=\"Tinhte_PS5.jpg\" src=\"https://photo2.tinhte.vn/data/attachment-files/2020/11/5212698_Tinhte_PS5.jpg\" /><br />\r\n<br />\r\nV&agrave; thậm ch&iacute; tr&ograve; chơi miễn ph&iacute; ho&agrave;n to&agrave;n, Astro&rsquo;s Playroom c&agrave;i sẵn trong từng chiếc m&aacute;y PS5 cũng được đ&aacute;nh gi&aacute; độc lập, v&agrave; nhận được những lời khen ngợi hiếm c&oacute; ở một bản demo. IGN viết:<br />\r\n<br />\r\n<em>&ldquo;C&aacute;i game tặng k&egrave;m với PS5 n&agrave;y thật ra giống một bản demo c&ocirc;ng nghệ, cơ bản l&agrave; một hệ thống sandbox đơn giản dễ tiếp cận cho bất kỳ ai chơi thử, v&agrave; kh&aacute;m ph&aacute; những thứ PS5 hứa hẹn đem lại cho người d&ugrave;ng. Nhưng n&oacute; c&oacute; đủ &yacute; tưởng s&aacute;ng tạo, đủ c&aacute;ch khai th&aacute;c những t&iacute;nh năng mới cho chiếc DualSense, đến ngưỡng người d&ugrave;ng PS5 kh&ocirc;ng n&ecirc;n bỏ qua bản demo miễn ph&iacute; ấy m&agrave; chỉ tập trung v&agrave;o những tr&ograve; chơi trả tiền ra mắt c&ugrave;ng PS5. Sau h&agrave;ng th&aacute;ng trời hứa hẹn về những t&iacute;nh năng của tay cầm DualSense, Astro&rsquo;s Playroom biến lời hứa th&agrave;nh sự thật m&agrave; ch&uacute;ng ta c&oacute; thể tự tay trải nghiệm, qua đ&oacute; kh&aacute;m ph&aacute; những điều c&oacute; thể trở th&agrave;nh hiện thực với hệ m&aacute;y PS5.&rdquo;</em><br />\r\n<br />\r\nTrong khi đ&oacute;, Spider-Man: Miles Morales, vốn được ph&aacute;t triển cho m&aacute;y PS5, cũng nhận được nhiều lời khen ngợi, d&ugrave; hầu hết đều ph&agrave;n n&agrave;n một vấn đề chung: Game ngắn, ngắn hơn Spider-Man ph&aacute;t h&agrave;nh năm 2018.<br />\r\n<br />\r\n<img alt=\"Tinhte_PS4.jpg\" src=\"https://photo2.tinhte.vn/data/attachment-files/2020/11/5212697_Tinhte_PS4.jpg\" /><br />\r\n<br />\r\nGame Informer viết:&nbsp;<em>&ldquo;Kh&ocirc;ng mất thời gian tải game. Raytracing qu&aacute; đẹp. Phi&ecirc;n bản PS5 của tựa game Spider-Man: Miles Morales l&agrave; một t&aacute;c phẩm giải tr&iacute; tương t&aacute;c với c&ocirc;ng nghệ tuyệt vời, tiếp tục khiến mọi người kinh ngạc. Phi&ecirc;n bản PS5 l&agrave; c&aacute;ch l&yacute; tưởng để thưởng thức tr&ograve; chơi n&agrave;y, nếu bạn c&oacute; điều kiện. N&oacute;i vậy kh&ocirc;ng đồng nghĩa với việc bản PS4 c&aacute;ch qu&aacute; xa về chất lượng. Spider-Man từ năm 2018 vẫn l&agrave; một trong số những game đẹp nhất của thời kỳ PS4, v&agrave; phi&ecirc;n bản Miles Morales vẫn tiếp tục giữ nguy&ecirc;n phong độ.&rdquo;</em><br />\r\n<br />\r\nPS5 sẽ ch&iacute;nh thức b&aacute;n ra thị trường v&agrave;o ng&agrave;y 12/11 tại Mỹ, &Uacute;c, Nhật Bản, v&agrave; 19/11 tại nhiều quốc gia kh&aacute;c như Singapore.</p>', '12500000', '11000000', 'ps5-review93.jpg', 'play-station,may-choi-game,ps5', 1, NULL, NULL),
(10, 2, 2, 'Xbox 360 Slim', '200', 0, 'Sau khi được giới thiệu tại E3 2010, phiên bản Slim của máy chơi game Xbox 360 cũng đã xuất hiện ở Việt Nam.', '<p>&nbsp;</p>\r\n\r\n<p><strong>Phi&ecirc;n bản console mới của Microsoft n&agrave;y c&oacute; một số đổi mới như: k&iacute;ch thước nhỏ hơn do t&iacute;ch hợp chip đồ họa, CPU v&agrave; RAM l&ecirc;n 1 bo mạch chủ duy nhất, chuyển n&uacute;t bấm vật l&yacute; th&agrave;nh ph&iacute;m cảm ứng, giảm thiểu tiếng ồn ph&aacute;t ra khi m&aacute;y hoạt động cũng như n&acirc;ng cấp dung lượng ổ cứng l&ecirc;n gấp đ&ocirc;i...</strong><br />\r\n<br />\r\nTuy mang t&ecirc;n gọi Slim nhưng Xbox360 kh&ocirc;ng hề mỏng m&agrave; n&oacute; vẫn kh&aacute; d&agrave;y, m&aacute;y cũng kh&ocirc;ng nhẹ hơn bản Xbox thường nhiều. Phi&ecirc;n bản m&igrave;nh&nbsp;<a href=\"https://tinhte.vn/tags/dap-hop/\">đập hộp</a>&nbsp;d&agrave;nh cho Nhật n&ecirc;n d&ugrave;ng điện 110v, m&aacute;y c&oacute; k&egrave;m theo kh&aacute; nhiều phụ kiện như tai nghe chơi game, tay cầm hay c&aacute;p TV out. Hiện&nbsp;<a href=\"http://haloshop.vn/\" target=\"_blank\">HaloShop</a>&nbsp;đang b&aacute;n&nbsp;<a href=\"https://tinhte.vn/tags/xbox-360-slim/\">Xbox 360 Slim</a>&nbsp;với gi&aacute; 8tr2.<br />\r\n&nbsp;</p>\r\n\r\n<p><iframe frameborder=\"0\" height=\"450\" src=\"https://www.youtube.com/embed/ehY9Ur0p3r4?wmode=opaque\" width=\"800\"></iframe><br />\r\n<br />\r\n<img alt=\"[​IMG]\" src=\"http://photo.tinhte.vn/attach/public_image/btv/9759/97594c2daee30f210_DSC_0105.jpg\" /><br />\r\n<br />\r\n<img alt=\"[​IMG]\" src=\"http://photo.tinhte.vn/attach/public_image/btv/9759/97594c2daee41b9d1_DSC_0106.jpg\" /><br />\r\n<br />\r\n<img alt=\"[​IMG]\" src=\"http://photo.tinhte.vn/attach/public_image/btv/9759/97594c2daee5731a6_DSC_0107.jpg\" /><br />\r\n<br />\r\n<img alt=\"[​IMG]\" src=\"http://photo.tinhte.vn/attach/public_image/btv/9759/97594c2daeecd2595_DSC_0112.jpg\" /><br />\r\n<br />\r\n<img alt=\"[​IMG]\" src=\"http://photo.tinhte.vn/attach/public_image/btv/9759/97594c2daeede8e92_DSC_0113.jpg\" /><br />\r\n<br />\r\n<img alt=\"[​IMG]\" src=\"http://photo.tinhte.vn/attach/public_image/btv/9759/97594c2daef5b6901_DSC_0122.jpg\" /></p>', '2600000', '2400000', '20191221_623bdcfe7e26e31e09e0b03f04fc485d_157692807094.jpg', 'xbox,may-choi-game,xbox-360', 1, NULL, NULL);
INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_quantity`, `product_sold`, `product_desc`, `product_content`, `product_price`, `price_cost`, `product_image`, `product_tags`, `product_status`, `created_at`, `updated_at`) VALUES
(11, 2, 2, 'Xbox One Elite', '199', 1, 'Sau hơn 2 năm lên kệ, chiếc máy chơi game Microsoft Xbox One vẫn luôn khẳng định đây là một trong những thiết bị giải trí hấp dẫn nhất hành tinh.', '<p>So với Xbox 360 trước đ&acirc;y, thế hệ mới nhận nhiều sự n&acirc;ng cấp li&ecirc;n quan tới khả năng xứ l&yacute; đồ họa cũng thiết kế đẹp mắt hơn, v&agrave; kh&ocirc;ng thể bỏ qua những chiếc tay cầm d&agrave;nh ri&ecirc;ng cho thế hệ mới n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9720-1459855257302/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9720-1459855257302/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>Controller của Xbox One mang mang v&oacute;c d&aacute;ng hiện đại hơn nhiều so với người tiền nhiệm, chất liệu v&agrave; khả năng ho&agrave;n thiện cũng được n&acirc;ng cao. Kh&ocirc;ng chỉ c&oacute; vậy, Microsoft c&ograve;n ra mắt một phi&ecirc;n bản đặc biệt của sản phẩm n&agrave;y, c&oacute; t&ecirc;n Xbox One Elite, chiếc tay chơi game cao cấp, ước mơ của mọi game thủ.</p>\r\n\r\n<p>Tại sao t&ocirc;i lại d&ugrave;ng từ &quot;ước mơ&quot; khi n&oacute;i tới Xbox One Elite? Đầu ti&ecirc;n, đ&acirc;y kh&ocirc;ng phải một sản phẩm đại tr&agrave;, Microsoft chỉ b&aacute;n ra một số lượng giới hạn, một phần bởi đ&acirc;y l&agrave; phi&ecirc;n bản đặc biệt, mặt kh&aacute;c c&ocirc;ng sức để ho&agrave;n thiện sản phẩm n&agrave;y cũng kh&ocirc;ng hề đơn giản. Thứ 2, gi&aacute; b&aacute;n của Xbox One Elite kh&ocirc;ng hề rẻ, ngay cả với c&aacute;c game thủ ở c&aacute;c nước ph&aacute;t triển.</p>\r\n\r\n<p><a href=\"javascript:void(0);\"><img alt=\"\" src=\"https://genk.mediacdn.vn/thumb_w/640/2016/dsc-9725-1459855257464.jpg\" title=\"\" /></a></p>\r\n\r\n<ul>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9725-1459855257464.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9721-1459855257492.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9722-1459855257289.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9724-1459855257519.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9727-1459855257238.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9728-1459855257264.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9729-1459855257450.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9732-1459855257478.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9739-1459855257604.jpg\" title=\"\" /></a></li>\r\n	<li><a href=\"javascript:void(0);\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/zoom/140_100/thumb_w/640/2016/dsc-9744-1459855257228.jpg\" title=\"\" /></a></li>\r\n</ul>\r\n\r\n<ul>\r\n</ul>\r\n\r\n<p>Trong thực tế, Xbox One Elite từng ch&aacute;y h&agrave;ng trong suốt một thời gian d&agrave;i. Ra mắt tại sự kiện E3 năm 2015 v&agrave; l&ecirc;n kệ kh&ocirc;ng l&acirc;u sau đ&oacute;, nhưng cho tới m&ugrave;a Gi&aacute;ng Sinh cuối năm ngo&aacute;i, Microsoft đ&atilde; bất lực trước việc Xbox One Elite ch&aacute;y h&agrave;ng. Đ&acirc;y l&agrave; một m&oacute;n qu&agrave; c&ocirc;ng nghệ hot nhất trong kỳ nghỉ lễ, phải tới đầu năm 2016, sản phẩm n&agrave;y mới đủ để đ&aacute;p ứng trở lại.</p>\r\n\r\n<p>V&agrave; tất nhi&ecirc;n, đ&acirc;y l&agrave; chiếc tay chơi game hiếm c&oacute; kh&oacute; t&igrave;m ở Việt Nam, bởi c&aacute;c nh&agrave; b&aacute;n lẻ biết rằng hầu hết game thủ Việt kh&ocirc;ng muốn chi một số tiền lớn l&ecirc;n tới nhiều triệu đồng cho một chiếc tay chơi game. N&ecirc;n sau gần một năm, ch&uacute;ng t&ocirc;i mới c&oacute; cơ hội tr&ecirc;n tay m&oacute;n h&agrave;ng xa xỉ n&agrave;y.</p>\r\n\r\n<p>Vậy với gi&aacute; tiền đ&oacute;, người d&ugrave;ng nhận được điều g&igrave; đặc biệt?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>00:08:15</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mở hộp v&agrave; tr&ecirc;n tay Xbox One Elite.</p>\r\n\r\n<p><strong>Thiết kế</strong></p>\r\n\r\n<p>Tổng thể Xbox One Elite kh&ocirc;ng kh&aacute;c nhiều so với c&aacute;c tay chơi game th&ocirc;ng thường tr&ecirc;n hệ m&aacute;y mới nhất của Microsoft. K&iacute;ch thước ngang bằng, chất liệu v&agrave; lớp sơn phủ cũng vậy. Tuy nhi&ecirc;n, phi&ecirc;n bản đặc biệt nổi bật hơn nhờ v&agrave;o những đường n&eacute;t kim loại sang trọng.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; bộ ảnh chi tiết về thiết kế của Xbox One Elite qua nhiều g&oacute;c nh&igrave;n.</p>\r\n\r\n<p><strong>Hiệu năng sử dụng</strong></p>\r\n\r\n<p>Kh&ocirc;ng đẹp hơn nhiều, th&igrave; c&ocirc;ng năng khi chơi game chắc chắn được n&acirc;ng cao đ&aacute;ng kể, chứ kh&ocirc;ng lẽ đắt hơn nhiều m&agrave; kh&ocirc;ng c&oacute; g&igrave; đặc biệt. Xbox One Elite c&oacute; một sự n&acirc;ng cấp đ&aacute;ng kể phần ph&iacute;m điều hướng, thường được gọi l&agrave; D-Pad.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9726-1459855257312/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9726-1459855257312/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>Nếu như trước đ&acirc;y, c&aacute;c bộ điều khiển của Xbox 360 hay Xbox One th&ocirc;ng thường, phần ph&iacute;m điều hướng chỉ cho ph&eacute;p điều chỉnh 4 hướng kh&aacute;c nhau, muốn linh hoạt hơn, bạn sẽ phải sử dụng cần Analog. Tuy vậy, đắt xắt ra miếng, Xbox One Elite sở hữu phần D-Pad c&oacute; khả năng điều chỉnh 11 hướng kh&aacute;c nhau.</p>\r\n\r\n<p>Chưa hết, ph&iacute;a sau của sản Xbox One Elite c&oacute; 2 điểm nhấn kh&aacute;c biệt. Phải kể tới đ&oacute; l&agrave; c&ocirc;ng tắc điều chỉnh h&agrave;nh tr&igrave;nh ph&iacute;m cho phần trigger (RT v&agrave; LT), độ s&acirc;u của 2 n&uacute;t ph&iacute;a tr&ecirc;n n&agrave;y c&oacute; thể thay đổi t&ugrave;y theo y&ecirc;u cầu của người d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9731-1459855257577/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9731-1459855257577/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>Tiếp đến, Xbox One Elite cho ph&eacute;p bạn bổ sung tới 4 ph&iacute;m kh&aacute;c ở mặt sau, được gọi l&agrave; Paddle. C&oacute; sẵn 4 chiếc cần trong bộ phụ kiện tặng k&egrave;m, để người d&ugrave;ng lắp v&agrave;o c&aacute;c vị tr&iacute; tương ứng ở mặt sau.</p>\r\n\r\n<p><iframe height=\"0\" id=\"ads-place-2017500\" scrolling=\"no\" src=\"javascript:if(typeof(admSspPageRg)!=\'undefined\'){admSspPageRg.draw(2017500);}else{parent.admSspPageRg.draw(2017500);}\" width=\"0\"></iframe></p>\r\n\r\n<p><strong>Phụ kiện v&agrave; khả năng t&ugrave;y biến b&aacute; đạo</strong></p>\r\n\r\n<p>Đắt gấp nhiều lần so với phi&ecirc;n bản th&ocirc;ng thường, từ c&aacute;ch đ&oacute;c hộp cho tới t&uacute;i tựng v&agrave; phụ kiện theo k&egrave;m, chiếc tay chơi game Elite được chăm s&oacute;c cực tận t&igrave;nh. Một bộ với 4 bộ thay cho cần Analog, 4 cần Paddle v&agrave; ph&iacute;m điều hướng kh&aacute;c l&agrave; những g&igrave; người d&ugrave;ng được tặng khi mua Xbox One Elite.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9737-1459855257507/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9737-1459855257507/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>V&acirc;ng, đ&acirc;y l&agrave; chiếc tay chơi game cho ph&eacute;p bạn dễ d&agrave;ng th&aacute;o lắp c&aacute;c th&agrave;nh phần ph&iacute;m bấm v&agrave; cần điều khiển, thay thế dựa theo y&ecirc;u cầu sử dụng của mỗi người. Ngo&agrave;i ra, với những ai ưa m&agrave;y m&ograve; v&agrave; DIY, t&ugrave;y biến c&aacute;c n&uacute;t bấm ri&ecirc;ng cho m&igrave;nh l&agrave; điều kh&ocirc;ng qu&aacute; kh&oacute; khăn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9734-1459855257531/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9734-1459855257531/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9736-1459855257550/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9736-1459855257550/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9740-1459855257589/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9740-1459855257589/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9742-1459855257217/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" src=\"https://genk.mediacdn.vn/k:thumb_w/640/2016/dsc-9742-1459855257217/tren-tay-xbox-one-elite-chiec-tay-choi-game-cuc-doc-uoc-mo-tri-gia-55-trieu-vnd-cua-game-thu-viet.jpg\" /></a></p>\r\n\r\n<p>Khả năng t&ugrave;y biến kh&ocirc;ng dừng lại ở phần cứng, n&oacute; c&ograve;n nhận được kh&ocirc;ng &iacute;t sự hỗ trợ từ phần mềm. Cụ thể, Microsoft ph&aacute;t triển cho c&aacute;c tay cầm Xbox bộ điều khiển c&oacute; t&ecirc;n Xbox Accessories, được ph&acirc;n phối th&ocirc;ng qua chợ ứng dụng Windows Store tr&ecirc;n hệ điều h&agrave;nh Windows 10.</p>\r\n\r\n<p>C&ugrave;ng xem video dưới đ&acirc;y để hiểu r&otilde; hơn về c&aacute;ch hiệu chỉnh tay cầm Xbox One Elite với phần mềm n&oacute;i tr&ecirc;n.</p>', '6000000', '5600000', 'xbox_1_e_268.jpg', 'xbox,may-choi-game,xbox-one-elite', 1, NULL, NULL),
(12, 3, 1, 'Tai nghe MDR-Z7M2', '200', 0, 'Tai nghe MDR-Z7M2 mang đến âm thanh tuyệt đỉnh cho bạn đắm mình thưởng thức trong nhiều giờ. Kế thừa công nghệ từ MDR-Z1R trứ danh', '<p><img alt=\"Logo Âm thanh độ phân giải cao\" src=\"https://www.sony.com.vn/image/9ecfb06e6be3f8409a7959d6e805fbdf?fmt=png-alpha&amp;wid=131\" /></p>\r\n\r\n<p>Tận hưởng &acirc;m thanh tuyệt đỉnh với &Acirc;m thanh độ ph&acirc;n giải cao&nbsp;</p>\r\n\r\n<p>Đưa bộ sưu tập nhạc số của bạn l&ecirc;n một tầm cao mới với &Acirc;m thanh độ ph&acirc;n giải cao. Nhờ khả năng ghi &acirc;m kỹ thuật số ở tốc độ cao hơn CD, &Acirc;m thanh độ ph&acirc;n giải cao sẽ đưa bạn tiếp cận gần hơn với chất lượng ban đầu trong ph&ograve;ng thu, từ đ&oacute; mang &acirc;m nhạc đến với bạn theo đ&uacute;ng &yacute; đồ của nghệ sĩ.</p>\r\n\r\n<p>Trải nghiệm &acirc;m thanh khiến bạn kh&ocirc;ng muốn rời xa</p>\r\n\r\n<p>Đắm ch&igrave;m trong &acirc;m nhạc, thưởng thức chất lượng &acirc;m thanh cao cấp v&agrave; h&agrave;ng giờ nghe nhạc thoải m&aacute;i. MDR-Z7M2 mang đến khả năng t&aacute;i tạo &acirc;m thanh tinh tế, từ &acirc;m cao đến &acirc;m thấp, với bộ m&agrave;ng loa HD 70 mm, m&agrave;ng chắn polymer tinh thể lỏng mạ nh&ocirc;m, lưới loa c&oacute; họa tiết theo quy luật Fibonacci, nam ch&acirc;m lớn hơn với k&iacute;ch thước gấp đ&ocirc;i nam ch&acirc;m của sản phẩm tiền nhiệm.&nbsp;Ch&iacute;nh v&igrave; vậy, sản phẩm t&aacute;i tạo được trải nghiệm &acirc;m thanh s&acirc;u rộng, ba chiều của nhạc sống.</p>\r\n\r\n<p>Bộ m&agrave;ng loa HD 70 mm mang đến &acirc;m thanh rộng mở, tho&aacute;ng đạt</p>\r\n\r\n<p>Bộ m&agrave;ng loa HD mới ph&aacute;t triển c&oacute; đường k&iacute;nh 70 mm, được thiết kế &ocirc;m trọn đ&ocirc;i tai, tạo ra mặt s&oacute;ng phẳng tương tự như ở trường &acirc;m thanh tự nhi&ecirc;n, đồng thời t&aacute;i tạo c&aacute;c tần số từ 4 Hz đến 100 kHz. Từng nốt nhạc giữ nguy&ecirc;n được độ trong trẻo tr&ecirc;n khắp &acirc;m tầng rộng, bảo to&agrave;n trọn vẹn cảm x&uacute;c v&agrave; năng lượng của bản ghi gốc, với giọng h&aacute;t trong vắt v&agrave; độ nhạy &acirc;m trầm ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>N&acirc;ng cao chất lượng &acirc;m thanh với Signature Series</p>\r\n\r\n<p>Tai nghe MDR-Z1R vẫn l&agrave; sản phẩm được giới đam m&ecirc; &acirc;m thanh tin y&ecirc;u lựa chọn v&agrave; giờ đ&acirc;y, c&aacute;c c&ocirc;ng nghệ ch&iacute;nh của d&ograve;ng tai nghe đ&oacute; đ&atilde; được n&acirc;ng tầm ở d&ograve;ng MDR-Z7M2 mới. &Acirc;m thanh tuyệt đỉnh cho trải nghiệm &acirc;m thanh khiến bạn m&ecirc; đắm nhiều giờ.</p>\r\n\r\n<p><img alt=\"Đồ thị so sánh tần số\" src=\"https://www.sony.com.vn/image/62678c8fc1d77fdfd68b72b3ecdfa292?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>Tần số phản hồi cao mượt m&agrave; hơn</p>\r\n\r\n<p>Kế thừa từ MDR-Z1R, lưới loa của MDR-Z7M2 c&oacute; họa tiết theo quy luật Fibonacci, cho ph&eacute;p t&aacute;i tạo mượt m&agrave; c&aacute;c đặc t&iacute;nh &acirc;m thanh đ&uacute;ng như &yacute; muốn - vốn l&agrave; một th&aacute;ch thức lớn đối với tần số si&ecirc;u cao.</p>\r\n\r\n<p><img alt=\"Lưới loa có họa tiết theo quy luật của dãy Fibonacci\" src=\"https://www.sony.com.vn/image/adc2fd99d234adccd58f2ae093a03636?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>Lưới loa c&oacute; họa tiết theo quy luật Fibonacci&nbsp;</p>\r\n\r\n<p>Lưới loa l&agrave;m từ nhựa cứng với khung mỏng hơn v&agrave; c&oacute; họa tiết theo quy luật Fibonacci n&ecirc;n vừa chắc khỏe vừa mảnh mai, gi&uacute;p giảm thiểu hiện tượng tấm lưới h&atilde;m &acirc;m ở c&aacute;c tần số si&ecirc;u cao.</p>\r\n\r\n<p><img alt=\"Màng chắn LCP mạ nhôm\" src=\"https://www.sony.com.vn/image/5165ac5cc2107cf9770e8a1cff9cf0de?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>M&agrave;ng chắn LCP mạ nh&ocirc;m&nbsp;</p>\r\n\r\n<p>V&ograve;m m&agrave;ng chắn lớn hơn c&ugrave;ng h&igrave;nh dạng được tối ưu h&oacute;a để &acirc;m thanh trung - thấp tần lu&ocirc;n trong trẻo, c&ograve;n &acirc;m thanh trung - cao tần lu&ocirc;n tuyệt hảo. Nhờ kết hợp giữa nh&ocirc;m v&agrave; LCP, độ cộng hưởng ri&ecirc;ng của vật liệu bị triệt ti&ecirc;u, đem lại &acirc;m thanh tự nhi&ecirc;n v&agrave; c&oacute; độ ch&acirc;n thực cao.</p>\r\n\r\n<p><img alt=\"Nam châm Neodymium động\" src=\"https://www.sony.com.vn/image/669b037fab0639209a0697ec444974ac?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>Nam ch&acirc;m Dynamic Neodymium lớn hơn xấp xỉ 200%</p>\r\n\r\n<p>Nhờ sử dụng loại nam ch&acirc;m mới được ph&aacute;t triển, lớn hơn gấp đ&ocirc;i so với k&iacute;ch thước của MDR-Z7, MDR-Z7M2 c&oacute; c&ocirc;ng suất lớn hơn v&agrave; khả năng phản hồi nhanh với c&aacute;c t&iacute;n hiệu độ ph&acirc;n giải cao, mang đến &acirc;m thanh trong trẻo kh&ocirc;ng g&igrave; s&aacute;nh bằng.</p>\r\n\r\n<p>Đắm ch&igrave;m trong thế giới &acirc;m thanh chất lượng cao</p>\r\n\r\n<p>Lu&ocirc;n kết nối với &acirc;m thanh tuyệt đỉnh nhờ c&aacute;p kết nối c&acirc;n bằng &Phi; 4,4 mm v&agrave; c&aacute;́p đ&ocirc;̀ng ch&ocirc;́ng oxy hóa mạ bạc - tất cả đều c&oacute; trong hộp.</p>\r\n\r\n<p><img alt=\"Biểu đồ kết nối cáp cân bằng Φ 4,4 mm\" src=\"https://www.sony.com.vn/image/47a32dfb4dd5fd0eda8a36dbb7b33421?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Kết nối c&aacute;p c&acirc;n bằng 4,4 mm</p>\r\n\r\n<p>Thưởng thức &acirc;m thanh động ch&iacute;nh x&aacute;c v&agrave; trong trẻo với c&aacute;p kết nối c&acirc;n bằng &Phi; 4,4 mm, c&oacute; khả năng t&aacute;ch biệt t&iacute;n hiệu &acirc;m thanh b&ecirc;n tr&aacute;i v&agrave; b&ecirc;n phải, giảm thiểu thất tho&aacute;t t&iacute;n hiệu khi truyền tải dẫn đến suy giảm chất lượng &acirc;m thanh.</p>\r\n\r\n<p><img alt=\"Cáp OFC mạ bạc\" src=\"https://www.sony.com.vn/image/0877d83339caa068f450a2c0e3dd0160?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>C&aacute;p đ&ocirc;̀ng ch&ocirc;́ng oxy hóa mạ bạc&nbsp;</p>\r\n\r\n<p>C&aacute;p đồng chống oxy h&oacute;a mạ bạc gi&uacute;p giảm thiểu điện trở v&agrave; hiện tượng thất tho&aacute;t t&iacute;n hiệu khi truyền tải. Nhờ đó, ch&acirc;́t lượng &acirc;m thanh kh&ocirc;ng bị giảm s&uacute;t, cho chi ti&ecirc;́t tinh tế hơn và &acirc;m b&ocirc;̉ng mượt mà hơn.&nbsp;</p>\r\n\r\n<p>Miếng đ&ecirc;̣m tai ti&ecirc;̣n dụng</p>\r\n\r\n<p>Bề mặt da tổng hợp được th&ecirc;u ba chiều kết hợp với đệm tai bằng bọt xốp urethane d&agrave;y c&oacute; độ đ&agrave;n hồi thấp kh&ocirc;ng chỉ mang lại h&igrave;nh d&aacute;ng thuận tiện, vừa vặn với mọi d&aacute;ng đầu m&agrave; c&ograve;n gi&uacute;p ph&acirc;n bổ đều &aacute;p lực. Nhờ độ kh&iacute;t tăng l&ecirc;n m&agrave; hiện tượng r&ograve; rỉ &acirc;m thanh đ&atilde; giảm xuống v&agrave; &acirc;m thanh được tối ưu h&oacute;a với &acirc;m trầm mạnh mẽ.</p>\r\n\r\n<p><img alt=\"Vành treo tai và thanh trượt bằng hợp kim nhôm\" src=\"https://www.sony.com.vn/image/93304f26560973ed82a2ab14f9227ac5?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>V&agrave;nh treo tai v&agrave; thanh trượt bằng hợp kim nh&ocirc;m</p>\r\n\r\n<p>Hợp kim nh&ocirc;m d&ugrave;ng cho v&agrave;nh treo tai v&agrave; thanh trượt cũng ch&iacute;nh l&agrave; vật liệu tạo th&agrave;nh khung ch&iacute;nh của tai nghe. Ch&iacute;nh những điều n&agrave;y đảm bảo sản phẩm vừa nhẹ vừa c&oacute; độ bền l&acirc;u d&agrave;i qua năm th&aacute;ng.</p>\r\n\r\n<p><img alt=\"Một người đàn ông thư giãn khi đeo đệm tai thoải mái của MDR-M7M2\" src=\"https://www.sony.com.vn/image/733fb6f5fd3c01c4c535e14e2b04822d?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Vật liệu đệm tai thoải m&aacute;i</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i đ&atilde; chọn đệm bằng bọt xốp urethane mềm mại c&oacute; đặc t&iacute;nh h&uacute;t v&agrave; giải ph&oacute;ng hơi ẩm l&agrave;m bề mặt của đệm tai để giảm cảm gi&aacute;c b&iacute; để bạn thoải m&aacute;i nghe l&acirc;u m&agrave; vẫn thoải m&aacute;i.</p>\r\n\r\n<p>Ch&acirc;n thực v&agrave; đầy m&ecirc; đắm</p>\r\n\r\n<p>H&ograve;a m&igrave;nh v&agrave;o kh&ocirc;ng gian ngập tr&agrave;n &acirc;m nhạc như thể đang tận hưởng buổi h&ograve;a nhạc trực tiếp hay ở tại ph&ograve;ng thu &acirc;m của nghệ sĩ. Với 360 Reality Audio, &acirc;m nhạc chưa bao giờ trở n&ecirc;n m&ecirc; đắm v&agrave; ch&acirc;n thực đến thế. Tai nghe n&agrave;y hỗ trợ 360 Reality Audio<sup>2</sup>.</p>', '16990000', '16000000', '54a91fff33a7a3f9679d6e42dbc7d4d177.webp', 'tai-nghe,sony,phu-kien', 1, NULL, NULL),
(13, 3, 1, 'Tai nghe không dây WH-1000XM4', '200', 0, 'Thông minh và nhạy bén, tai nghe WH-1000XM4 mang đến những cải tiến tinh vi trong công nghệ chống ồn dẫn đầu ngành của chúng tôi', '<p>H&ograve;a m&igrave;nh v&agrave;o &acirc;m nhạc</p>\r\n\r\n<p>Kh&aacute;m ph&aacute; c&aacute;ch tai nghe WH-1000XM4 kết hợp c&ocirc;ng nghệ chống ồn ti&ecirc;n tiến nhất của ch&uacute;ng t&ocirc;i với chất lượng &acirc;m thanh vượt trội c&ugrave;ng h&agrave;ng loạt t&iacute;nh năng th&ocirc;ng minh cho trải nghiệm nghe tuyệt vời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c t&iacute;nh năng nổi bật</p>\r\n\r\n<p><img alt=\"Tai nghe chống ồn trên tàu hỏa\" src=\"https://www.sony.com.vn/image/a9ab86d8519ffe98f446c2986136bec3?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>C&ocirc;ng nghệ chống ồn h&agrave;ng đầu<sup>2</sup></p>\r\n\r\n<p><a href=\"https://www.sony.com.vn/electronics/tai-nghe-choang-dau-tai-nghe/wh-1000xm4#noise-cancellation\" tabindex=\"0\" target=\"_self\" title=\"Tìm hiểu thêm\">T&igrave;m hiểu th&ecirc;m</a></p>\r\n\r\n<p><img alt=\"Âm thanh tuyệt đỉnh trên sân ga\" src=\"https://www.sony.com.vn/image/1274973dbfcb9e092a8ce2b37cfd5e09?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>&Acirc;m thanh tuyệt đỉnh</p>\r\n\r\n<p><a href=\"https://www.sony.com.vn/electronics/tai-nghe-choang-dau-tai-nghe/wh-1000xm4#premium-sound\" tabindex=\"0\" target=\"_self\" title=\"Tìm hiểu thêm\">T&igrave;m hiểu th&ecirc;m</a></p>\r\n\r\n<p><img alt=\"tai nghe công nghệ thông minh trong nhà hàng\" src=\"https://www.sony.com.vn/image/ed4dc5edda83ca57d69aeeacd2893b40?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>C&ocirc;ng nghệ nghe th&ocirc;ng minh</p>\r\n\r\n<p><a href=\"https://www.sony.com.vn/electronics/tai-nghe-choang-dau-tai-nghe/wh-1000xm4#intelligent-tech\" tabindex=\"0\" target=\"_self\" title=\"Tìm hiểu thêm\">T&igrave;m hiểu th&ecirc;m</a></p>\r\n\r\n<p>C&ocirc;ng nghệ chống ồn h&agrave;ng đầu<sup>2</sup></p>\r\n\r\n<p>D&ugrave; bạn bay đường d&agrave;i hay thư gi&atilde;n trong qu&aacute;n c&agrave; ph&ecirc;, tai nghe WH-1000XM4 với hiệu suất chống ồn tốt nhất từ trước đến nay của ch&uacute;ng t&ocirc;i sẽ loại bỏ nhiều hơn nữa những &acirc;m thanh thuộc dải tần cao v&agrave; trung.</p>\r\n\r\n<p><a href=\"https://www.sony.com.vn/electronics/tai-nghe-choang-dau-tai-nghe/wh-1000xm4#\" target=\"_self\" title=\"Phát Video\">Ph&aacute;t Video</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ocirc;ng nghệ biến giấc mơ th&agrave;nh hiện thực</p>\r\n\r\n<p>Với hai micro tr&ecirc;n mỗi v&agrave;nh tai nghe, c&ocirc;ng nghệ cảm biến tiếng ồn k&eacute;p thu tiếng ồn m&ocirc;i trường xung quanh v&agrave; truyền dữ liệu đến Bộ xử l&yacute; chống ồn HD QN1. Sau đ&oacute;, bằng c&aacute;ch sử dụng thuật to&aacute;n mới, Bộ xử l&yacute; chống ồn HD QN1 sẽ xử l&yacute; chống ồn trong thời gian thực cho nhiều m&ocirc;i trường &acirc;m thanh. C&ugrave;ng với Bluetooth&reg; Audio SoC (System on Chip) mới, tai nghe cảm nhận v&agrave; điều chỉnh theo t&iacute;n hiệu &acirc;m nhạc v&agrave; tiếng ồn, cũng như c&aacute;c đặc t&iacute;nh &acirc;m thanh giữa bộ m&agrave;ng loa v&agrave; tai, ở mức hơn 700 lần/gi&acirc;y.</p>\r\n\r\n<p><img alt=\"Logo Digital NC\" src=\"https://www.sony.com.vn/image/4be5135fba3a6a746b299fbe2a26b394?fmt=png-alpha&amp;scl=1\" /></p>\r\n\r\n<p><img alt=\"Chip Bộ xử lý chống ồn HD QN1\" src=\"https://www.sony.com.vn/image/91fca93eec9c89b6000cb67f935d00b2?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tối ưu h&oacute;a c&ocirc;ng nghệ chống ồn</p>\r\n\r\n<p>Để tối đa h&oacute;a hiệu suất chống ồn, tai nghe WH-1000XM4 t&iacute;ch hợp hai c&ocirc;ng nghệ: Tr&igrave;nh tối ưu h&oacute;a c&ocirc;ng nghệ chống ồn c&aacute; nh&acirc;n được thiết kế d&agrave;nh ri&ecirc;ng cho bạn v&agrave; Tối ưu h&oacute;a &aacute;p suất kh&iacute; quyển được thiết kế d&agrave;nh ri&ecirc;ng cho chuyến đi bằng m&aacute;y bay.</p>\r\n\r\n<p>Điều khiển &acirc;m thanh xung quanh</p>\r\n\r\n<p>Ứng dụng Sony | Headphones Connect gi&uacute;p điều chỉnh &acirc;m thanh xung quanh để loại bỏ tiếng ồn m&agrave; vẫn kh&ocirc;ng bỏ s&oacute;t những &acirc;m thanh quan trọng như th&ocirc;ng b&aacute;o t&agrave;u xe khi bạn vừa nghe vừa di chuyển.</p>\r\n\r\n<p><img alt=\"Ảnh cận cảnh vành tai nghe bên trái của tai nghe Wh-1000xm4\" src=\"https://www.sony.com.vn/image/10517a29b497c2f31fb2cc1d1839f93f?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>Tự do kh&ocirc;ng d&acirc;y, &acirc;m thanh đỉnh cao</p>\r\n\r\n<p>LDAC truyền dữ liệu nhiều hơn xấp xỉ ba lần (ở tốc độ truyền tối đa 990 kbps) so với &acirc;m thanh BLUETOOTH&reg; th&ocirc;ng thường, cho bạn thưởng thức nội dung c&oacute; &Acirc;m thanh độ ph&acirc;n giải cao với chất lượng vượt trội, gần với chất lượng khi truyền qua kết nối c&oacute; d&acirc;y chuy&ecirc;n dụng.</p>', '8490000', '8000000', '5d02da5df552836db894cead8a68f5f338.webp', 'tai-nghe,sony', 1, NULL, NULL),
(14, 3, 1, 'tai nghe không dây WH-1000XM3', '200', 0, 'Tai nghe WH-1000XM3 cho bạn thả mình trong không gian tĩnh lặng với những cải tiến tinh vi hơn cho công nghệ chống ồn dẫn đầu ngành của chúng tôi', '<p>Sự y&ecirc;n tĩnh ở tầm cao mới</p>\r\n\r\n<p>Kh&aacute;m ph&aacute; trải nghiệm tuyệt vời khi chỉ c&oacute; bạn v&agrave; &acirc;m nhạc nhờ t&iacute;nh năng chống ồn loại bỏ &acirc;m thanh b&ecirc;n ngo&agrave;i một c&aacute;ch ưu việt. Tai nghe đ&atilde; trở n&ecirc;n th&ocirc;ng minh hơn bao giờ hết với c&ocirc;ng nghệ chống ồn ti&ecirc;n tiến như Chỉnh &acirc;m th&iacute;ch ứng c&ugrave;ng sự tiện lợi đến từ những t&iacute;nh năng th&ocirc;ng minh như Ch&uacute; &yacute; nhanh, khả năng tương th&iacute;ch với Trợ l&yacute; giọng n&oacute;i cũng như c&aacute;c thao t&aacute;c điều khiển cảm ứng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sony WH-1000XM3&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tai nghe kh&ocirc;ng d&acirc;y với c&ocirc;ng nghệ chống ồn&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đắm ch&igrave;m trong kh&ocirc;ng gian tĩnh lặng</p>\r\n\r\n<p>Tho&aacute;t khỏi những &acirc;m thanh n&aacute;o nhiệt của th&agrave;nh phố với c&ocirc;ng nghệ chống ồn dẫn đầu ng&agrave;nh&nbsp;<sup>1</sup>. WH-1000XM3 sở hữu c&ocirc;ng nghệ chống ồn ti&ecirc;n tiến nhất của ch&uacute;ng t&ocirc;i từ trước đến nay với miếng đệm tai mềm vừa kh&iacute;t v&agrave; Bộ xử l&yacute; chống ồn HD QN1.</p>\r\n\r\n<p>C&ocirc;ng suất cao hơn = &iacute;t tiếng ồn hơn nữa</p>\r\n\r\n<p>Với Bộ xử l&yacute; chống ồn HD QN1 mới, khả năng chống ồn đ&atilde; được tăng cường đ&aacute;ng kể. Việc sở hữu một bộ xử l&yacute; chống ồn chuy&ecirc;n dụng cũng đảm bảo khả năng t&aacute;i tạo &acirc;m nhạc chất lượng cao nhờ hiệu suất của bộ xử l&yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Bộ xử lý chống ồn\" src=\"https://www.sony.com.vn/image/ea77406f42ee2ca24daef87ba596d100?fmt=jpeg&amp;wid=720&amp;qlt=85\" /></p>\r\n\r\n<p>Chống ồn hiệu quả</p>\r\n\r\n<p>Hầu hết tai nghe c&oacute; c&ocirc;ng nghệ chống ồn đều được thiết kế nhằm chặn tiếng ồn của xe cộ khi bạn đi đường. Vượt xa khả năng đ&oacute;, tai nghe WH-1000XM3 c&ograve;n chặn được tiếng ồn th&ocirc;ng thường hằng ng&agrave;y như tiếng n&oacute;i v&agrave; nhiều tiếng động kh&aacute;c hiện hữu trong kh&ocirc;ng gian th&agrave;nh phố.</p>\r\n\r\n<p><img alt=\"Logo công nghệ chống ồn kỹ thuật số\" src=\"https://www.sony.com.vn/image/4be5135fba3a6a746b299fbe2a26b394?fmt=jpeg&amp;scl=1&amp;qlt=85\" /></p>\r\n\r\n<p>Chống ồn khi đang bay</p>\r\n\r\n<p>T&iacute;nh năng Tối ưu h&oacute;a theo &aacute;p suất kh&ocirc;ng kh&iacute; độc đ&aacute;o cho chất lượng &acirc;m thanh tối ưu khi ở tr&ecirc;n cao.<sup>3</sup></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tối ưu h&oacute;a &aacute;p suất kh&ocirc;ng kh&iacute;</li>\r\n	<li>Tr&igrave;nh tối ưu h&oacute;a NC c&aacute; nh&acirc;n</li>\r\n</ul>\r\n\r\n<p>Thoải m&aacute;i sử dụng kh&ocirc;ng d&acirc;y, &Acirc;m thanh ngất ng&acirc;y trong y&ecirc;n lặng</p>\r\n\r\n<p>LDAC truyền dữ liệu nhiều hơn xấp xỉ ba lần (ở tốc độ truyền tối đa l&agrave; 990 kbps) so với &acirc;m thanh kh&ocirc;ng d&acirc;y BLUETOOTH&reg; th&ocirc;ng thường, cho ph&eacute;p bạn thưởng thức nội dung c&oacute; &Acirc;m thanh độ ph&acirc;n giải cao với chất lượng vượt trội, gần với chất lượng khi truyền qua kết nối c&oacute; d&acirc;y chuy&ecirc;n dụng.</p>\r\n\r\n<p><img alt=\"LDAC &amp; Bluetooth\" src=\"https://www.sony.com.vn/image/6dffe09916913dc6cb27eddaee59936d?fmt=png-alpha&amp;scl=1\" /></p>\r\n\r\n<p><img alt=\"Màng loa 40 mm\" src=\"https://www.sony.com.vn/image/aedad7c818302db008251f907acd8cf9?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>Chất lượng &acirc;m thanh đ&aacute;ng tin cậy</p>\r\n\r\n<p>Bộ khuếch đại t&iacute;ch hợp trong Bộ xử l&yacute; chống ồn HD QN1 cho tỉ lệ nhiễu t&iacute;n hiệu vượt trội nhất trong ph&acirc;n kh&uacute;c, độ m&eacute;o tiếng thấp cho thiết bị di động c&ugrave;ng chất lượng &acirc;m thanh ấn tượng. Với m&agrave;ng loa 40 mm mạnh mẽ c&ugrave;ng m&agrave;ng chắn Polymer tinh thể lỏng (LCP), d&ograve;ng tai nghe n&agrave;y c&oacute; thể xử l&yacute; ho&agrave;n hảo những nhịp mạnh v&agrave; t&aacute;i tạo được to&agrave;n dải tần số l&ecirc;n đến 40 kHz.</p>\r\n\r\n<p><img alt=\"Logo Âm thanh độ phân giải cao\" src=\"https://www.sony.com.vn/image/c3c172b68167fbeac0928353dc9e0724?fmt=png-alpha&amp;scl=1\" /></p>', '8500000', '8000000', 'images97.jpg', 'tai-nghe,sony', 1, NULL, NULL),
(15, 3, 1, 'Tai nghe MDR-1AM2', '100', 0, 'Âm thanh chuẩn xác và độ sắc nét trên toàn phổ âm của tai nghe MDR-1AM2 sẽ chinh phục trái tim của người yêu nhạc trên toàn thế giới', '<p><img alt=\"Logo Âm thanh độ phân giải cao\" src=\"https://www.sony.com.vn/image/05bb7c95a5be2c5e6271bd62feecc8a3?fmt=png-alpha&amp;wid=131\" /></p>\r\n\r\n<p>Sống động như biểu diễn trực tiếp</p>\r\n\r\n<p>Thưởng thức những sắc th&aacute;i &acirc;m thanh tinh tế nhất chất lượng hơn cả CD với &Acirc;m thanh độ ph&acirc;n giải cao. Niềm đam m&ecirc; &acirc;m nhạc quy tụ trong từng chi tiết từ t&iacute;n hiệu đến loa mang đến cảm gi&aacute;c như nghệ sĩ đang biểu diễn trước mặt bạn.</p>\r\n\r\n<p><a href=\"https://www.sony.com.vn/electronics/am-thanh-do-phan-giai-cao\" tabindex=\"0\" target=\"_self\" title=\"Tìm hiểu về âm thanh Hi-Res\">T&igrave;m hiểu về &acirc;m thanh Hi-Res</a></p>\r\n\r\n<p>Phong c&aacute;ch cổ điển. &Acirc;m thanh trong. Cảm gi&aacute;c thoải m&aacute;i.&nbsp;</p>\r\n\r\n<p>Với &acirc;m thanh chuẩn x&aacute;c v&agrave; độ r&otilde; n&eacute;t của phổ &acirc;m đầy đủ, tai nghe MDR-1AM2 l&agrave;m m&ecirc; hoặc giới y&ecirc;u nhạc ở mọi nơi. Bạn c&oacute; thể d&ugrave;ng tai nghe thoải m&aacute;i trong nhiều giờ liền nhờ thiết kế gọn nhẹ v&agrave; đệm tai &ecirc;m &aacute;i.</p>\r\n\r\n<p><img alt=\"Biểu tượng màng loa HD 40 mm\" src=\"https://www.sony.com.vn/image/50c8423ae71e42602c0ab263788fc042?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>M&agrave;ng loa HD 40 mm</p>\r\n\r\n<p>Tận hưởng &acirc;m thanh trong trẻo, sống động tr&ecirc;n tất cả dải tần từ m&agrave;ng loa HD 40 mm.</p>\r\n\r\n<p><img alt=\"Biểu tượng 100 kHz\" src=\"https://www.sony.com.vn/image/bd897bede9821e0c3f60fdd1e1023a17?fmt=png-alpha&amp;wid=400\" /></p>\r\n\r\n<p>Dải tần số rộng</p>\r\n\r\n<p>Thưởng thức những nốt trầm s&acirc;u lắng, những nốt cao r&eacute;o rắt với tần số phản hồi to&agrave;n dải từ 3 Hz đến 100 kHz.<sup>2</sup></p>\r\n\r\n<p>Được tinh chỉnh cho &acirc;m thanh r&otilde; n&eacute;t hơn</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i vừa mới ph&aacute;t triển m&agrave;ng loa HD 40 mm rất nhẹ c&oacute; lớp m&agrave;ng polyme tinh thể lỏng (LCP) được tr&aacute;ng nh&ocirc;m mang đến &acirc;m thanh trong trẻo, sống động, đặc biệt l&agrave; c&aacute;c &acirc;m trung v&agrave; cao - ho&agrave;n hảo khi cần tập trung v&agrave;o thanh &acirc;m - c&ugrave;ng với độ nhạy mượt m&agrave; v&agrave; chuẩn x&aacute;c tr&ecirc;n khắp dải tần số.</p>\r\n\r\n<p><img alt=\"Lưới loa có họa tiết theo quy luật của dãy Fibonacci\" src=\"https://www.sony.com.vn/image/d9b1f0b31ccfd3f81a1de85b2401c8ae?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>&Acirc;m bổng với cao độ ho&agrave;n hảo</p>\r\n\r\n<p>Được thiết kế đặc biệt c&oacute; họa tiết theo quy luật của d&atilde;y Fibonacci, lưới vừa bảo vệ m&agrave;ng loa, vừa giảm thiểu mất &acirc;m của c&aacute;c tần số độ ph&acirc;n giải cao. Nhờ vậy, bạn c&oacute; thể thưởng thức từng chi tiết của c&aacute;c nốt nhạc cực cao.</p>\r\n\r\n<p>&Acirc;m thanh thực sự trung thực</p>\r\n\r\n<p>C&aacute;p kết nối c&acirc;n bằng &Phi; 4,4 mm k&egrave;m theo tai nghe gi&uacute;p đảm bảo độ r&otilde; của mọi &acirc;m thanh, cho bạn thưởng thức mọi chi tiết tinh tế của &Acirc;m thanh độ ph&acirc;n giải cao.&nbsp;</p>\r\n\r\n<p><img alt=\"Ảnh minh họa cáp âm thanh cân bằng\" src=\"https://www.sony.com.vn/image/b211920cc0b3fac8787900b2c58926d2?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>&Acirc;m thanh c&acirc;n bằng</p>\r\n\r\n<p>C&aacute;p c&acirc;n bằng ho&agrave;n to&agrave;n t&aacute;ch rời hai k&ecirc;nh phải v&agrave; tr&aacute;i bằng c&aacute;ch bổ sung một kết nối tiếp đất để giảm nhiễu xuy&ecirc;n &acirc;m v&agrave; tăng hiệu quả &acirc;m thanh, cho chất lượng vượt trội.</p>\r\n\r\n<p><img alt=\"Ảnh minh họa cáp đồng chống oxy hóa phủ bạc\" src=\"https://www.sony.com.vn/image/77be487bcb626e2d94730376beb72675?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Đ&ocirc;̀ng ch&ocirc;́ng oxy hóa phủ bạc&nbsp;</p>\r\n\r\n<p>Bốn c&aacute;p đồng chống oxy h&oacute;a (OFC) phủ bạc gi&uacute;p giảm thiểu điện trở v&agrave; hiện tượng mất t&iacute;n hiệu truyền. Kết quả l&agrave; &acirc;m thanh kh&ocirc;ng bị giảm chất lượng, mọi chi tiết c&agrave;ng sắc n&eacute;t hơn, &acirc;m bổng c&agrave;ng mượt m&agrave; hơn.&nbsp;</p>\r\n\r\n<p>Tai nghe phong c&aacute;ch, thoải m&aacute;i v&ocirc; c&ugrave;ng</p>\r\n\r\n<p>Thiết kế khung si&ecirc;u nhẹ v&agrave; đệm tai tho&aacute;ng kh&iacute; cho ph&eacute;p bạn sử dụng tai nghe theo phong c&aacute;ch ri&ecirc;ng m&agrave; vẫn thoải m&aacute;i trong nhiều giờ liền.</p>\r\n\r\n<p><img alt=\"Ảnh cận cảnh đệm tai\" src=\"https://www.sony.com.vn/image/a216b89f48d908562cdc8a9ab31424a3?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Miếng đệm tai nghe c&oacute; thiết kế giảm &aacute;p lực l&ecirc;n tai</p>\r\n\r\n<p>Thoải m&aacute;i nghe trong nhiều giờ liền với đệm tai bằng da tổng hợp &ecirc;m &aacute;i. Thiết kế mặt ngo&agrave;i k&iacute;n gi&uacute;p giảm thiểu hiện tượng mất &acirc;m.</p>\r\n\r\n<p><img alt=\"Tai nghe choàng đầu\" src=\"https://www.sony.com.vn/image/82c9d9dddb3ccce27f203f1fc6b4dff7?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Si&ecirc;u nhẹ</p>\r\n\r\n<p>Khung si&ecirc;u nhẹ tho&aacute;t kh&iacute; cho bạn nghe l&acirc;u hơn m&agrave; vẫn ho&agrave;n to&agrave;n thoải m&aacute;i. Vật liệu sản xuất tai nghe cao cấp cho độ bền cao.</p>', '6900000', '6300000', 'tải xuống (4)23.jpg', 'tai-nghe,sony', 1, NULL, NULL),
(16, 3, 1, 'Tai nghe In-ear IER-Z1R', '200', 0, 'Thưởng thức khả năng tái tạo âm thanh chân thực đến không ngờ với tai nghe In-ear IER-Z1R.', '<p>Kh&aacute;m ph&aacute; IER-Z1R</p>\r\n\r\n<p>Tìm hi&ecirc;̉u v&ecirc;̀ c&ocirc;ng ngh&ecirc;̣ và cảm hứng &acirc;̉n sau tai nghe In-ear cao c&acirc;́p nh&acirc;́t của chúng t&ocirc;i, tai nghe IER-Z1R.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&aacute;m ph&aacute; IER-Z1R</p>\r\n\r\n<p>Tìm hi&ecirc;̉u v&ecirc;̀ c&ocirc;ng ngh&ecirc;̣ và cảm hứng &acirc;̉n sau tai nghe In-ear cao c&acirc;́p nh&acirc;́t của chúng t&ocirc;i, tai nghe IER-Z1R.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiết kế lay động cảm quan&nbsp;</p>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i đ&atilde; thiết kế mới ho&agrave;n to&agrave;n c&aacute;c driver trong tai nghe IER-Z1R, để tất cả phối hợp h&agrave;i h&ograve;a trọn vẹn. Dấu ấn &acirc;m thanh nhất qu&aacute;n được truyền đi khắp từng driver như thể chỉ c&oacute; một driver ho&agrave;n mỹ duy nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>[1] Vỏ bảo vệ, [2] M&agrave;ng chắn LCP mạ nh&ocirc;m, [3] Mạch từ t&iacute;nh</p>\r\n\r\n<p>Nghe thật r&otilde; nhờ tần số phản hồi l&ecirc;n tới 100 kHz&nbsp;</p>\r\n\r\n<p>Loa si&ecirc;u thanh Tweeter trong IER-Z1R được ph&aacute;t triển gi&uacute;p mở rộng dải tần số si&ecirc;u cao với m&agrave;ng chắn LCP phủ nh&ocirc;m v&agrave; mạch từ t&iacute;nh ngo&agrave;i. Tốc độ phản hồi cực nhanh ho&agrave;n to&agrave;n tu&acirc;n theo t&iacute;nh chất chuyển động nhanh kh&ocirc;ng ngừng của &acirc;m nhạc, mang đến cho bạn bầu kh&ocirc;ng kh&iacute; như trong ph&ograve;ng h&ograve;a nhạc trực tiếp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Driver 5 mm</li>\r\n	<li>Driver 12 mm</li>\r\n	<li>M&agrave;ng tai nghe loại hở</li>\r\n	<li>Mạng</li>\r\n</ul>\r\n\r\n<p>&Acirc;m thanh được điều khiển một c&aacute;ch tự nhi&ecirc;n</p>\r\n\r\n<p>Kinh nghiệm d&agrave;y dạn c&ugrave;ng cảm nhận phong ph&uacute; của đội ngũ kỹ sư của ch&uacute;ng t&ocirc;i ch&iacute;nh l&agrave; ch&igrave;a kh&oacute;a trong thiết kế &acirc;m thanh, mang đến &acirc;m thanh ch&acirc;n thực nhất. Cấu tr&uacute;c &acirc;m thanh được tinh chỉnh IER-Z1R dẫn &acirc;m thanh từ nh&oacute;m ba bộ m&agrave;ng loa như thể nh&oacute;m ba bộ m&agrave;ng loa n&agrave;y l&agrave; một m&agrave;ng loa l&yacute; tưởng duy nhất.</p>\r\n\r\n<p><img alt=\"Đường dẫn âm thanh\" src=\"https://www.sony.com.vn/image/15a8601226adbe35d28aa471cb7caac7?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>Đường dẫn &acirc;m thanh thẳng ho&agrave;n hảo</p>\r\n\r\n<p>Loa si&ecirc;u thanh Tweeter nằm ở vị tr&iacute; đồng trục với lỗ tai. Nhờ c&aacute;ch bố tr&iacute; n&agrave;y, những nốt tần số cực cao được truyền thẳng đến tai bạn một c&aacute;ch ch&iacute;nh x&aacute;c với tần số phản hồi si&ecirc;u rộng, l&ecirc;n tới 100 kHz.</p>\r\n\r\n<p>Cấu tr&uacute;c pha được tinh chỉnh cho h&ograve;a &acirc;m ch&acirc;n thực</p>\r\n\r\n<p>Sự nhất qu&aacute;n về mặt thời gian của &acirc;m thanh từ ba m&agrave;ng loa cũng quan trọng như tần số phản hồi rộng của ba m&agrave;ng loa đ&oacute;. Cấu tr&uacute;c pha được tinh chỉnh đảm bảo s&oacute;ng &acirc;m thanh được căn chỉnh, th&ocirc;ng qua điều chỉnh độ ch&iacute;nh x&aacute;c của chiều rộng v&agrave; chiều d&agrave;i của mỗi đường dẫn &acirc;m thanh. Cấu tr&uacute;c hợp kim ma nh&ecirc; cũng loại bỏ th&ecirc;m hiện tượng rung v&agrave; cộng hưởng kh&ocirc;ng mong muốn, để mang đến độ trong trẻo v&agrave; sống động ho&agrave;n mỹ cho nhiều thể loại nhạc.</p>\r\n\r\n<p><img alt=\"Bộ màng loa và vỏ trong\" src=\"https://www.sony.com.vn/image/123f5d358109b786ef5ad505338de3f6?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p><img alt=\"Ống âm học\" src=\"https://www.sony.com.vn/image/b2f7bc2fa5ecf63fbadf1c47df2a32ca?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>T&iacute;nh năng điều khiển Sound Space Control cho kh&ocirc;ng gian &acirc;m thanh rộng</p>\r\n\r\n<p>Để c&oacute; được &acirc;m thanh c&acirc;n bằng khi chuyển từ tần số thấp đến tần số cao, ống &acirc;m sẽ kết nối với khoang nằm sau bộ m&agrave;ng loa. C&ocirc;ng nghệ n&agrave;y cho khoảng &acirc;m rộng với những nốt rất đẹp v&agrave; thật tai.</p>', '41990000', '39000000', 'tai-nghe-sony-ier-z1r-86ch715.jpg', 'tai-nghe,sony', 1, NULL, NULL),
(17, 3, 1, 'Tai nghe kiểm âm IER-M9', '200', 0, 'Khám phá một thế giới phong cách âm nhạc mới, cả trong phòng thu lẫn trên sân khấu', '<p>T&aacute;i tạo ch&acirc;n thực mọi m&agrave;n biểu diễn</p>\r\n\r\n<p>Với hệ thống phần ứng c&acirc;n bằng Penta bao gồm loa si&ecirc;u thanh tweeter ma nh&ecirc;, vỏ trong bằng ma nh&ecirc; t&iacute;ch hợp sẵn v&agrave; đường dẫn &acirc;m thanh tối ưu, tai nghe kiểm &acirc;m s&acirc;n khấu n&agrave;y truyền tải ch&iacute;nh x&aacute;c cả những &acirc;m thanh v&agrave; cảm x&uacute;c tinh tế nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Hệ thống BA Penta\" src=\"https://www.sony.com.vn/image/a27d447ecc6c9387b9117625f7d6bed5?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>Hệ thống phần ứng c&acirc;n bằng Penta cho &acirc;m thanh s&acirc;n khấu tối ưu</p>\r\n\r\n<p>Để c&oacute; nốt cao trong vắt, &acirc;m sắc tự nhi&ecirc;n v&agrave; cảm x&uacute;c thuần khiết cho m&agrave;n biểu diễn, IER-M9 c&oacute; hệ thống BA Penta gồm loa si&ecirc;u thanh tweeter ma nh&ecirc;. BA h&igrave;nh chữ T nguy&ecirc;n bản của Sony trực tiếp điều khiển m&agrave;ng chắn cho chuyển động tuyến t&iacute;nh, nốt cao trung thực v&agrave; trong trẻo hơn.</p>\r\n\r\n<p>Loa si&ecirc;u thanh tweeter c&oacute; m&agrave;ng chắn bằng ma nh&ecirc;&nbsp;</p>\r\n\r\n<p>M&agrave;ng chắn loa si&ecirc;u thanh tweeter của IER-M9 l&agrave;m bằng hợp kim ma nh&ecirc; vừa nhẹ vừa cứng, truyền tải ho&agrave;n hảo nốt đệm nhanh, s&ocirc;i nổi trong m&agrave;n biểu diễn. Thiết kế độc đ&aacute;o của bộ BA cũng mang đến độ cộng hưởng si&ecirc;u thấp nhờ v&agrave;o mức tho&aacute;t &acirc;m cao. Hệ thống d&acirc;y b&ecirc;n trong cũng được n&acirc;ng cấp, với cuộn d&acirc;y &acirc;m thanh bằng đồng mạ bạc mới v&agrave; đầu nối mạ v&agrave;ng, gi&uacute;p t&aacute;i tạo ch&acirc;n thực &acirc;m thanh m&agrave; kh&ocirc;ng l&agrave;m mất đi những t&iacute;n hiệu nhỏ nhất, khắc họa ho&agrave;n hảo &acirc;m độ, h&ograve;a &acirc;m v&agrave; cảm x&uacute;c.</p>\r\n\r\n<p><img alt=\"Loa siêu thanh tweeter có màng chắn bằng ma nhê\" src=\"https://www.sony.com.vn/image/e5e0430175e1070b374b49544a8a563c?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p><img alt=\"Tụ màng mỏng cho sản phẩm âm thanh\" src=\"https://www.sony.com.vn/image/a13376b5e0ecf34984ab5b55092b5155?fmt=png-alpha&amp;wid=720\" /></p>\r\n\r\n<p>Tụ m&agrave;ng mỏng chuy&ecirc;n dụng cho sản phẩm &acirc;m thanh gi&uacute;p giảm nhiễu &acirc;m&nbsp;</p>\r\n\r\n<p>Tụ m&agrave;ng mỏng chuy&ecirc;n dụng cho sản phẩm &acirc;m thanh của Sony trong mạch ph&acirc;n tần gi&uacute;p giảm m&eacute;o &acirc;m hiệu quả. Tụ m&agrave;ng mỏng cho sản phẩm &acirc;m thanh của IER-M9 mang đến &acirc;m thanh tinh tế. Ngo&agrave;i ra, mối h&agrave;n chuy&ecirc;n dụng cho sản phẩm &acirc;m thanh n&agrave;y cũng gi&uacute;p giảm thiểu hiện tượng thất tho&aacute;t trong đường dẫn t&iacute;n hiệu.&nbsp;</p>\r\n\r\n<p>Được thiết kế ho&agrave;n hảo cho độ c&acirc;n bằng &acirc;m ch&iacute;nh x&aacute;c</p>\r\n\r\n<p>Với vỏ trong bằng ma nh&ecirc; t&iacute;ch hợp sẵn v&agrave; đường dẫn &acirc;m thanh tối ưu, tai nghe IER-M9 mang đến &acirc;m sắc c&acirc;n bằng v&agrave; mọi sắc th&aacute;i của m&agrave;n biểu diễn.</p>\r\n\r\n<p><img alt=\"Vỏ BA bằng ma nhê tích hợp sẵn\" src=\"https://www.sony.com.vn/image/5f276e0d07a35829812523947cda2589?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Vỏ trong bằng ma nh&ecirc; t&iacute;ch hợp sẵn để giảm rung</p>\r\n\r\n<p>Phần vỏ cứng giữ cho bộ BA lu&ocirc;n nằm chắc chắn, gi&uacute;p giảm rung v&agrave; khiến cho &acirc;m thanh lu&ocirc;n trong trẻo, kh&ocirc;ng l&agrave;m mất đi d&ugrave; chỉ một chi tiết nhỏ trong thanh &acirc;m.</p>\r\n\r\n<p><img alt=\"Đường dẫn âm tối ưu\" src=\"https://www.sony.com.vn/image/d8d3962f42d7b4f6f676266c6da6a92f?fmt=png-alpha&amp;wid=600\" /></p>\r\n\r\n<p>Đường dẫn &acirc;m tối ưu</p>\r\n\r\n<p>Đường dẫn &acirc;m thanh rộng v&agrave; ngắn được l&agrave;m bằng hợp kim ma nh&ecirc; gi&uacute;p ổn định tần số, mang lại &acirc;m thanh kiểm &acirc;m c&oacute; độ ch&iacute;nh x&aacute;c vừa l&ograve;ng đ&ocirc;i tai kh&oacute; t&iacute;nh nhất.</p>', '26990000', '25300000', 'techzones-sony-ier-m9-19.jpg', 'tai-nghe,sony', 1, NULL, NULL);
INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_quantity`, `product_sold`, `product_desc`, `product_content`, `product_price`, `price_cost`, `product_image`, `product_tags`, `product_status`, `created_at`, `updated_at`) VALUES
(18, 3, 2, 'Tai nghe Microsoft Surface Earbuds', '200', 0, 'Vừa mới đây, Microsoft đã chính thức bán ra chiếc tai nghe true wireless đầu tiên của mình, Surface Earbuds', '<p>Mặc d&ugrave; h&atilde;ng đ&atilde; điều chỉnh mức gi&aacute; của sản phẩm xuống 199 USD so với 249 USD như dự kiến ban đầu để sản phẩm c&oacute; t&iacute;nh cạnh tranh cao hơn so với c&aacute;c đối thủ. Nhưng liệu mức gi&aacute; như vậy đ&atilde; đủ cạnh tranh chưa? N&oacute; c&oacute; điểm g&igrave; vượt trội hơn so với c&aacute;c đối thủ tr&ecirc;n thị trường? V&agrave; liệu Surface Earbuds c&oacute; đem lại trải nghiệm sử dụng thực sự tốt cho người mua?</p>\r\n\r\n<p>H&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i t&igrave;m hiểu trong b&agrave;i viết dưới đ&acirc;y!</p>\r\n\r\n<h2>Thiết kế độc đ&aacute;o</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"earbuds on ear\" height=\"1185\" sizes=\"(max-width: 1778px) 100vw, 1778px\" src=\"https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-on-ear.jpg\" srcset=\"https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-on-ear.jpg 1778w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-on-ear-293x195.jpg 293w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-on-ear-768x512.jpg 768w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-on-ear-1536x1024.jpg 1536w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-on-ear-510x340.jpg 510w\" width=\"1778\" /></p>\r\n\r\n<p>(earbuds on ear &ndash; nguồn: Microsoft)</p>\r\n\r\n<p>Dĩ nhi&ecirc;n, dưới g&oacute;c độ l&agrave; một chiếc tai nghe True Wireless Stereo, Surface Earbuds c&oacute; thiết kế kh&aacute; ti&ecirc;u chuẩn như mọi thiết bị kh&aacute;c với 2 phần tai nghe ri&ecirc;ng biệt v&agrave; phần dock sạc pin vừa để chứa cặp tai nghe, vừa để cung cấp năng lượng cho ch&uacute;ng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, cặp tai nghe Surface Earbuds của Microsoft lại c&oacute; thiết kế rất độc đ&aacute;o, c&oacute; một kh&ocirc;ng hai tr&ecirc;n thị trường.</p>\r\n\r\n<p>Đầu ti&ecirc;n, n&oacute; sẽ c&oacute; thiết kế dạng earbuds tương tự như đối thủ Airpods chứ kh&ocirc;ng phải dạng in-ear. Nhưng thay v&igrave; phần ống tai nghe d&agrave;i như Airpods th&igrave; Surface Earbuds lại c&oacute; phần housing h&igrave;nh tr&ograve;n v&agrave; rất to, kh&aacute;c biệt ho&agrave;n to&agrave;n với c&aacute;c sản phẩm tai nghe true wireless kh&aacute;c từ trước đến nay.</p>\r\n\r\n<p>Thiết kế n&agrave;y kh&ocirc;ng chỉ g&acirc;y ch&uacute; &yacute; cho người kh&aacute;c về mặt thẩm mỹ, m&agrave; c&ograve;n cung cấp một bề mặt &ldquo;si&ecirc;u to khổng lồ&rdquo; d&agrave;nh cho người d&ugrave;ng thoải m&aacute;i thao t&aacute;c c&aacute;c &ldquo;cử chỉ th&ocirc;ng minh&rdquo; tr&ecirc;n sản phẩm. Tuy nhi&ecirc;n do thiết kế phần tai nghe nh&ocirc; ra kh&aacute; nhiều so với tai, n&ecirc;n đ&ocirc;i l&uacute;c sẽ g&acirc;y ra cảm gi&aacute;c vướng v&iacute;u.</p>\r\n\r\n<p>Phần c&ograve;n lại của Surface Earbuds cũng rất đẹp với t&ocirc;ng m&agrave;u trắng xuy&ecirc;n suốt thiết kế tổng thể của thiết bị. Phần g&agrave;i tai c&oacute; k&iacute;ch thước hợp l&yacute; v&agrave; c&oacute; thể thay đổi &ldquo;tips&rdquo; tương tự như c&aacute;i tai nghe in-ear với 3 k&iacute;ch thước S/M/L phổ biến được Microsoft để sẵn trong hộp.</p>\r\n\r\n<p>Đ&acirc;y quả l&agrave; điểm th&ocirc;ng minh trong thiết kế của Surface Earbuds khi vừa c&oacute; thể cung cấp thiết kế c&ocirc;ng th&aacute;i học, đeo l&acirc;u &iacute;t bị đau tai, m&agrave; vừa c&oacute; thể thay đổi k&iacute;ch thước cho vừa với tai của người người kh&aacute;c nhau, tr&aacute;nh t&igrave;nh trạng đeo lỏng dễ rơi rớt. Th&ecirc;m v&agrave;o đ&oacute;, n&oacute; cũng c&oacute; đ&egrave;n b&aacute;o hiệu mức pin như nhiều sản phẩm kh&aacute;c tr&ecirc;n thị trường như Pamu Slide hay Galaxy Buds plus.</p>\r\n\r\n<p>Phần dock sạc của Surface Earbuds cũng tone sur tone với cặp tai nghe khi cũng được phủ to&agrave;n bộ bằng một m&agrave;u trắng tương đồng. Chiếc dock n&agrave;y kh&ocirc;ng c&oacute; thiết kế g&igrave; qu&aacute; đặc sắc nhưng n&oacute; cho thấy sự đơn giản m&agrave; tinh tế của sản phẩm. Hạn chế của dock n&agrave;y l&agrave; n&oacute; hơi to, kh&oacute; c&oacute; thể đ&uacute;t v&agrave;o t&uacute;i quần như Airpods.</p>\r\n\r\n<p><img alt=\"earbuds cận cảnh 2\" height=\"417\" sizes=\"(max-width: 740px) 100vw, 740px\" src=\"https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-2.jpg\" srcset=\"https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-2.jpg 740w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-2-346x195.jpg 346w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-2-510x287.jpg 510w\" width=\"740\" /></p>\r\n\r\n<p>Iearbuds cận cảnh 2 &ndash; nguồn: Microsoft)</p>\r\n\r\n<h2>Thời lượng pin v&agrave; tốc độ sạc</h2>\r\n\r\n<p>Microsoft c&ocirc;ng bố thời gian sử dụng li&ecirc;n tục trong một lần sạc l&agrave; 8 giờ v&agrave; dock cung cấp th&ecirc;m 2 lần sạc đầy cả cặp tai nghe. Tức l&agrave; bạn sẽ c&oacute; thể sử dụng cặp tai nghe n&agrave;y trong 24H cho đến l&uacute;c phải cắm sạc ho&agrave;n to&agrave;n.</p>\r\n\r\n<p>Thời gian thực tế sử dụng m&agrave; ch&uacute;ng t&ocirc;i test được rơi v&agrave;o khoảng 7 tiếng d&ugrave;ng cặp tai nghe li&ecirc;n tục với mức &acirc;m lượng 60%. V&agrave; dock sạc cung cấp đủ 2 lần sạc đầy như theo c&ocirc;ng bố của nh&agrave; sản xuất. Mức pin n&agrave;y kh&aacute; tốt v&agrave; c&oacute; thể đ&aacute;p ứng được nhu cầu sử dụng kh&aacute; cao của nhiều người ti&ecirc;u d&ugrave;ng.</p>\r\n\r\n<p>Việc d&ugrave;ng thiết bị trong cả tuần l&agrave;m việc rồi mới phải cắm sạc l&agrave; điều ho&agrave;n to&agrave;n c&oacute; thể đạt được v&igrave; &iacute;t ai sử dụng li&ecirc;n tục tai nghe li&ecirc;n tục trong qu&aacute; nhiều giờ.</p>\r\n\r\n<p>Mức pin n&agrave;y cũng tương ứng với đối thủ như Airpods nhưng vẫn thua k&eacute;m xa một số sản phẩm c&oacute; thời lượng pin si&ecirc;u tr&acirc;u l&ecirc;n tới 60, thậm ch&iacute; 80 giờ sử dụng. Dĩ nhi&ecirc;n, thời lượng đ&oacute; cũng rất tốt nhưng trải nghiệm trong đời thực lại kh&ocirc;ng mấy cần thiết.</p>\r\n\r\n<p>Tốc độ sạc đầy cả tai nghe v&agrave; dock cũng kh&aacute; ổn với chỉ hơn 2 giờ đồng hồ. Khi cần d&ugrave;ng gấp, bạn cũng chỉ cần sạc 10 ph&uacute;t l&agrave; c&oacute; thể d&ugrave;ng cặp tai nghe th&ecirc;m 1 tiếng đồng hồ. Rất tiện cho l&uacute;c nhỡ nh&agrave;ng qu&ecirc;n kh&ocirc;ng sạc từ trước. Ngo&agrave;i ra dock c&ograve;n được trang bị cổng Type &ndash; C để hỗ trợ sạc, đ&acirc;y cũng l&agrave; một điểm đ&aacute;ng khen ngợi của sản phẩm.</p>\r\n\r\n<h2>Chất &acirc;m</h2>\r\n\r\n<p>Ch&uacute;ng t&ocirc;i kh&ocirc;ng phải l&agrave; audiophile n&ecirc;n cũng kh&ocirc;ng qu&aacute; kh&oacute; t&iacute;nh trong việc đ&aacute;nh gi&aacute; chất &acirc;m của tai nghe tuy nhi&ecirc;n khi sử dụng thực tế &acirc;m thanh của Surface Earbuds cho cảm gi&aacute;c kh&aacute; dễ nghe, c&oacute; phần hơi hiền v&agrave; kh&ocirc;ng qu&aacute; đặc sắc.</p>\r\n\r\n<p>Dải bass của thiết bị đủ d&ugrave;ng, kh&ocirc;ng nhiều lượng v&agrave; cũng kh&ocirc;ng c&oacute; độ nảy nhưng cũng kh&ocirc;ng phải dạng &ldquo;flat bass&rdquo; &ndash; tức l&agrave; chả nghe thấy t&iacute; bass n&agrave;o. Dải mid kh&aacute; hiền, &acirc;m thanh giọng h&aacute;t của ca sĩ kh&aacute; r&otilde; r&agrave;ng nhưng chưa c&oacute; mức độ &ldquo;th&aacute;nh th&oacute;t&rdquo; cần thiết. Dải treble th&igrave; chỉ tr&ograve;n vai khi khắc họa được một số &acirc;m thanh nhạc cụ kh&aacute; tốt nhưng lại hơi hời hợt với c&aacute;c tiếng s&aacute;o hoặc tiếng k&egrave;n trong một v&agrave;i trường hợp.</p>\r\n\r\n<p>Người d&ugrave;ng phổ th&ocirc;ng sẽ kh&aacute; dễ chịu khi sử dụng cặp tai nghe n&agrave;y nhưng những người hơi kh&oacute; t&iacute;nh về &acirc;m thanh một ch&uacute;t sẽ kh&ocirc;ng mấy ấn tượng với m&agrave;n thể hiện của Surface Earbuds.</p>\r\n\r\n<p>Vậy nếu &acirc;m thanh chỉ ở mức ổn, kh&ocirc;ng c&oacute; g&igrave; nổi trội so với c&aacute;c đối thủ như Airpods th&igrave; điểm mạnh của Surface Earbuds l&agrave; g&igrave;? Đ&oacute; ch&iacute;nh l&agrave; c&aacute;c t&iacute;nh năng th&ocirc;ng minh.</p>\r\n\r\n<p><img alt=\"earbuds cận cảnh 1\" height=\"417\" sizes=\"(max-width: 740px) 100vw, 740px\" src=\"https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-1.jpg\" srcset=\"https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-1.jpg 740w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-1-346x195.jpg 346w, https://newtechshop.vn/wp-content/uploads/2020/09/earbuds-can-canh-1-510x287.jpg 510w\" width=\"740\" /></p>\r\n\r\n<p>(earbuds cận cảnh 1 &ndash; nguồn: Microsoft)</p>', '6700000', '6300000', 'Surface-Earbuds53.png', 'tai-nghe,microsoft', 1, NULL, NULL),
(19, 3, 2, 'Microsoft Surface Headphones 2', '200', 0, 'Microsoft Surface Headphones 2 dòng tai nghe cao cấp đến từ hãng Microsoft.', '<p>Microsoft Surface Headphones 2 d&ograve;ng tai nghe cao cấp đến từ h&atilde;ng Microsoft. Chiếc tai nghe n&agrave;y hỗ trợ c&ocirc;ng nghệ chống ồn chủ động ANC cũng như c&oacute; mức gi&aacute; giảm ngang bằng với AirPods Pro.</p>\r\n\r\n<p>Surface Headphones 2 được trang bị 13 mức độ chống ồn, tương tự như thế hệ tiền nhiệm. Người d&ugrave;ng c&oacute; thể điều chỉnh mức độ chống ồn v&agrave; &acirc;m lượng volume ở ngay ph&iacute;a hai b&ecirc;n tai nghe (ph&iacute;a b&ecirc;n ngo&agrave;i phần earcup). C&aacute;ch thiết kế n&agrave;y được nhiều người d&ugrave;ng đ&aacute;nh gi&aacute; l&agrave; th&ocirc;ng minh v&agrave; tiện lợi trong qu&aacute; tr&igrave;nh sử dụng. Phần mặt ngo&agrave;i của ear cup cũng sẽ hỗ trợ c&aacute;c thao t&aacute;c chạm d&ugrave;ng để điều khiển nhạc, c&ograve;n bản th&acirc;n ear cup cũng sẽ hỗ trợ xoay 180 độ để người d&ugrave;ng c&oacute; thể t&ugrave;y chỉnh c&aacute;c vị tr&iacute; đeo kh&aacute;c nhau cho thoải m&aacute;i.</p>\r\n\r\n<p>Surface Headphones mới đi k&egrave;m với drivers điều khiển &ldquo;Free Edge&rdquo; 40mm v&agrave; c&oacute; khả năng xuất ra &acirc;m thanh chất lượng cao m&agrave; Microsoft gọi hoa mỹ l&agrave; &ldquo;omnisonic&rdquo;. Ngo&agrave;i ra, chiếc tai nghe n&agrave;y cũng được n&acirc;ng cấp l&ecirc;n Bluetooth 5.0 với khả năng hỗ trợ bộ giải m&atilde; aptX Bluetooth của Qualcomm.</p>\r\n\r\n<p>Về thời lượng pin, đ&acirc;y c&oacute; lẽ l&agrave; một điểm nhấn tr&ecirc;n chiếc tai nghe Surface Headphones mới. Microsoft tuy&ecirc;n bố đ&atilde; n&acirc;ng cấp thời lượng pin từ 15 giờ l&ecirc;n 20 giờ tr&ecirc;n thế h&ecirc; thứ 2 với chế độ ANC bật. Ngo&agrave;i ra, h&atilde;ng cũng cho biết người d&ugrave;ng c&oacute; thể sử dụng được gần 1 giờ nghe nhạc chỉ với 5 ph&uacute;t sạc.</p>', '6499000', '6000000', 'microsoft-surface-headphones-2-1-667x80086.jpg', 'tai-nghe,microsoft', 1, NULL, NULL),
(20, 3, 4, 'Tai nghe Bluetooth Samsung MG900E', '200', 0, 'Tai nghe Bluetooth Samsung MG900E có 2 màu đen - trắng, kiểu dáng nhỏ gọn, vỏ nhựa cho trọng lượng nhẹ', '<h2>Đặc điểm nổi bật của Tai nghe Bluetooth Samsung MG900E</h2>\r\n\r\n<p><img src=\"https://cdn.tgdd.vn/Products/Images/54/113119/Slider/tai-nghe-bluetooth-samsung-mg900e-250620-0811550.jpg\" /></p>\r\n\r\n<h3>&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E thiết kế nhỏ gọn\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E thiết kế nhỏ gọn\" /></a></p>\r\n\r\n<h3>Đệm tai kh&ocirc;ng qu&aacute; to hay nhỏ, kh&ocirc;ng g&acirc;y kh&oacute; chịu cho bạn</h3>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Đệm tai không quá to hay nhỏ, không dây khó chịu cho bạn\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-2.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Đệm tai không quá to hay nhỏ, không dây khó chịu cho bạn\" /></a></h3>\r\n\r\n<h3>Khoảng c&aacute;ch giữ kết nối&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>&nbsp;từ 7 - 10 m với c&ocirc;ng nghệ Bluetooth V3.0</h3>\r\n\r\n<p>C&oacute; thể kết nối c&ugrave;ng l&uacute;c 2 thiết bị nhưng chỉ ph&aacute;t 1 thiết bị c&ograve;n thiết bị thứ 2 th&igrave; ở trong t&igrave;nh trạng chờ, đến khi thiết bị đầu ngắt kết nối, thiết bị thứ 2 mới được ph&aacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Khoảng cách kết nối điện thoại lên tới 10 m\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-3.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Khoảng cách kết nối điện thoại lên tới 10 m\" /></a></p>\r\n\r\n<h3><strong>Thiết lập c&ocirc;ng nghệ giảm tạp &acirc;m, loại bỏ tiếng vang kết hợp c&aacute;c dải &acirc;m tần rộng cho chất lượng &acirc;m thanh cao</strong></h3>\r\n\r\n<p>Gi&uacute;p bạn nghe được cuộc gọi đến r&otilde; tiếng hơn. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng nghệ S Voice nhận th&ocirc;ng b&aacute;o bằng giọng n&oacute;i&nbsp;cho việc sử dụng tiện lợi hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Kết hợp 2 công nghệ lọc tiếng ồn và tiếng vang\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-4.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Kết hợp 2 công nghệ lọc tiếng ồn và tiếng vang\" /></a></p>\r\n\r\n<h3><strong>Samsung MG900E​ chuy&ecirc;n dụng để nghe nhận cuộc gọi</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-10.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Samsung MG900E​ chuyên dụng để nghe nhận cuộc gọi\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-10.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Samsung MG900E​ chuyên dụng để nghe nhận cuộc gọi\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-104320-084331.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Mic thoại\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-104320-084331.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Mic thoại\" /></a></h3>\r\n\r\n<h3><strong>Thời gian sử dụng k&eacute;o d&agrave;i, đ&agrave;m thoại 8 - 9 tiếng li&ecirc;n tục, sạc đầy trong 2 tiếng</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-1-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Đa phương tiện\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-1-2.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Đa phương tiện\" /></a></p>\r\n\r\n<h3><strong>C&aacute;ch kết nối</strong></h3>\r\n\r\n<p>- Bạn gạt n&uacute;t nguồn, nhấn giữ n&uacute;t tr&ograve;n biểu tượng điện thoại từ 4 đến 6 gi&acirc;y, l&uacute;c n&agrave;y đ&egrave;n sẽ&nbsp;chớp xanh đỏ.</p>\r\n\r\n<p>- Bật Bluetooth trong&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>&nbsp;v&agrave; d&ograve; t&igrave;m t&ecirc;n&nbsp;tai nghe&nbsp;để kết nối, nếu th&agrave;nh c&ocirc;ng đ&egrave;n sẽ chớp cam.</p>\r\n\r\n<p>- Tai nghe sẽ tự động kết nối ở những lần kết nối sau.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-2-fix.jpg\" onclick=\"return false;\"><img alt=\"Cách kết nối tai nghe Bluetooth Samsung MG900E\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-2-fix.jpg\" title=\"Cách kết nối tai nghe Bluetooth Samsung MG900E\" /></a></p>\r\n\r\n<h3><strong>C&aacute;ch sử dụng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo tai nghe kinh doanh tại Thế Giới Di Động\">tai nghe</a></strong></h3>\r\n\r\n<p>Đ&egrave;n LED b&aacute;o hiệu t&igrave;nh trạng của tai nghe, muốn kiểm tra pin, bạn nhấn đồng thời n&uacute;t nhận cuộc gọi v&agrave; giảm &acirc;m lượng nếu thấy đ&egrave;n m&agrave;u xanh th&igrave; pin c&ograve;n tr&ecirc;n 60%, đ&egrave;n m&agrave;u v&agrave;ng pin c&ograve;n 30 - 60% c&ograve;n m&agrave;u đỏ l&agrave; dưới 30%, trường hợp đ&egrave;n đỏ nhấp nh&aacute;y li&ecirc;n tục th&igrave; pin c&ograve;n dưới 10%.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-6-fix.jpg\" onclick=\"return false;\"><img alt=\"Cách sử dụng tai nghe Bluetooth Samsung MG900E\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-6-fix.jpg\" title=\"Cách sử dụng tai nghe Bluetooth Samsung MG900E\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-11.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Cách sử dụng\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-11.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Cách sử dụng\" /></a></p>\r\n\r\n<h3><strong>Phụ kiện đi k&egrave;m&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-nhet-trong\" target=\"_blank\" title=\"Tai nghe nhét trong tại Thế Giới Di Động \">tai nghe nh&eacute;t trong</a></strong></h3>\r\n\r\n<p>Ngo&agrave;i đệm gắn liền tr&ecirc;n tai nghe, sản phẩm c&ograve;n được tặng k&egrave;m th&ecirc;m 2 đệm tai nghe kh&aacute;c cho bạn chọn đeo linh hoạt, thoải m&aacute;i. Gọng tai giữ cho tai nghe b&aacute;m chặt v&agrave;o tai mọi l&uacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113119/tai-nghe-bluetooth-samsung-mg900e-9.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung MG900E - Phụ kiện đi kèm\" src=\"https://cdn.tgdd.vn/Products/Images/54/113119/tai-nghe-bluetooth-samsung-mg900e-9.jpg\" title=\"Tai nghe Bluetooth Samsung MG900E - Phụ kiện đi kèm\" /></a></p>', '850000', '800000', 'tai-nghe-bluetooth-samsung-mg900e-add-600x60033.jpg', 'tai-nghe,samsung', 1, NULL, NULL),
(21, 3, 4, 'Tai nghe Samsung EG920B', '200', 0, 'Tai nghe nhét trong Samsung EG920B có đi nút đệm tai nghe dạng móc', '<h3>Giới thiệu sản phẩm</h3>\r\n\r\n<h3><strong><a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Phụ kiện tai nghe\">Tai nghe</a>&nbsp;nh&eacute;t trong Samsung EG920B c&oacute; đi n&uacute;t đệm tai nghe dạng m&oacute;c</strong></h3>\r\n\r\n<p>Gi&uacute;p người đeo khi vận động mạnh sẽ kh&ocirc;ng bị rớt ra.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung EG920B - Có đi nút đệm tai nghe dạng móc\" src=\"https://cdn.tgdd.vn/Products/Images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-1.jpg\" title=\"Tai nghe nhét trong Samsung EG920B - Có đi nút đệm tai nghe dạng móc\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung EG920B - Có đi nút đệm tai nghe dạng móc\" src=\"https://cdn.tgdd.vn/Products/Images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-2.jpg\" title=\"Tai nghe nhét trong Samsung EG920B - Có đi nút đệm tai nghe dạng móc\" /></a></p>\r\n\r\n<h3><strong>D&acirc;y d&agrave;i vừa đủ 1.2 m</strong></h3>\r\n\r\n<p>Thoải m&aacute;i để bạn vừa nghe nhạc vừa cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>&nbsp;v&agrave;o t&uacute;i x&aacute;ch hay balo.&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-nhet-trong\" target=\"_blank\" title=\"Tai nghe nhét trong giá rẻ tại Thế Giới Di Động \">Tai nghe nh&eacute;t trong</a>&nbsp;được tặng k&egrave;m th&ecirc;m 3 cặp đệm tai dạng thường v&agrave; 1 cặp đệm tai dạng m&oacute;c.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung EG920B - Dây dài 1.2 m\" src=\"https://cdn.tgdd.vn/Products/Images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-3.jpg\" title=\"Tai nghe nhét trong Samsung EG920B - Dây dài 1.2 m\" /></a></p>\r\n\r\n<h3><strong>&Acirc;m thanh được xử l&yacute; tốt để mang tới chất lượng nhạc trong trẻo</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung EG920B - Âm thanh được xử lý tốt để mang tới chất lượng nhạc trong trẻo\" src=\"https://cdn.tgdd.vn/Products/Images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-4.jpg\" title=\"Tai nghe nhét trong Samsung EG920B - Âm thanh được xử lý tốt để mang tới chất lượng nhạc trong trẻo\" /></a></p>\r\n\r\n<h3><strong>Tương th&iacute;ch được với nhiều thiết bị di động</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-5.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung EG920B - Tương thích được với nhiều thiết bị di động\" src=\"https://cdn.tgdd.vn/Products/Images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-5.jpg\" title=\"Tai nghe nhét trong Samsung EG920B - Tương thích được với nhiều thiết bị di động\" /></a></p>\r\n\r\n<h3><strong>C&aacute;c ph&iacute;m ấn chức năng tr&ecirc;n d&acirc;y loa</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-6.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung EG920B - Các phím ấn chức năng trên dây loa\" src=\"https://cdn.tgdd.vn/Products/Images/54/113118/tai-nghe-nhet-trong-samsung-eg920b-6.jpg\" title=\"Tai nghe nhét trong Samsung EG920B - Các phím ấn chức năng trên dây loa\" /></a></p>', '280000', '250000', 'tai-nghe-nhet-trong-samsung-eg920b-2-1-600x6003.jpg', 'tai-nghe,samsung', 1, NULL, NULL),
(22, 3, 4, 'Tai nghe Bluetooth Level U Pro BN920C', '200', 0, 'Tai nghe Bluetooth Samsung Level U Pro BN920C', '<p>To&agrave;n th&acirc;n tai nghe được gia c&ocirc;ng bằng vỏ nhựa v&agrave; cao su, phần cao su sẽ gi&uacute;p tai nghe tiếp x&uacute;c với da trơn hơn, thoải m&aacute;i hơn.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Thiết kế\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-1.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Thiết kế\" /></a></p>\r\n\r\n<p>Trọng lượng tai nghe nhẹ, kh&ocirc;ng l&agrave;m mỏi cổ hay l&agrave;m kh&oacute; chịu cho người đeo.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-fix.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C có trọng lượng nhẹ\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-fix.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C có trọng lượng nhẹ\" /></a></p>\r\n\r\n<h3><strong>Hai đầu tai nghe h&iacute;t nam ch&acirc;m</strong></h3>\r\n\r\n<p>Phần đầu h&iacute;t n&agrave;y gi&uacute;p tai nghe kh&ocirc;ng bị rớt ra khỏi cổ của bạn khi tạm thời kh&ocirc;ng d&ugrave;ng đến.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - 2 đầu tai nghe hít nam châm\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-3.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - 2 đầu tai nghe hít nam châm\" /></a></p>\r\n\r\n<h3><strong>C&ocirc;ng nghệ lọc tiếng ồn th&ocirc;ng minh</strong></h3>\r\n\r\n<p>2 mic với 2 c&ocirc;ng nghệ NR giảm&nbsp;tiếng ồn v&agrave; EC giảm tiếng vang, ph&acirc;n biệt giọng n&oacute;i của bạn với tiếng n&oacute;i ồn b&ecirc;n ngo&agrave;i, mang đến chất lượng cuộc gọi r&otilde; n&eacute;t nhất.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Công nghệ lọc tiếng ồn thông minh\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-4.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Công nghệ lọc tiếng ồn thông minh\" /></a></p>\r\n\r\n<h3><strong><a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth kinh doanh tại Thế Giới Di Động\">Tai nghe Bluetooth</a>&nbsp;thể hiện ch&iacute;nh x&aacute;c những &acirc;m thanh, bass, treble của b&agrave;i h&aacute;t tới tai của bạn</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-5.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Âm thanh chất lượng\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-5.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Âm thanh chất lượng\" /></a></p>\r\n\r\n<p>T&iacute;nh năng rung khi c&oacute; cuộc gọi đến gi&uacute;p bạn kh&ocirc;ng bỏ lỡ c&aacute;c cuộc gọi quan trọng.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-42.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-42.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" /></a></p>\r\n\r\n<h3><strong>Pin tr&acirc;u, sử dụng được cho mọi nhu cầu</strong></h3>\r\n\r\n<p>Tai nghe cho thời gian sử dụng khoảng 9 giờ nghe nhạc, 9 giờ đ&agrave;m thoại v&agrave; 300 giờ ở chế độ chờ.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-6.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Sử dụng được cho mọi nhu cầu\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-6.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C - Sử dụng được cho mọi nhu cầu\" /></a></p>\r\n\r\n<h3><strong>Kết nối với 2 tai nghe c&ugrave;ng một l&uacute;c, chia sẻ &acirc;m nhạc</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-44.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-44.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-43.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-43.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" /></a></p>\r\n\r\n<h3><strong>C&aacute;ch sử dụng&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-nhet-trong\" target=\"_blank\" title=\"Tai nghe nhét trong giá rẻ tại Thế Giới Di Động \">tai nghe nh&eacute;t trong</a></strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-41.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" src=\"https://cdn.tgdd.vn/Products/Images/54/113117/tai-nghe-bluetooth-samsung-level-u-pro-bn920c-41.jpg\" title=\"Tai nghe Bluetooth Samsung Level U Pro BN920C\" /></a></p>', '1450000', '1380000', 'tai-nghe-bluetooth-samsung-level-u-pro-bn920c-add-600x60053.jpg', 'tai-nghe,samsung', 1, NULL, NULL),
(23, 3, 4, 'Tai nghe nhét tai IG935B', '200', 0, 'Tai nghe nhét tai Samsung IG935B', '<h3>Giới thiệu sản phẩm</h3>\r\n\r\n<h3><strong><a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Phụ kiện tai nghe\">Tai nghe</a>&nbsp;Samsung IG935B thiết kế kiểu d&aacute;ng đơn giản nhưng vẫn trẻ trung</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung IG935B - thiết kế kiểu dáng đơn giản nhưng vẫn trẻ trung\" src=\"https://cdn.tgdd.vn/Products/Images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-1.jpg\" title=\"Tai nghe nhét trong Samsung IG935B - thiết kế kiểu dáng đơn giản nhưng vẫn trẻ trung\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung IG935B - thiết kế kiểu dáng đơn giản nhưng vẫn trẻ trung\" src=\"https://cdn.tgdd.vn/Products/Images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-2.jpg\" title=\"Tai nghe nhét trong Samsung IG935B - thiết kế kiểu dáng đơn giản nhưng vẫn trẻ trung\" /></a></p>\r\n\r\n<h3><strong>Độ d&agrave;i d&acirc;y 1.2 m</strong></h3>\r\n\r\n<p>Vừa đủ để bạn vừa nghe nhạc vừa cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động\">điện thoại</a>&nbsp;v&agrave;o t&uacute;i x&aacute;ch hay balo.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung IG935B - Độ dài dây 1.2 m\" src=\"https://cdn.tgdd.vn/Products/Images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-3.jpg\" title=\"Tai nghe nhét trong Samsung IG935B - Độ dài dây 1.2 m\" /></a></p>\r\n\r\n<h3><strong>T&aacute;i tạo &acirc;m thanh xuất sắc</strong></h3>\r\n\r\n<p>Cho bạn cảm nhận từng &acirc;m thanh chi tiết trong b&agrave;i h&aacute;t.</p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung IG935B - Tái tạo âm thanh xuất sắc\" src=\"https://cdn.tgdd.vn/Products/Images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-4.jpg\" title=\"Tai nghe nhét trong Samsung IG935B - Tái tạo âm thanh xuất sắc\" /></a></p>\r\n\r\n<h3><strong>Jack cắm 3.5mm hợp chuẩn với nhiều thiết bị di động hiện nay</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-5.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung IG935B có jack cắm 3.5mm\" src=\"https://cdn.tgdd.vn/Products/Images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-5.jpg\" title=\"Tai nghe nhét trong Samsung IG935B có jack cắm 3.5mm\" /></a></p>\r\n\r\n<h3><strong>Ph&iacute;m ấn tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-nhet-trong\" target=\"_blank\" title=\"Tham khảo tai nghe nhét trong giá rẻ tại Thế Giới Di Động \">tai nghe nh&eacute;t trong</a>&nbsp;đa chức năng</strong></h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-6.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe nhét trong Samsung IG935B - Phím ấn trên loa đa chức năng\" src=\"https://cdn.tgdd.vn/Products/Images/54/113116/tai-nghe-nhet-trong-samsung-ig935b-6.jpg\" title=\"Tai nghe nhét trong Samsung IG935B - Phím ấn trên loa đa chức năng\" /></a></p>', '300000', '274000', 'tai-nghe-nhet-trong-samsung-ig935b-2-1-600x60029.jpg', 'tai-nghe,samsung', 1, NULL, NULL),
(24, 4, 3, 'iPhone 12 Pro Max 128GB', '200', 0, 'Iphone series 12', '<h2>Đặc điểm nổi bật của&nbsp;iPhone 12 Pro Max</h2>\r\n\r\n<p><img alt=\"iPhone 12 Pro Max 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/10/17/637385314969336078_iphone-12-pro-max-1.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro Max 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/10/17/637385314969447243_iphone-12-pro-max-5.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro Max 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/10/17/637385314969786022_iphone-12-pro-max-4.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro Max 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/10/17/637385314967856533_iphone-12-pro-max-3.JPG\" /></p>\r\n\r\n<h2>Đ&aacute;nh gi&aacute; chi tiết&nbsp;iPhone 12 Pro Max</h2>\r\n\r\n<p><strong>&ldquo;Tr&ugrave;m cuối&rdquo; của d&ograve;ng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12\" title=\"Tham khảo iPhone 12 chính hãng giá tốt tại FPTShop\" type=\"Tham khảo iPhone 12 chính hãng giá tốt tại FPTShop\">iPhone 12</a>&nbsp;đ&atilde; xuất hiện.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro-max\" title=\"Điện thoại iPhone 12 Pro Max chính hãng FPTShop\" type=\"Điện thoại iPhone 12 Pro Max chính hãng FPTShop\">iPhone 12 Pro Max</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\" title=\"Xem thêm điện thoại iPhone chính hãng Apple tại FPTShop\" type=\"Xem thêm điện thoại iPhone chính hãng Apple tại FPTShop\">iPhone</a>&nbsp;c&oacute; m&agrave;n h&igrave;nh lớn nhất từ trước đến nay, mang tr&ecirc;n m&igrave;nh bộ vi xử l&yacute; mạnh nhất, camera&nbsp;</strong><strong>đẳng cấp pro c&ugrave;ng kết nối 5G si&ecirc;u tốc, cho bạn những trải nghiệm tuyệt vời chưa từng c&oacute;.</strong></p>\r\n\r\n<p><strong><img alt=\"iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-3.JPG\" title=\"Điện thoại iPhone 12 Pro Max chính hãng FPTShop\" /></strong></p>\r\n\r\n<h3><strong>Chiếc iPhone m&agrave;n h&igrave;nh lớn nhất</strong></h3>\r\n\r\n<p>iPhone 12 Pro Max sở hữu m&agrave;n h&igrave;nh k&iacute;ch thước kh&oacute; tin, l&ecirc;n tới 6,7 inch, cho bạn trải nghiệm h&igrave;nh ảnh cực đ&atilde;. Chất lượng đến từ một m&agrave;n h&igrave;nh c&ocirc;ng nghệ Super Retina XDR, tấm nền OLED cao cấp v&agrave; độ ph&acirc;n giải si&ecirc;u cao thực sự qu&aacute; tuyệt vời.</p>\r\n\r\n<p>Mọi thứ đều trở n&ecirc;n sống động tr&ecirc;n iPhone 12 Pro Max, đặc biệt khi bạn xem ảnh, xem phim hay chơi game. C&ograve;n bất ngờ hơn khi d&ugrave; m&agrave;n h&igrave;nh lớn hơn, nhưng iPhone 12 Pro Max vẫn gọn g&agrave;ng như thế hệ trước nhờ viền m&agrave;n h&igrave;nh được tối ưu si&ecirc;u mỏng đầy tinh tế.</p>\r\n\r\n<p><img alt=\"màn hình iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-2.JPG\" title=\"Chiếc iPhone 12 Pro Max màn hình lớn nhất\" /></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh si&ecirc;u bền, giảm nguy cơ trầy xước rơi vỡ</strong></h3>\r\n\r\n<p>Kh&ocirc;ng chỉ c&oacute; một m&agrave;n h&igrave;nh đẹp xuất sắc, m&agrave;n h&igrave;nh iPhone 12 Pro Max&nbsp;c&ograve;n được bảo vệ bởi c&ocirc;ng nghệ phủ gốm Ceramic Shield. C&ocirc;ng nghệ n&agrave;y sử dụng c&aacute;c tinh thể gốm nano cứng hơn cả kim loại để tấm k&iacute;nh c&oacute; khả năng chống xước cao hơn v&agrave; bền hơn tới 4 lần nếu chẳng may điện thoại bị rơi. Đ&acirc;y cũng l&agrave; lớp k&iacute;nh m&agrave;n h&igrave;nh cứng nhất thế giới smartphone hiện nay, cho bạn cảm gi&aacute;c y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n\r\n<p><img alt=\"thiết kế iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-1.JPG\" title=\"Màn hình iPhone 12 Pro Max siêu bền, giảm nguy cơ trầy xước rơi vỡ\" /></p>\r\n\r\n<h3><strong>Chế t&aacute;c từ th&eacute;p kh&ocirc;ng gỉ cao cấp</strong></h3>\r\n\r\n<p>Được l&agrave;m từ những vật liệu cao cấp h&agrave;ng đầu v&agrave; khả năng ho&agrave;n thiện tinh xảo, iPhone 12 Pro Max khiến tất cả đều phải ngước nh&igrave;n. Ấn tượng nhất chắc chắn l&agrave; phần khung viền l&agrave;m từ th&eacute;p kh&ocirc;ng gỉ, được l&agrave;m phẳng hiện đại v&agrave; c&aacute;c đường cắt kim cương s&aacute;ng b&oacute;ng sang trọng.</p>\r\n\r\n<p>Phần viền m&agrave;n h&igrave;nh v&agrave; th&acirc;n m&aacute;y đều được l&agrave;m si&ecirc;u mỏng, tạo n&ecirc;n sự thanh lịch. Ngo&agrave;i ra bạn c&ograve;n c&oacute; tới 4 m&agrave;u sắc để lựa chọn l&agrave; Bạc, X&aacute;m than ch&igrave;, V&agrave;ng v&agrave; Xanh đại dương, tất cả đều rất đẳng cấp. B&ecirc;n cạnh đ&oacute;,&nbsp;iPhone 12 Pro Max sẽ c&oacute; 3 phi&ecirc;n bản dung lượng với c&aacute;c t&ugrave;y chọn m&agrave;u sắc kể tr&ecirc;n&nbsp;cho người d&ugrave;ng lựa chọn gồm:&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro-max?dung-luong=128gb\" title=\"iPhone 12 Pro Max 128GB\" type=\"iPhone 12 Pro Max 128GB\">iPhone 12 Pro Max&nbsp;128GB</a>,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro-max?dung-luong=256gb\" title=\"iPhone 12 Pro Max 256GB\" type=\"iPhone 12 Pro Max 256GB\">iPhone 12 Pro Max&nbsp;256GB</a>&nbsp;v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro-max?dung-luong=512gb\" title=\"iPhone 12 Pro Max 512GB\" type=\"iPhone 12 Pro Max 512GB\">iPhone 12 Pro Max&nbsp;512GB</a>.</p>\r\n\r\n<p><img alt=\"thép không gỉ iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-4.JPG\" title=\"iPhone 12 Pro Max chế tác từ thép không gỉ cao cấp, vẻ đẹp đầy tính nghệ thuật\" /></p>\r\n\r\n<h3><strong>Khả năng chống nước ho&agrave;n hảo</strong></h3>\r\n\r\n<p>iPhone 12 Pro Max c&oacute; thể ng&acirc;m nước ở độ s&acirc;u tối đa l&ecirc;n tới 6m trong khoảng thời gian 30 ph&uacute;t. Trang bị chuẩn&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai?tinh-nang-dac-biet=chong-nuoc-bui\" title=\"Tham khảo điện thoại có tính năng chống nước tại FPTShop\" type=\"Tham khảo điện thoại có tính năng chống nước tại FPTShop\">chống nước</a>&nbsp;IP68 tốt nhất thế giới smartphone hiện nay, iPhone 12 Pro Max ho&agrave;n to&agrave;n kh&ocirc;ng bị nguy hại khi đối mặt với những nguy cơ từ nước trong cuộc sống h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p><img alt=\"chống nước iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-8.JPG\" title=\"Khả năng chống nước hoàn hảo của iPhone 12 Pro Max\" /></p>\r\n\r\n<h3><strong>Tốc độ nhanh kh&ocirc;ng tưởng với mạng 5G</strong></h3>\r\n\r\n<p>iPhone 12 Pro Max trang bị băng tần&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/mang-5g-la-gi-tong-quan-ve-mang-khong-day-nhanh-nhat-the-gioi-hien-nay-50246\" title=\"Mạng 5G là gì? Tổng quan về mạng không dây nhanh nhất thế giới hiện nay\" type=\"Mạng 5G là gì? Tổng quan về mạng không dây nhanh nhất thế giới hiện nay\">5G</a>&nbsp;si&ecirc;u nhanh, gi&uacute;p trải nghiệm Internet của bạn l&ecirc;n một tầm cao mới. Bất cứ điều g&igrave; bạn l&agrave;m tr&ecirc;n mạng Internet đều diễn ra nhanh ch&oacute;ng. Xem phim trực tuyến chất lượng cao, livestream, chơi game online, tải t&agrave;i liệu, tất cả đều được xử l&yacute; trong chớp mắt với kết nối 5G.</p>\r\n\r\n<p><img alt=\"mạng 5G iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-max-6.JPG\" title=\"Tận hưởng tốc độ nhanh không tưởng với mạng 5G trên iPhone 12 Pro Max\" /></p>\r\n\r\n<h3><strong>Chiếc điện thoại mạnh mẽ nhất thế giới</strong></h3>\r\n\r\n<p><a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-chip-apple-a14-trai-tim-se-lam-nen-suc-manh-cho-iphone-12-121314\" title=\"Apple A14 Bionic\" type=\"Apple A14 Bionic\">Apple A14 Bionic</a>, bộ vi xử l&yacute; điện thoại mạnh nhất thế giới đ&atilde; c&oacute; mặt tr&ecirc;n iPhone 12 Pro Max. Đ&acirc;y l&agrave; con chip đầu ti&ecirc;n được sản xuất tr&ecirc;n tiến tr&igrave;nh 5nm, c&oacute; số lượng b&oacute;ng b&aacute;n dẫn nhiều hơn tới 40% so với thế hệ trước, kh&ocirc;ng chỉ mang đến hiệu năng cực đỉnh m&agrave; c&ograve;n c&oacute; thời lượng pin tuyệt vời.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Apple A14 Bionic c&ograve;n t&iacute;ch hợp chip xử l&yacute; h&igrave;nh ảnh ISP mới, n&acirc;ng cao khả năng chụp ảnh v&agrave; quay video. Số nh&acirc;n Neural Engine cũng được tăng gấp đ&ocirc;i, từ 8 l&ecirc;n 16 để việc vận dụng AI c&oacute; hiệu suất cao hơn bao giờ hết.</p>\r\n\r\n<p><img alt=\"cấu hình iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-7.JPG\" title=\"Chiếc điện thoại iPhone 12 Pro Max mạnh mẽ nhất thế giới\" /></p>\r\n\r\n<h3><strong>M&aacute;y qu&eacute;t LiDAR chuy&ecirc;n nghiệp</strong></h3>\r\n\r\n<p>NASA đang sử dụng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/cam-bien-lidar-tren-iphone-12-pro-max-la-gi-127418\" title=\"Tìm hiểu công nghệ cảm biến LiDAR là gì?\" type=\"Tìm hiểu công nghệ cảm biến LiDAR là gì?\">c&ocirc;ng nghệ LiDAR</a>&nbsp;cho lần tiếp theo đặt ch&acirc;n l&ecirc;n sao Hỏa. Bạn c&oacute; thể trải nghiệm c&ocirc;ng nghệ ti&ecirc;n tiến n&agrave;y qua m&aacute;y qu&eacute;t LiDAR ngay tr&ecirc;n ch&iacute;nh iPhone 12 Pro Max.</p>\r\n\r\n<p>LiDAR c&oacute; khả năng dựng lại vật thể bằng c&aacute;ch đo thời gian &aacute;nh s&aacute;ng phản xạ đến vật thể, với độ ch&iacute;nh x&aacute;c tới từng nano gi&acirc;y. LiDAR sẽ qu&eacute;t c&aacute;c bề mặt trong kh&ocirc;ng gian để ph&acirc;n t&iacute;ch v&agrave; tạo cảnh dựa tr&ecirc;n ứng dụng AR. Đ&acirc;y l&agrave; ng&agrave;nh c&ocirc;ng nghệ c&oacute; t&iacute;nh ứng dụng rất cao trong tương lai gần.</p>\r\n\r\n<p><img alt=\"LiDAR iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-6.JPG\" title=\"iPhone 12 Pro Max sở hữu máy quét LiDAR chuyên nghiệp, dựng AR với tốc độ ánh sáng\" /></p>\r\n\r\n<h3><strong>Zoom quang học 2,5x, chụp sắc n&eacute;t ở cự ly xa</strong></h3>\r\n\r\n<p>Với ống k&iacute;nh Tele mới ti&ecirc;u cự 65mm, iPhone 12 Pro Max c&oacute; khả năng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai?camera=zoom-quang-hoc\" title=\"Tham khảo điện thoại có tính năng zoom quang học tại FPTShop\" type=\"Tham khảo điện thoại có tính năng zoom quang học tại FPTShop\">zoom quang học</a>&nbsp;2,5x, ph&oacute;ng to h&igrave;nh ảnh tới 2,5 lần m&agrave; chất lượng kh&ocirc;ng hề thay đổi. Nghĩa l&agrave; bạn c&oacute; thể chụp những vật thể ở khoảng c&aacute;ch xa với khả năng thu ph&oacute;ng v&agrave; độ n&eacute;t đ&aacute;ng kinh ngạc.</p>\r\n\r\n<p><img alt=\"zoom quang iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-14(1).JPG\" title=\"Zoom quang học 2,5x, chụp ảnh sắc nét ở cự ly xa với iPhone 12 Pro Max\" /></p>\r\n\r\n<h3><strong>T&iacute;nh năng chống rung quang học mới</strong></h3>\r\n\r\n<p>Camera ch&iacute;nh tr&ecirc;n iPhone 12 Pro Max l&agrave; camera smartphone đầu ti&ecirc;n c&oacute; t&iacute;nh năng chống rung quang học thay đổi cảm biến (sensor-shift OIS). T&iacute;nh năng n&agrave;y mang tới hiệu quả chống rung gấp 5 lần so với OIS th&ocirc;ng thường.</p>\r\n\r\n<p>Kết hợp th&ecirc;m khẩu độ lớn f/1.6 v&agrave; k&iacute;ch thước điểm ảnh l&ecirc;n tới 1,7um, camera iPhone 12 Pro Max đ&iacute;ch thực l&agrave; một ngọn hải đăng trong đ&ecirc;m. C&aacute;c bức ảnh chụp đ&ecirc;m sẽ s&aacute;ng r&otilde;, sắc n&eacute;t v&agrave; m&agrave;u sắc v&ocirc; c&ugrave;ng ch&iacute;nh x&aacute;c. Tha hồ để bạn tự do s&aacute;ng tạo, ghi lại những khoảnh khắc đ&aacute;ng nhớ trong đ&ecirc;m tối.</p>\r\n\r\n<p><img alt=\"OIS iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-19.JPG\" title=\"Tính năng chống rung quang học mới OIS thay đổi cảm biến của iPhone 12 Pro Max\" /></p>\r\n\r\n<h3><strong>Dựng phim chuy&ecirc;n nghiệp, quay video 4K HDR Dolby Vision</strong></h3>\r\n\r\n<p>Đến với thế giới m&agrave;u sắc bằng những đoạn phim 4K HDR Dolby Vision ch&acirc;n thực một c&aacute;ch đ&aacute;ng kinh ngạc tr&ecirc;n iPhone 12 Pro Max. Những video giờ đ&acirc;y sẽ c&oacute; m&agrave;u sắc sống động chưa từng thấy với 700 triệu m&agrave;u, gấp 60 lần so với video ti&ecirc;u chuẩn.</p>\r\n\r\n<p>Bạn sẽ thấy r&otilde; sự kh&aacute;c biệt khi thưởng thức video Dolby Vision tr&ecirc;n TV 4K HDR. iPhone 12 Pro Max l&agrave; chiếc điện thoại đầu ti&ecirc;n c&oacute; thể quay video Dolby Vision, thậm ch&iacute; bạn c&ograve;n c&oacute; những c&ocirc;ng cụ để chỉnh sửa, bi&ecirc;n tập video trước khi xuất bản. Tất cả c&aacute;c c&ocirc;ng việc của một studio chuy&ecirc;n nghiệp g&oacute;i gọn trong chiếc điện thoại đầy quyền lực tr&ecirc;n tay.</p>\r\n\r\n<p><img alt=\"4K HDR iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-15.JPG\" title=\"Dựng phim chuyên nghiệp bằng khả năng quay video 4K HDR Dolby Vision với iPhone 12 Pro Max\" /></p>\r\n\r\n<h3><strong>Chụp ảnh v&agrave; quay video thiếu s&aacute;ng ho&agrave;n hảo</strong></h3>\r\n\r\n<p>Nếu như ở những chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\" title=\"Tham khảo điện thoại chính hãng giá tốt tại FPTShop\" type=\"Tham khảo điện thoại chính hãng giá tốt tại FPTShop\">điện thoại</a>&nbsp;kh&aacute;c, thiếu s&aacute;ng lu&ocirc;n l&agrave; một điều kiện kh&oacute; khăn cho camera, th&igrave; với iPhone 12 Pro Max, m&agrave;n đ&ecirc;m chỉ khiến cho những bức ảnh v&agrave; video của bạn lung linh hơn. Bạn c&oacute; thể chụp ảnh r&otilde; n&eacute;t, chụp ch&acirc;n dung đ&ecirc;m v&agrave; quay video với độ s&aacute;ng tốt hơn tới 87%.</p>\r\n\r\n<p>Ngay cả những v&ugrave;ng tối cũng c&oacute; độ chi tiết rất cao tr&ecirc;n iPhone 12 Pro Max. Cảm biến camera h&agrave;ng đầu, chip xử l&yacute; h&igrave;nh ảnh ISP mới tr&ecirc;n Apple A14 Bionic, m&aacute;y qu&eacute;t LiDAR tạo n&ecirc;n sự kết hợp ho&agrave;n hảo khi chụp ảnh, quay video v&agrave;o ban đ&ecirc;m.</p>\r\n\r\n<p><img alt=\"thiếu sáng iPhone 12 Pro Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-13.JPG\" title=\"Chụp ảnh và quay video thiếu sáng hoàn hảo với iPhone 12\" /></p>', '32990000', '30000000', 'iphone-12-pro-max-xanh-duong-new-600x600-200x20089.jpg', 'iphone,dien-thoai,apple', 1, NULL, NULL);
INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_quantity`, `product_sold`, `product_desc`, `product_content`, `product_price`, `price_cost`, `product_image`, `product_tags`, `product_status`, `created_at`, `updated_at`) VALUES
(25, 4, 3, 'iPhone 12 64GB', '200', 0, 'Iphone series 12', '<p><strong>iPhone 12 ra mắt với vai tr&ograve; mở ra một kỷ nguy&ecirc;n ho&agrave;n to&agrave;n mới. Tốc độ mạng 5G si&ecirc;u tốc, bộ vi xử l&yacute; A14 Bionic nhanh nhất thế giới smartphone, m&agrave;n h&igrave;nh OLED tr&agrave;n cạnh tuyệt đẹp v&agrave; camera si&ecirc;u chụp đ&ecirc;m, tất cả đều c&oacute; mặt tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12\" title=\"Điện thoại iPhone 12 chính hãng tại FPTShop\" type=\"Điện thoại iPhone 12 chính hãng tại FPTShop\">iPhone 12</a>.</strong></p>\r\n\r\n<p><strong><img alt=\"iPhone 12 chính hãng FPTShop\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-1(1).JPG\" title=\"iPhone 12 chính hãng FPTShop\" /></strong></p>\r\n\r\n<h2><strong>Thiết kế mỏng nhẹ, nhỏ gọn v&agrave; đẳng cấp</strong></h2>\r\n\r\n<p>iPhone 12 đ&atilde; c&oacute; sự đột ph&aacute; về thiết kế với kiểu d&aacute;ng mới vu&ocirc;ng vắn, mạnh mẽ v&agrave; sang trọng hơn. Kh&ocirc;ng chỉ vậy, iPhone 12 mỏng hơn 11%, nhỏ gọn hơn 15% v&agrave; nhẹ hơn 16% so với thế hệ trước&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-11-64gb\" title=\"iPhone 11 64GB\" type=\"iPhone 11 64GB\">iPhone 11</a>.</p>\r\n\r\n<p>Bất ngờ hơn nữa l&agrave; d&ugrave; gọn hơn đ&aacute;ng kể nhưng iPhone 12 vẫn c&oacute;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai?man-hinh=tren-60-inch\" title=\"Tham khảo điện thoại màn hình trên 6.0 inch tại FPTShop\" type=\"Tham khảo điện thoại màn hình trên 6.0 inch tại FPTShop\">m&agrave;n h&igrave;nh 6,1 inch</a>&nbsp;như iPhone 11 m&agrave; kh&ocirc;ng hề bị cắt giảm. Phần viền m&agrave;n h&igrave;nh thu hẹp tối đa c&ugrave;ng sự nỗ lực trong thiết kế đ&atilde; tạo n&ecirc;n điều kỳ diệu ở iPhone 12.</p>\r\n\r\n<p><img alt=\"Thiết kế iPhone 12\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-2(1).JPG\" title=\"Thiết kế iPhone 12 mỏng nhẹ, nhỏ gọn và đẳng cấp\" /></p>\r\n\r\n<p><em>iPhone 12 c&oacute; thiết kế nhỏ gọn, mỏng nhẹ v&agrave; đẳng cấp</em></p>\r\n\r\n<h2><strong>Ceramic Shield, bảo vệ an to&agrave;n cho mặt k&iacute;nh</strong></h2>\r\n\r\n<p>iPhone 12 thể hiện sự cao cấp từ những vật liệu chế t&aacute;c, bao gồm khung nh&ocirc;m cứng c&aacute;p v&agrave; 2 mặt k&iacute;nh tuyệt đẹp. Hơn thế nữa, mặt k&iacute;nh của iPhone 12 được bảo vệ bởi một lớp gốm (Ceramic Shield), gi&uacute;p cứng hơn mặt k&iacute;nh của bất kỳ chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\" title=\"Xem thêm điện thoại chính hãng giá tốt tại FPTShop\" type=\"Xem thêm điện thoại chính hãng giá tốt tại FPTShop\">điện thoại</a>&nbsp;n&agrave;o kh&aacute;c. iPhone của bạn sẽ bền vững hơn tới 4 lần, kh&oacute; xước hơn, mang tới cảm gi&aacute;c y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n\r\n<p><img alt=\"mặt kính iPhone 12\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-3(1).JPG\" title=\"Ceramic Shield, bảo vệ an toàn cho mặt kính iPhone 12\" /></p>\r\n\r\n<h2><strong>iPhone 12 chống nước ho&agrave;n hảo</strong></h2>\r\n\r\n<p>iPhone 12 tiếp tục c&oacute; khả năng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai?tinh-nang-dac-biet=chong-nuoc-bui\" title=\"Tham khảo điện thoại có chống nước và bụi bẩn giá tốt tại FPTShop\" type=\"Tham khảo điện thoại có chống nước và bụi bẩn giá tốt tại FPTShop\">chống nước v&agrave; chống bụi</a>&nbsp;chuẩn IP68, nhưng giờ đ&acirc;y bạn đ&atilde; c&oacute; thể ng&acirc;m nước ở độ s&acirc;u tới 6m trong v&ograve;ng 30 ph&uacute;t thay v&igrave; 1,5m như trước kia. Tha hồ sử dụng m&agrave; kh&ocirc;ng c&ograve;n bất cứ nỗi lo n&agrave;o về hư hại từ nước.</p>\r\n\r\n<p><img alt=\"iPhone 12 chống nước\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-5(1).JPG\" title=\"iPhone 12 chống nước hoàn hảo\" /></p>\r\n\r\n<h2><strong>M&agrave;u sắc mới của iPhone 12</strong></h2>\r\n\r\n<p>iPhone 12 mang đến cho bạn nhiều sự lựa chọn m&agrave;u hơn bao giờ hết. C&oacute; tới 5&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/iphone-12-co-may-mau-mau-sac-nao-se-hot-nhat-nam-nay-125077\" title=\"iPhone 12 có mấy màu\" type=\"iPhone 12 có mấy màu\">m&agrave;u sắc iPhone 12</a>&nbsp;thời trang, bao gồm Đen, Trắng, Đỏ, Xanh l&aacute; v&agrave; Xanh dương. Bạn c&oacute; thể tự do thể hiện c&aacute; t&iacute;nh, kh&aacute;c biệt với phi&ecirc;n bản m&agrave;u sắc ph&ugrave; hợp. Ngo&agrave;i ra iPhone 12&nbsp;c&oacute; 3 phi&ecirc;n bản dung lượng cho người d&ugrave;ng lựa chọn gồm:&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12?dung-luong=64gb\" title=\"iPhone 12 64GB\" type=\"iPhone 12 64GB\">iPhone 12 64GB</a>,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12?dung-luong=128gb\" title=\"iPhone 12 128GB\" type=\"iPhone 12 128GB\">iPhone 12 128GB</a>&nbsp;v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12?dung-luong=256gb\" title=\"iPhone 12 256GB\" type=\"iPhone 12 256GB\">iPhone 12 256GB</a>.</p>\r\n\r\n<p><img alt=\"Màu sắc mới của iPhone 12\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-8(1).JPG\" title=\"Màu sắc mới của iPhone 12\" /></p>\r\n\r\n<p><em>Tổng hợp c&aacute;c t&ugrave;y chọn m&agrave;u sắc mới tr&ecirc;n iPhone 12</em></p>\r\n\r\n<h2><strong>5G si&ecirc;u tốc, mở ra kỷ nguy&ecirc;n di động mới</strong></h2>\r\n\r\n<p><a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/iphone-12-co-5g-khong-tinh-nang-nay-co-thuc-su-quan-trong-124977\" title=\"iPhone 12 sẽ có hỗ trợ kết nối mạng 5G\" type=\"iPhone 12 sẽ có hỗ trợ kết nối mạng 5G\">iPhone 12 sẽ c&oacute; hỗ trợ kết nối mạng 5G</a>&nbsp;nhanh nhất hiện nay. Bạn c&oacute; thể l&agrave;m việc, giải tr&iacute; với tốc độ mạng nhanh đ&aacute;ng kinh ngạc. Xem video trực tuyến, ph&aacute;t trực tiếp, chơi game online, gọi FaceTime HD hay l&agrave;m bất cứ điều g&igrave; bạn muốn m&agrave; kh&ocirc;ng hề c&oacute; hiện tượng giật h&igrave;nh v&igrave; mạng yếu. iPhone 12 cho trải nghiệm mạng di động nhanh chưa từng thấy.</p>\r\n\r\n<p><img alt=\"iPhone 12 với 5G\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-7(1).JPG\" title=\"iPhone 12 với 5G siêu tốc, mở ra kỷ nguyên di động mới\" /></p>\r\n\r\n<p><em>iPhone 12 sẽ c&oacute; hỗ trợ kết nối 5G cho c&aacute;c model series</em></p>\r\n\r\n<h2><strong>Apple A14 Bionic, bộ vi xử l&yacute; nhanh nhất thế giới smartphone</strong></h2>\r\n\r\n<p>Sức mạnh của iPhone 12 vượt trội so với phần c&ograve;n lại nhờ bộ vi xử l&yacute;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-chip-apple-a14-trai-tim-se-lam-nen-suc-manh-cho-iphone-12-121314\" title=\"Chip Apple A14 Bionic\" type=\"Chip Apple A14 Bionic\">Apple A14 Bionic</a>, con chip nhanh nhất thế giới điện thoại hiện nay. Apple A14 Bionic l&agrave; con chip đầu ti&ecirc;n tr&ecirc;n thế giới sản xuất tr&ecirc;n tiến tr&igrave;nh 5nm, với 6 nh&acirc;n CPU v&agrave; 4 nh&acirc;n GPU c&ugrave;ng 11,8 tỷ b&oacute;ng b&aacute;n dẫn, kh&ocirc;ng chỉ cho hiệu năng tuyệt đỉnh m&agrave; c&ograve;n tiết kiệm năng lượng hơn rất nhiều.</p>\r\n\r\n<p>Apple A14 Bionic cũng được n&acirc;ng cấp về khả năng học hỏi th&oacute;i quen người d&ugrave;ng khi tăng từ 8 l&ecirc;n 16 l&otilde;i Neural Engine, đồng thời trang bị bộ xử l&yacute; t&iacute;n hiệu h&igrave;nh ảnh ho&agrave;n to&agrave;n mới để iPhone 12 c&oacute; thể mang đến những điều kh&aacute;c biệt trong cả chụp ảnh v&agrave; quay phim.</p>\r\n\r\n<p><img alt=\"Chip Apple A14 Bionic trên iPhone 12\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-10.JPG\" title=\"Chip Apple A14 Bionic trên iPhone 12\" /></p>\r\n\r\n<p><em>Hiệu năng iPhone 12 c&oacute; trở n&ecirc;n &quot;v&ocirc; đối&quot; với chip Apple A14 Bionic?</em></p>\r\n\r\n<h2><strong>M&agrave;n h&igrave;nh OLED Super Retina XDR si&ecirc;u sắc n&eacute;t</strong></h2>\r\n\r\n<p>So với m&agrave;n h&igrave;nh iPhone 11,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tat-ve-man-hinh-iphone-12-ba-kich-co-cong-nghe-oled-va-an-so-120hz-124872\" title=\"màn hình iPhone 12\" type=\"màn hình iPhone 12\">m&agrave;n h&igrave;nh iPhone 12</a>&nbsp;đ&atilde; c&oacute; một sự nhảy vọt. Ngo&agrave;i thiết kế viền mỏng hơn, chất lượng m&agrave;n h&igrave;nh iPhone 12 cải thiện r&otilde; rệt với c&ocirc;ng nghệ OLED v&agrave; độ sắc n&eacute;t tuyệt vời từ c&ocirc;ng nghệ Super Retina XDR. Bạn sẽ được chi&ecirc;m ngưỡng những h&igrave;nh ảnh gi&agrave;u chi tiết, độ tương phản cao tới 2.000.000:1, m&agrave;u sắc rực rỡ v&agrave; m&agrave;u đen cực s&acirc;u. Đ&acirc;y l&agrave; m&agrave;n h&igrave;nh&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\" title=\"Mua điện thoại iPhone chính hãng giá tốt nhất tại FPTShop\" type=\"Mua điện thoại iPhone chính hãng giá tốt nhất tại FPTShop\">iPhone</a>&nbsp;đẹp nhất từ trước đến nay, khiến bạn đắm ch&igrave;m trong kh&ocirc;ng gian giải tr&iacute; hấp dẫn.</p>\r\n\r\n<p><img alt=\"Màn hình iPhone 12 \" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-9.JPG\" title=\"Màn hình OLED Super Retina XDR siêu sắc nét của iPhone 12 \" /></p>', '23000000', '20800000', 'iphone-12-red-select-202070.png', 'iphone,apple,dien-thoai', 1, NULL, NULL),
(26, 4, 3, 'iPhone 11 Pro 64GB', '199', 1, 'Iphone series 11', '<h2>Đặc điểm nổi bật của&nbsp;iPhone 11 Pro 64GB</h2>\r\n\r\n<p><img alt=\"iPhone 11 Pro 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/23/637443160738411341_Custom%20Size%20%E2%80%93%201.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 Pro 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/23/637443160741044429_Custom%20Size%20%E2%80%93%203.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 Pro 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/23/637443160740108825_Custom%20Size%20%E2%80%93%202.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 Pro 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/23/637443160740888277_Custom%20Size%20%E2%80%93%204.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 Pro 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/23/637443160738411341_Custom%20Size%20%E2%80%93%205.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 Pro 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/23/637443160735106730_Custom%20Size%20%E2%80%93%206.png\" /></p>\r\n\r\n<h2>Đ&aacute;nh gi&aacute; chi tiết&nbsp;iPhone 11 Pro 64GB</h2>\r\n\r\n<p><strong>Ch&agrave;o đ&oacute;n bạn đến với thế hệ iPhone Pro đầu ti&ecirc;n.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-11-pro-64gb\" target=\"_blank\">iPhone 11 Pro</a>&nbsp;sở hữu hệ thống 3 camera đẳng cấp, một bước nhảy vọt về thời lượng pin v&agrave; con chip mới c&oacute; hiệu năng mạnh mẽ chưa từng thấy. Mạnh mẽ, kh&aacute;c biệt v&agrave; chuy&ecirc;n nghiệp, sản phẩm ho&agrave;n to&agrave;n xứng đ&aacute;ng với t&ecirc;n gọi.</strong></p>\r\n\r\n<p><strong><img alt=\"iPhone Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-1.jpg\" /></strong></p>\r\n\r\n<h3><strong>Kiệt t&aacute;c thiết kế, 4 m&agrave;u sắc thời thượng</strong></h3>\r\n\r\n<p>Ho&agrave;n thiện từ khung th&eacute;p kh&ocirc;ng gỉ chắc chắn v&agrave; hai mặt k&iacute;nh cường lực b&oacute;ng bẩy, iPhone 11 Pro 64GB thật sự nổi bật giữa đ&aacute;m đ&ocirc;ng. Chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>&nbsp;nhỏ gọn, cho cảm gi&aacute;c cầm nắm ho&agrave;n hảo nhưng vẫn c&oacute; m&agrave;n h&igrave;nh đủ lớn để trải nghiệm tốt mọi thứ. 4 phi&ecirc;n bản m&agrave;u sắc sang trọng, mang đến nhiều lựa chọn cho người d&ugrave;ng. 4 m&agrave;u sắc iPhone 11 Pro bao gồm V&agrave;ng, X&aacute;m kh&ocirc;ng gian, Bạc v&agrave; đặc biệt l&agrave; m&agrave;u mới Xanh Midnight đầy cuốn h&uacute;t.</p>\r\n\r\n<p><img alt=\"thiết kế iPhone Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro.jpg\" /></p>\r\n\r\n<h3><strong>Chiếc iPhone đầu ti&ecirc;n c&oacute; 3 camera sau</strong></h3>\r\n\r\n<p>Hệ thống 3 camera lần đầu ti&ecirc;n xuất hiện tr&ecirc;n Apple iPhone 11 Pro. Chụp cảnh rộng gấp 4 lần; h&igrave;nh ảnh tuyệt đẹp trong điều kiện &aacute;nh s&aacute;ng yếu; quay video chất lượng cao nhất thế giới&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>&nbsp;v&agrave; tr&ecirc;n hết khả năng chỉnh sửa với những c&ocirc;ng cụ chuy&ecirc;n nghiệp ngay tr&ecirc;n chiếc điện thoại của bạn. iPhone 11 Pro sẽ l&agrave; nơi khởi nguồn cho những s&aacute;ng tạo v&agrave; cảm hứng bất tận trong nhiếp ảnh.</p>\r\n\r\n<p><img alt=\"camera iPhone Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-2.jpg\" /></p>\r\n\r\n<h3><strong>Kh&ocirc;ng giới hạn tầm nh&igrave;n</strong></h3>\r\n\r\n<p>Với bộ 3 camera đẳng cấp, điện thoại iPhone 11 Pro cho ph&eacute;p bạn ph&oacute;ng to ảnh từ camera Tele cho đến camera g&oacute;c si&ecirc;u rộng mới. Khả năng zoom quang 4x v&agrave; thu được khung cảnh lớn gấp 4 lần đầy ấn tượng. H&atilde;y mang những d&atilde;y n&uacute;i h&ugrave;ng vĩ, những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c đặc biệt hay cả một c&aacute;nh rừng nguy&ecirc;n sinh v&agrave;o trong ống k&iacute;nh của ri&ecirc;ng bạn.</p>\r\n\r\n<p><img alt=\"góc siêu rộng iPhone Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-3.jpg\" /></p>\r\n\r\n<h3><strong>Chụp ảnh ho&agrave;n hảo ngay cả khi &aacute;nh s&aacute;ng yếu</strong></h3>\r\n\r\n<p>Từ nh&agrave; h&agrave;ng &aacute;nh đ&egrave;n lung linh cho đến b&atilde;i biển dưới &aacute;nh trăng, iPhone 11 Pro dễ d&agrave;ng ghi lại những khoảnh khắc của bạn trong điều kiện thiếu s&aacute;ng nhờ chế độ chụp đ&ecirc;m Night Mode. Sự kết hợp giữa cảm biến camera mới, phần mềm th&ocirc;ng minh v&agrave; khả năng xử l&yacute; tuyệt vời của con chip A13 Bionic mang lại những bức ảnh thiếu s&aacute;ng chưa từng c&oacute; tr&ecirc;n iPhone. Đơn giản bạn chỉ cần đưa m&aacute;y l&ecirc;n v&agrave; chụp, điện thoại sẽ tự động nhận diện điều kiện &aacute;nh s&aacute;ng để đưa ra chế độ chụp ph&ugrave; hợp, cho kết quả ho&agrave;n hảo nhất.</p>\r\n\r\n<p><img alt=\"iPhone Pro thiếu sáng\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-4.jpg\" /></p>\r\n\r\n<h3><strong>Ảnh ch&acirc;n dung chuẩn studio</strong></h3>\r\n\r\n<p>Với 3 m&aacute;y ảnh hoạt động c&ugrave;ng l&uacute;c, khả năng chụp ảnh ch&acirc;n dung của iPhone 11 Pro 64GB trở n&ecirc;n chuy&ecirc;n nghiệp hơn bao giờ hết. Bạn sẽ c&oacute; th&ecirc;m những hiệu ứng &aacute;nh s&aacute;ng đơn sắc chuẩn studio.&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tim-hieu-ve-cong-nghe-chup-xoa-phong-la-gi-63770\">Chế độ x&oacute;a ph&ocirc;ng</a>&nbsp;mới cũng cho ph&eacute;p bạn điều chỉnh cường độ &aacute;nh s&aacute;ng, nhờ vậy chủ thể sẽ xuất hiện đẹp lung linh, đậm phong c&aacute;ch v&agrave; c&aacute; t&iacute;nh kh&aacute;c biệt.</p>\r\n\r\n<p><img alt=\"iPhone Pro xóa phông\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-5.jpg\" /></p>\r\n\r\n<h3><strong>Sắc n&eacute;t đến từng chi tiết với Smart HDR</strong></h3>\r\n\r\n<p>Những bức ảnh chụp bằng iPhone 11 Pro sẽ c&oacute; chất lượng kh&oacute; tin nhờ Smart HDR thế hệ mới. C&aacute;c thuật to&aacute;n ti&ecirc;n tiến c&ugrave;ng khả năng tự học hỏi Machine Learning được &aacute;p dụng để l&agrave;m nổi bật &aacute;nh s&aacute;ng, hiệu ứng b&oacute;ng đổ v&agrave; từng chi tiết nhỏ trong bức ảnh. Apple iPhone 11 Pro c&oacute; thể tinh chỉnh ri&ecirc;ng biệt chủ thể v&agrave; cảnh nền, t&iacute;nh năng vốn chỉ c&oacute; ở một số m&aacute;y ảnh DSLR cao cấp. Mỗi bức ảnh sẽ l&agrave; một t&aacute;c phẩm nghệ thuật với iPhone 11 Pro.</p>\r\n\r\n<p><img alt=\"iPhone Pro smart hdr\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iPhone-11-Pro-6.jpg\" /></p>', '22400000', '20000000', 'tải xuống (5)91.jpg', 'iphone,dien-thoai,apple', 1, NULL, NULL),
(27, 4, 3, 'iPhone 11 64GB', '200', 0, 'Iphone series 11', '<h2>Đặc điểm nổi bật của&nbsp;iPhone 11 64GB</h2>\r\n\r\n<p><img alt=\"iPhone 11 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/11/637432846053372998_Custom%20Size%20%E2%80%93%201.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/11/637432846056810489_Custom%20Size%20%E2%80%93%202.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/11/637432846055873033_Custom%20Size%20%E2%80%93%203.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/11/637432846056966742_Custom%20Size%20%E2%80%93%204.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/11/637432846055560524_Custom%20Size%20%E2%80%93%205.png\" /></p>\r\n\r\n<p><img alt=\"iPhone 11 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/11/637432846054623023_Custom%20Size%20%E2%80%93%206.png\" /></p>\r\n\r\n<h2>Đ&aacute;nh gi&aacute; chi tiết&nbsp;iPhone 11 64GB</h2>\r\n\r\n<p><strong><a href=\"https://fptshop.com.vn/dien-thoai/iphone-11-64gb\" target=\"_blank\">iPhone 11</a>&nbsp;với 6 phi&ecirc;n bản m&agrave;u sắc, camera c&oacute; khả năng chụp ảnh vượt trội, thời lượng pin cực d&agrave;i v&agrave; bộ vi xử l&yacute; mạnh nhất từ trước đến nay sẽ mang đến trải nghiệm tuyệt vời d&agrave;nh cho bạn.</strong></p>\r\n\r\n<p><strong><img alt=\"iPhone 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-1.jpg\" /></strong></p>\r\n\r\n<h3><strong>Rực rỡ sắc m&agrave;u, thể hiện c&aacute; t&iacute;nh</strong></h3>\r\n\r\n<p>C&oacute; tới 6 sự lựa chọn m&agrave;u sắc cho iPhone 11 64GB, bao gồm T&iacute;m, V&agrave;ng, Xanh lục, Đen, Trắng v&agrave; Đỏ, tha hồ để bạn lựa chọn m&agrave;u ph&ugrave; hợp với sở th&iacute;ch, c&aacute; t&iacute;nh ri&ecirc;ng của bản th&acirc;n. Vẻ đẹp của iPhone 11 đến từ thiết kế cao cấp khi được l&agrave;m từ khung nh&ocirc;m nguy&ecirc;n khối v&agrave; mặt lưng liền lạc với một tấm k&iacute;nh duy nhất. Ở mặt trước, bạn sẽ được chi&ecirc;m ngưỡng m&agrave;n h&igrave;nh Liquid Retina lớn 6,1 inch, m&agrave;u sắc v&ocirc; c&ugrave;ng ch&acirc;n thực. Tất cả tạo n&ecirc;n chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;tr&agrave;n đầy hứng khởi.</p>\r\n\r\n<p><img alt=\"iPhone 11 màu sắc \" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-2.jpg\" /></p>\r\n\r\n<h3><strong>Hệ thống camera k&eacute;p mới</strong></h3>\r\n\r\n<p>Apple iPhone 11 sở hữu cụm camera k&eacute;p mặt sau, bao gồm camera g&oacute;c rộng v&agrave; camera g&oacute;c si&ecirc;u rộng. Cảm biến camera g&oacute;c rộng 12MP c&oacute; khả năng lấy n&eacute;t tự động nhanh gấp 3 lần trong điều kiện thiếu s&aacute;ng. B&ecirc;n cạnh đ&oacute; cảm biến g&oacute;c si&ecirc;u rộng cho khả năng chụp cảnh rộng gấp 4 lần, l&agrave; phương tiện ghi h&igrave;nh tuyệt vời cho những chuyến du lịch, chụp h&igrave;nh nh&oacute;m. Nhanh ch&oacute;ng chụp ảnh, quay video, chỉnh sửa v&agrave; chia sẻ ngay tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>, bạn sẽ kh&ocirc;ng bỏ lỡ bất cứ khoảnh khắc đ&aacute;ng nhớ n&agrave;o.</p>\r\n\r\n<p><img alt=\"iPhone 11 camera kép\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-3.jpg\" /></p>\r\n\r\n<h3><strong>Quay video chưa bao giờ chuy&ecirc;n nghiệp đến thế</strong></h3>\r\n\r\n<p>Điện thoại iPhone 11 64GB c&oacute; khả năng quay những đoạn video tuyệt đẹp với độ ph&acirc;n giải 4K 60fps si&ecirc;u sắc n&eacute;t. Camera g&oacute;c si&ecirc;u rộng mang đến nhiều khung cảnh hơn, đồng thời khả năng quay chuyển động cực ấn tượng sẽ tạo n&ecirc;n những thước phim ho&agrave;n hảo. Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể tập trung thu &acirc;m v&agrave;o một chủ thể khi quay video bằng c&aacute;ch ph&oacute;ng to, zoom h&igrave;nh ảnh &ndash; đồng thời zoom &acirc;m thanh hết sức th&uacute; vị. Tất nhi&ecirc;n sau khi quay video, việc chỉnh sửa v&agrave; xuất bản sẽ diễn ra hết sức tiện lợi, nhanh ch&oacute;ng tr&ecirc;n iPhone 11.</p>\r\n\r\n<p><img alt=\"video iPhone 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-4.jpg\" /></p>\r\n\r\n<h3><strong>Night Mode, chuy&ecirc;n gia chụp ảnh thiếu s&aacute;ng</strong></h3>\r\n\r\n<p>Chụp ảnh thiếu s&aacute;ng kh&ocirc;ng c&ograve;n l&agrave; vấn đề tr&ecirc;n iPhone 11. Với chế độ chụp đ&ecirc;m chuy&ecirc;n dụng Night Mode ho&agrave;n to&agrave;n mới, m&aacute;y ảnh sẽ nh&igrave;n thấy r&otilde; r&agrave;ng từng chi tiết trong đ&ecirc;m tối. Kh&ocirc;ng cần phải bật đ&egrave;n flash, h&igrave;nh ảnh vẫn trở n&ecirc;n sống động nhờ &aacute;nh s&aacute;ng tự nhi&ecirc;n, m&agrave;u sắc ch&iacute;nh x&aacute;c v&agrave; chất ảnh s&aacute;ng r&otilde;.</p>\r\n\r\n<p><img alt=\"iPhone 11 night mode\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-5.jpg\" /></p>\r\n\r\n<h3><strong>Hiệu ứng x&oacute;a ph&ocirc;ng &ldquo;ảo diệu&rdquo;</strong></h3>\r\n\r\n<p>Những bức ảnh ch&acirc;n dung x&oacute;a ph&ocirc;ng của bạn sẽ trở n&ecirc;n &ldquo;ảo diệu&rdquo; hơn tr&ecirc;n iPhone 11. Với cơ chế x&oacute;a ph&ocirc;ng v&agrave; &aacute;nh s&aacute;ng mới, giờ đ&acirc;y bạn c&oacute; thể chụp ảnh x&oacute;a ph&ocirc;ng c&ugrave;ng người bạn th&acirc;n; th&ecirc;m hiệu ứng &aacute;nh s&aacute;ng Studio; thay đổi độ mờ ph&ocirc;ng nền. C&aacute;c bức ảnh x&oacute;a ph&ocirc;ng sẽ c&oacute; độ l&agrave;m mờ tuyệt hảo, ph&acirc;n t&aacute;ch chủ thể ch&iacute;nh x&aacute;c v&agrave; &aacute;nh s&aacute;ng đẹp mắt.</p>\r\n\r\n<p><img alt=\"iPhone 11 xóa phông\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-6.jpg\" /></p>\r\n\r\n<h3><strong>Smart HDR thế hệ mới, chụp ảnh tốt hơn trong điều kiện &aacute;nh s&aacute;ng phức tạp</strong></h3>\r\n\r\n<p>Nhờ tr&iacute; tuệ nh&acirc;n tạo Machine Learning, t&iacute;nh năng Smart HDR tr&ecirc;n camera iPhone 11 th&ocirc;ng minh hơn bao giờ hết. Camera sẽ tự động nhận diện người v&agrave; cảnh, sau đ&oacute; đưa ra c&aacute;c tinh chỉnh ri&ecirc;ng biệt. Trong c&ugrave;ng một bức ảnh, khu&ocirc;n mặt sẽ c&oacute; điểm nhấn đẹp hơn, t&ocirc;ng m&agrave;u da tự nhi&ecirc;n &ndash; trong khi đ&oacute; phần khung cảnh nền ho&agrave;ng h&ocirc;n vẫn tuyệt đẹp theo một c&aacute;ch kh&aacute;c. Smart HDR sẽ gi&uacute;p điện thoại xử l&yacute; bức ảnh xuất sắc, đặc biệt l&agrave; trong điều kiện &aacute;nh s&aacute;ng phức tạp.</p>\r\n\r\n<p><img alt=\"iPhone 11 smart HDR\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-11-7.jpg\" /></p>', '16690000', '15800000', '21325844445.jpeg', 'iphone,apple,dien-thoai,iphone-11', 1, NULL, NULL),
(28, 4, 3, 'iPhone XR 64GB', '200', 0, 'IPhone', '<h2>Đặc điểm nổi bật của&nbsp;iPhone XR 64GB</h2>\r\n\r\n<p><img alt=\"iPhone XR 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2019/1/2/636820266201692916_3.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone XR 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2019/1/2/636820266201902916_2.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone XR 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2019/1/2/636820266201852916_1.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone XR 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2019/1/2/636820266202262916_4.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone XR 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2019/1/2/636820266206032796_5.jpg\" /></p>\r\n\r\n<h2>Đ&aacute;nh gi&aacute; chi tiết&nbsp;iPhone XR 64GB</h2>\r\n\r\n<p>Chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>&nbsp;với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/ti-m-hie-u-ve-cong-nghe-liquid-retina-tren-iphone-xr-73345\">m&agrave;n h&igrave;nh Liquid Retina</a>&nbsp;ho&agrave;n to&agrave;n mới, c&ocirc;ng nghệ m&agrave;n h&igrave;nh LCD ti&ecirc;n tiến nhất đi c&ugrave;ng Face ID nhanh hơn, con chip mạnh mẽ v&agrave; hệ thống camera x&oacute;a ph&ocirc;ng ho&agrave;n hảo. Đ&oacute; ch&iacute;nh l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-xr-64gb\" target=\"_blank\"><strong>iPhone XR</strong>&nbsp;</a>với nhiều m&agrave;u sắc tuyệt vời đang chờ đ&oacute;n bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-16.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh Liquid Retina cực lớn</strong></h3>\r\n\r\n<p><strong>iPhone XR</strong>&nbsp;sử dụng m&agrave;n h&igrave;nh LCD ti&ecirc;n tiến nhất hiện nay, hiển thị tuyệt đẹp với k&iacute;ch thước lớn 6,1 inch Liquid Retina sắc n&eacute;t, tr&agrave;n viền cạnh. Thiết kế tr&agrave;n viền gi&uacute;p cho d&ugrave; m&agrave;n h&igrave;nh lớn tới 6,1 inch nhưng iPhone XR vẫn nhỏ gọn hơn so với&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-8-plus-64gb\">iPhone 8 Plus</a>, dễ d&agrave;ng cầm nắm thao t&aacute;c bằng một tay. C&ocirc;ng nghệ LCD cho m&agrave;u sắc ch&iacute;nh x&aacute;c, độ s&aacute;ng cao, t&aacute;i tạo cuộc sống v&ocirc; c&ugrave;ng ch&acirc;n thực. C&oacute; tới 6 tấm cảm biến đ&egrave;n nền dưới m&agrave;n h&igrave;nh, tự động điều chỉnh c&acirc;n bằng trắng th&ocirc;ng minh để thay đổi nhiệt độ m&agrave;u dựa theo m&ocirc;i trường, để bạn lu&ocirc;n được tận hưởng m&agrave;n h&igrave;nh đẹp v&agrave; dễ chịu cho mắt.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-4.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Chiếc iPhone đầy m&agrave;u sắc tuyệt đẹp</strong></h3>\r\n\r\n<p><strong>iPhone XR</strong>&nbsp;c&oacute; kiểu d&aacute;ng đẹp mắt khi được ho&agrave;n thiện từ nh&ocirc;m v&agrave; vỏ k&iacute;nh bền nhất trong thế giới&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>. Sử dụng tới 7 tầm nền m&agrave;u sắc gi&uacute;p m&agrave;u sơn c&oacute; độ s&acirc;u đầy ấn tượng phản chiếu qua lớp k&iacute;nh sang trọng. Việc sử dụng vỏ k&iacute;nh cũng gi&uacute;p cho iPhone XR c&oacute; khả năng&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-cap?tinh-nang=sac-khong-day\">sạc kh&ocirc;ng d&acirc;y</a>. Rất nhiều m&agrave;u sắc đang chờ bạn lựa chọn: Đỏ, v&agrave;ng, trắng, hồng, đen, xanh. Kh&ocirc;ng chỉ đẹp,&nbsp;<strong>iPhone XR</strong>&nbsp;c&ograve;n rất bền vững nhờ khả năng chống nước, mang tới sự y&ecirc;n t&acirc;m khi sử dụng.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-17.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Nhận diện khu&ocirc;n mặt Face ID n&acirc;ng cao</strong></h3>\r\n\r\n<p>Bảo mật tr&ecirc;n&nbsp;<strong>iPhone XR</strong>&nbsp;đơn giản v&agrave; an to&agrave;n hơn bao giờ hết khi mật khẩu ch&iacute;nh l&agrave; khu&ocirc;n mặt của bạn. Bạn c&oacute; thể mở kh&oacute;a iPhone, đăng nhập v&agrave;o ứng dụng hay thanh to&aacute;n chỉ trong nh&aacute;y mắt m&agrave; kh&ocirc;ng cần nhập mật khẩu. Đ&acirc;y l&agrave; phương thức bảo mật an to&agrave;n nhất tr&ecirc;n smartphone v&agrave; n&oacute; c&ograve;n nhanh hơn nữa tr&ecirc;n iPhone XR.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-11.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Apple A12 Bionic, con chip th&ocirc;ng minh v&agrave; mạnh mẽ nhất</strong></h3>\r\n\r\n<p><strong>iPhone XR</strong>&nbsp;được trang bị con chip A12 Bionic c&oacute; 6 nh&acirc;n, trong đ&oacute; 2 nh&acirc;n hiệu năng cao nhanh hơn 15% v&agrave; 4 nh&acirc;n tiết kiệm điện tiết kiệm hơn 50% so với thế hệ trước A11 Bionic của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-x\">iPhone X</a>. Đồng thời GPU đồ họa của A12 Bionic mạnh hơn tới 50%, cho ph&eacute;p bạn chơi mọi tựa game ở cấu h&igrave;nh cao nhất một c&aacute;ch nhẹ nh&agrave;ng v&agrave; mượt m&agrave;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-12.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng chỉ mạnh mẽ, điểm kh&aacute;c biệt của A12 Bionic c&ograve;n đến từ sự th&ocirc;ng minh của con chip n&agrave;y. Với 8 nh&acirc;n Neural Engine c&oacute; khả năng học hỏi theo thời gian thực, iPhone XR c&oacute; thể nhận ra th&oacute;i quen sử dụng điện thoại của bạn, sau đ&oacute; đưa ra những dự đo&aacute;n, h&agrave;nh động xử l&yacute; nhanh dựa tr&ecirc;n những g&igrave; bạn l&agrave;m từng ng&agrave;y. 8 nh&acirc;n AI ri&ecirc;ng biệt c&ograve;n hỗ trợ cho&nbsp;<strong>iPhone XR</strong>trong rất nhiều trường hợp, từ nhận diện khu&ocirc;n mặt,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tim-hieu-ve-cong-nghe-chup-xoa-phong-la-gi-63770\">chụp ảnh x&oacute;a ph&ocirc;ng</a>,<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cach-tat-smart-hdr-va-auto-hdr-tren-iphone-80056\">&nbsp;smart HDR</a>&nbsp;cho đến&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/cong-nghe-thuc-te-ao-tang-cuong-ar-xu-huong-cong-nghe-tuong-lai-52560\">c&ocirc;ng nghệ thực tế tăng cường AR</a>.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-2.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Kỷ nguy&ecirc;n mới trong nhiếp ảnh di động</strong></h3>\r\n\r\n<p><strong>iPhone XR</strong>&nbsp;sở hữu cảm biến camera xuất sắc h&agrave;ng đầu, với độ ph&acirc;n giải 12MP, khẩu độ f/1.8, lấy n&eacute;t nhanh gấp đ&ocirc;i, chụp đ&ecirc;m sắc n&eacute;t v&agrave; c&oacute; khả năng chụp ảnh x&oacute;a ph&ocirc;ng ưu việt. T&iacute;nh năng Smart HDR mang tới những bức ảnh với &aacute;nh s&aacute;ng ch&acirc;n thực, kể cả trong điều kiện ngược s&aacute;ng. iPhone XR kh&ocirc;ng chỉ chụp ảnh x&oacute;a ph&ocirc;ng ch&iacute;nh x&aacute;c m&agrave; c&ograve;n c&oacute; thể điều chỉnh được độ mờ của hậu cảnh, th&ecirc;m c&aacute;c hiệu ứng để ảnh ch&acirc;n dung của bạn đẹp một c&aacute;ch chuy&ecirc;n nghiệp.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-1.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khả năng chụp thiếu s&aacute;ng của iPhone XR được n&acirc;ng l&ecirc;n một tầm cao mới nhờ cảm biến s&acirc;u hơn, điểm ảnh lớn hơn. D&ugrave; l&agrave; trong đ&ecirc;m tối th&igrave; h&igrave;nh ảnh vẫn đầy đủ chi tiết v&agrave; &iacute;t nhiễu. Bạn cũng c&oacute; thể chụp những bức ảnh chuyển động nhanh một c&aacute;ch dễ d&agrave;ng khi tốc độ bắt n&eacute;t của iPhone XR gần như ngay lập tức.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-13.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Chiếc iPhone c&oacute; pin tốt nhất</strong></h3>\r\n\r\n<p><strong>iPhone XR</strong>&nbsp;ch&iacute;nh l&agrave; chiếc iPhone c&oacute; thời lượng pin tốt nhất hiện nay, đảm bảo cho bạn sử dụng thoải m&aacute;i trong thời gian l&ecirc;n tới hai ng&agrave;y. C&ocirc;ng việc v&agrave; giải tr&iacute; sẽ lu&ocirc;n lu&ocirc;n tr&ocirc;i chảy với một chiếc điện thoại hiệu năng mạnh, pin tốt như iPhone XR.</p>\r\n\r\n<p><img alt=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-xr-9.jpg\" /></p>', '11990000', '10500000', '637274707339798536_2 iPhoneXr-Red-PB-PF-US-EN-SCREEN2.jpg', 'iphone,di-dong,dien-thoai,apple', 1, NULL, NULL),
(29, 4, 3, 'iPhone 12 Pro 128GB', '200', 0, 'Iphone series 12', '<h2>Đặc điểm nổi bật của&nbsp;iPhone 12 Pro</h2>\r\n\r\n<p><img alt=\"iPhone 12 Pro 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/7/637429538430750697_Artboard%201.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/7/637429538430906818_Artboard%202.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/7/637429538430595739_Artboard%203.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/7/637429538430125438_Artboard%204.jpg\" /></p>\r\n\r\n<p><img alt=\"iPhone 12 Pro 128GB\" src=\"https://img.fpt.shop/w_665/h_374/f_jpg/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/7/637429538429813220_Artboard%205.jpg\" /></p>\r\n\r\n<h2>Đ&aacute;nh gi&aacute; chi tiết&nbsp;iPhone 12 Pro</h2>\r\n\r\n<p><strong>Đến với đẳng cấp Pro đ&iacute;ch thực, nơi m&agrave; những điều đặc biệt đang chờ đ&oacute;n bạn tr&ecirc;n&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro\" title=\"iPhone 12 Pro chính hãng tại FPTShop\" type=\"iPhone 12 Pro chính hãng tại FPTShop\">iPhone 12 Pro</a>. Từ hệ thống camera Pro chụp thiếu s&aacute;ng cực đỉnh, kết nối 5G si&ecirc;u tốc cho đến bộ vi xử l&yacute; A14 Bionic nhanh nhất thế giới smartphone, vẫn c&ograve;n nhiều bất ngờ kh&aacute;c để bạn kh&aacute;m ph&aacute;.</strong></p>\r\n\r\n<p><strong><img alt=\"Điện thoại iPhone 12 Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-1.jpg\" title=\"Điện thoại iPhone 12 Pro chính hãng tại FPTShop\" /></strong></p>\r\n\r\n<h3><strong>Viền m&agrave;n h&igrave;nh mỏng hơn, m&agrave;n h&igrave;nh lớn hơn</strong></h3>\r\n\r\n<p>Nhờ viền m&agrave;n h&igrave;nh mỏng hơn, iPhone 12 Pro đ&atilde; c&oacute; thể trang bị một m&agrave;n h&igrave;nh lớn hơn, nhưng k&iacute;ch thước vẫn nhỏ gọn tương tự&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-11-pro-64gb\" title=\"iPhone 11 Pro\" type=\"iPhone 11 Pro\">iPhone 11 Pro</a>. Giờ đ&acirc;y bạn sẽ c&oacute; m&agrave;n h&igrave;nh lớn tới 6,1 inch, để trải nghiệm được nhiều hơn.</p>\r\n\r\n<p>Ấn tượng hơn nữa, m&agrave;n h&igrave;nh iPhone 12 Pro si&ecirc;u sắc n&eacute;t với c&ocirc;ng nghệ Super Retina XDR. Tấm nền OLED mang tới h&igrave;nh ảnh trong trẻo, m&agrave;u sắc ch&iacute;nh x&aacute;c, độ tương phản l&ecirc;n tới 2.000.000:1, độ s&aacute;ng tối đa 1200 nits. C&aacute;c c&ocirc;ng nghệ kh&aacute;c như HDR hay True Tone khiến cho chất lượng hiển thị của iPhone 12 Pro th&ecirc;m phần ho&agrave;n hảo.</p>\r\n\r\n<p><img alt=\"màn hình iPhone 12 Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-10.jpg\" /></p>\r\n\r\n<h3><strong>Mặt k&iacute;nh m&agrave;n h&igrave;nh bền nhất thế giới smartphone</strong></h3>\r\n\r\n<p>Apple đ&atilde; đưa c&ocirc;ng nghệ bảo vệ m&agrave;n h&igrave;nh phủ gốm Ceramic Shield l&ecirc;n iPhone 12 Pro. C&aacute;c tinh thể gốm nano cứng hơn cả kim loại v&agrave; k&iacute;nh được đưa v&agrave;o tấm k&iacute;nh m&agrave;n h&igrave;nh, mang tới độ bền cũng như khả năng chống xước tuyệt vời. Kết quả l&agrave; m&agrave;n h&igrave;nh iPhone 12 Pro sẽ bền hơn tới 4 lần trong thử nghiệm thả rơi. Nguy cơ bị vỡ m&agrave;n h&igrave;nh của bạn sẽ giảm đi rất nhiều với Ceramic Shield.</p>\r\n\r\n<p><img alt=\"mặt kính iPhone 12 Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-3.jpg\" /></p>\r\n\r\n<h3><strong>T&aacute;c phẩm nghệ thuật đ&iacute;ch thực</strong></h3>\r\n\r\n<p>iPhone 12 Pro đ&atilde; đạt đến tầm tinh xảo thượng thừa trong thiết kế. Bạn sẽ c&oacute; một chiếc điện thoại viền th&eacute;p kh&ocirc;ng gỉ đặc biệt sang trọng v&agrave; cứng c&aacute;p. Phần viền th&eacute;p được l&agrave;m phẳng, vu&ocirc;ng vắn với những đường cắt kim cương s&aacute;ng b&oacute;ng tạo n&ecirc;n vẻ ngo&agrave;i cao cấp, xứng tầm t&aacute;c phẩm nghệ thuật.</p>\r\n\r\n<p>Sẽ c&oacute; 4 m&agrave;u sắc để bạn lựa chọn l&agrave; Bạc, X&aacute;m than ch&igrave;, V&agrave;ng v&agrave; Xanh đại dương. Cả 4 m&agrave;u sắc n&agrave;y đều rất đẳng cấp v&agrave; tạo phong th&aacute;i ri&ecirc;ng cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"thiết kế iPhone 12 Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-2.jpg\" /></p>\r\n\r\n<h3><strong>Đ&aacute;nh bật nỗi lo v&agrave;o nước</strong></h3>\r\n\r\n<p>Chuẩn&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai?tinh-nang-dac-biet=chong-nuoc-bui\" title=\"Tham khảo điện thoại có hỗ trợ chống nước tại FPTShop\" type=\"Tham khảo điện thoại có hỗ trợ chống nước tại FPTShop\">chống nước</a>&nbsp;IP68 tr&ecirc;n iPhone 12 Pro mang tới khả năng chống nước h&agrave;ng đầu trong ng&agrave;nh smartphone. Bạn c&oacute; thể ng&acirc;m nước ở độ s&acirc;u l&ecirc;n đến 6m trong v&ograve;ng 30 ph&uacute;t m&agrave; kh&ocirc;ng ảnh hưởng g&igrave; đến điện thoại. Gần như to&agrave;n bộ mối nguy hại từ nước đều được miễn nhiễm bởi iPhone 12 Pro.</p>\r\n\r\n<p><img alt=\"chống nước iPhone 12 Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-8.jpg\" /></p>\r\n\r\n<h3><strong>C&ocirc;ng nghệ&nbsp;5G si&ecirc;u&nbsp;tốc độ</strong></h3>\r\n\r\n<p><a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/mang-5g-la-gi-tong-quan-ve-mang-khong-day-nhanh-nhat-the-gioi-hien-nay-50246\" title=\"Mạng 5G là gì? Tổng quan về mạng không dây nhanh nhất thế giới hiện nay\" type=\"Mạng 5G là gì? Tổng quan về mạng không dây nhanh nhất thế giới hiện nay\">Kết nối 5G</a>&nbsp;gi&uacute;p cho iPhone 12 Pro trở th&agrave;nh thiết bị ho&agrave;n hảo để v&agrave;o mạng. Giờ đ&acirc;y bạn c&oacute; thể tải xuống những tệp tin lớn, xem phim HDR chất lượng cao, chơi game online si&ecirc;u mượt, kh&ocirc;ng hề c&oacute; bất cứ hiện tượng lag giật nhỏ n&agrave;o. iPhone 12 Pro cũng l&agrave; chiếc điện thoại c&oacute; băng tần 5G lớn nhất hiện nay, mở ra một tương lai mới cho tốc độ Internet.</p>\r\n\r\n<p><img alt=\"5G iPhone 12 Pro\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-12-pro-9.jpg\" /></p>', '30990000', '28900000', '637420740532520004_iphone-12-pro-12-pro-max52.jpg', 'iphone,apple,dien-thoai', 1, NULL, NULL),
(30, 4, 3, 'iPhone SE (2020) 64GB', '200', 0, 'Iphone asdsadhsajk', '<h2>Đặc điểm nổi bật của&nbsp;iPhone SE (2020) 64GB</h2>\r\n\r\n<p><img alt=\"iPhone SE (2020) 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/29/637448543849201856_Custom%20Size%20%E2%80%93%201.png\" /></p>\r\n\r\n<p><img alt=\"iPhone SE (2020) 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/29/637448543761902303_Custom%20Size%20%E2%80%93%203.png\" /></p>\r\n\r\n<p><img alt=\"iPhone SE (2020) 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/29/637448543833681359_Custom%20Size%20%E2%80%93%202.png\" /></p>\r\n\r\n<p><img alt=\"iPhone SE (2020) 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/29/637448543814333181_Custom%20Size%20%E2%80%93%205.png\" /></p>\r\n\r\n<p><img alt=\"iPhone SE (2020) 64GB\" src=\"https://img.fpt.shop/w_665/h_374/f_png/cmpr_10/m_letterbox_ffffff_100/https://fptshop.com.vn/Uploads/Originals/2020/12/29/637448543837721552_Custom%20Size%20%E2%80%93%204.png\" /></p>\r\n\r\n<h2>Đ&aacute;nh gi&aacute; chi tiết&nbsp;iPhone SE (2020) 64GB</h2>\r\n\r\n<p><strong>Một chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>&nbsp;nhỏ gọn mang tr&ecirc;n m&igrave;nh bộ vi xử l&yacute; mạnh mẽ h&agrave;ng đầu,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-se-2\" target=\"_blank\">iPhone SE 2020</a>với mức gi&aacute; hấp dẫn chưa từng thấy sẽ l&agrave; sự lựa chọn ph&ugrave; hợp d&agrave;nh cho tất cả người d&ugrave;ng.</strong></p>\r\n\r\n<p><strong><img alt=\"iPhone SE 2020\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-se-2020-1.jpg\" /></strong></p>\r\n\r\n<h3><strong>Mang t&igrave;nh y&ecirc;u trở lại với thiết kế sang trọng</strong></h3>\r\n\r\n<p>iPhone SE 2020 c&oacute; một thiết kế quen thuộc, thiết kế đ&atilde; d&agrave;nh được rất nhiều t&igrave;nh cảm của người d&ugrave;ng trong những năm qua. Kiểu d&aacute;ng nhỏ gọn, bo tr&ograve;n c&aacute;c cạnh, th&acirc;n m&aacute;y si&ecirc;u mỏng tinh tế v&agrave; ho&agrave;n thiện từ chất liệu nh&ocirc;m kết hợp k&iacute;nh cao cấp, iPhone SE 2020 mang đến cảm hứng bất tận cho người d&ugrave;ng. Chiếc iPhone nhỏ gọn rất dễ d&agrave;ng cầm nắm, thao t&aacute;c hay cho v&agrave;o t&uacute;i mang đi bất cứ đ&acirc;u nhưng vẫn to&aacute;t l&ecirc;n vẻ sang trọng đậm chất Apple, iPhone SE 2020 cho bạn cảm gi&aacute;c th&acirc;n thuộc, thoải m&aacute;i hơn bao giờ hết.</p>\r\n\r\n<p><img alt=\"thiết kế iPhone SE 2020\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-se-2020-2.jpg\" /></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh 4,7 inch Retina HD tuyệt đẹp</strong></h3>\r\n\r\n<p>Kh&ocirc;ng cần phải c&oacute; một m&agrave;n h&igrave;nh &ldquo;qu&aacute; khổ&rdquo;, iPhone SE 2020 vẫn tỏa s&aacute;ng với m&agrave;n h&igrave;nh 4,7 inch Retina HD tuyệt đẹp. Với c&ocirc;ng nghệ IPS LCD, h&igrave;nh ảnh hiển thị trong trẻo, m&agrave;u sắc ch&iacute;nh x&aacute;c v&agrave; độ s&aacute;ng cực cao, đ&aacute;p ứng tốt mọi nhu cầu sử dụng. M&agrave;n h&igrave;nh iPhone SE 2020 c&ograve;n hỗ trợ&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/cong-nghe-man-hinh-true-tone-la-gi-56537\">c&ocirc;ng nghệ True Tone</a>, gi&uacute;p m&agrave;u sắc c&oacute; khả năng thay đổi theo m&ocirc;i trường để bạn lu&ocirc;n được tận hưởng m&agrave;n h&igrave;nh tự nhi&ecirc;n v&agrave; dịu mắt nhất. Một m&agrave;n h&igrave;nh đẹp, sẵn s&agrave;ng cho những trải nghiệm nhưng lại kh&ocirc;ng qu&aacute; lớn để ảnh hưởng đến cảm gi&aacute;c cầm nắm, đ&oacute; ch&iacute;nh l&agrave; iPhone SE 2020.</p>\r\n\r\n<p><img alt=\"màn hình iPhone SE 2020\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-se-2020-6.jpg\" /></p>\r\n\r\n<h3><strong>Sức mạnh đ&aacute;ng kinh ngạc</strong></h3>\r\n\r\n<p>Điểm ấn tượng nhất tr&ecirc;n iPhone SE 2020 ch&iacute;nh l&agrave; sức mạnh cấu h&igrave;nh. Apple đ&atilde; trang bị cho iPhone SE mới bộ vi xử l&yacute; A13 Bionic, con chip mạnh mẽ nhất thế giới&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>&nbsp;hiện nay v&agrave; cũng ch&iacute;nh l&agrave; bộ n&atilde;o đang c&oacute; mặt tr&ecirc;n chiếc iPhone 11 Pro cao cấp. Nhờ A13 Bionic, chiếc iPhone SE 2020 nhỏ gọn của ch&uacute;ng ta sở hữu tốc độ đ&aacute;ng kinh ngạc. Bạn c&oacute; thể mở ứng dụng, chơi game, chỉnh sửa video hay bất cứ t&aacute;c vụ n&agrave;o, iPhone SE 2020 đều đ&aacute;p ứng ho&agrave;n hảo. Cấu h&igrave;nh của iPhone SE 2020 đủ để m&aacute;y chạy mượt trong nhiều năm tới, ph&ugrave; hợp cho nhu cầu sử dụng l&acirc;u d&agrave;i.</p>\r\n\r\n<p><img alt=\"cấu hình iPhone SE 2020\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-se-2020-7.jpg\" /></p>\r\n\r\n<h3><strong>Chụp ảnh ch&acirc;n dung x&oacute;a ph&ocirc;ng chuy&ecirc;n nghiệp</strong></h3>\r\n\r\n<p>Hệ thống camera ti&ecirc;n tiết kết hợp với bộ vi xử l&yacute; A13 Bionic xử l&yacute; h&igrave;nh ảnh mạnh mẽ gi&uacute;p iPhone SE 2020 c&oacute; khả năng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tim-hieu-ve-cong-nghe-chup-xoa-phong-la-gi-63770\">chụp ảnh x&oacute;a ph&ocirc;ng</a>&nbsp;kinh ngạc. Đặc biệt, bạn c&oacute; thể sử dụng t&iacute;nh năng ảnh chụp x&oacute;a ph&ocirc;ng ở cả camera trước v&agrave; sau. Hiệu ứng l&agrave;m mờ ch&iacute;nh x&aacute;c, khả năng điều chỉnh độ mờ theo &yacute; muốn kết hợp c&ugrave;ng 6 t&ugrave;y chọn &aacute;nh s&aacute;ng chuẩn studio, mang đến cho bạn những bức h&igrave;nh x&oacute;a ph&ocirc;ng &ldquo;sống ảo&rdquo; đầy nghệ thuật v&agrave; s&aacute;ng tạo.</p>\r\n\r\n<p><img alt=\"xóa phông iPhone SE 2020\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-se-2020-5.jpg\" /></p>\r\n\r\n<h3><strong>Thỏa sức chụp ảnh trong mọi điều kiện &aacute;nh s&aacute;ng</strong></h3>\r\n\r\n<p>Ngược s&aacute;ng hay thiếu s&aacute;ng sẽ kh&ocirc;ng c&ograve;n l&agrave; vấn đề với iPhone SE 2020. T&iacute;nh năng Smart HDR thế hệ mới nhất sẽ xử l&yacute; th&ocirc;ng minh mọi điều kiện &aacute;nh s&aacute;ng, nhận diện khu&ocirc;n mặt để gi&uacute;p l&agrave;n da bạn lu&ocirc;n đẹp tự nhi&ecirc;n d&ugrave; l&agrave; chụp trong c&aacute;c điều kiện &aacute;nh s&aacute;ng phức tạp. C&aacute;c hiệu ứng b&oacute;ng đổ, độ chi tiết sắc n&eacute;t sẽ gi&uacute;p ảnh chụp từ iPhone SE 2020 trở n&ecirc;n thực sự ấn tượng.</p>\r\n\r\n<p><img alt=\"chụp ảnh iPhone SE 2020\" src=\"https://images.fpt.shop/unsafe/fit-in/filters:quality(90)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/iphone-se-2020-9.jpg\" /></p>', '10490000', '9800000', '637274741787675449_iphone-se-black-select-20206.jpg', 'iphone,apple,dien-thoai', 1, NULL, NULL);
INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_quantity`, `product_sold`, `product_desc`, `product_content`, `product_price`, `price_cost`, `product_image`, `product_tags`, `product_status`, `created_at`, `updated_at`) VALUES
(31, 3, 3, 'AirPods Pro Apple MWP22', '200', 0, 'Tai nghe AirPods Pro sạc không dây Apple MWP22', '<h3>Thiết kế nhỏ gọn, dễ d&agrave;ng đem theo bất cứ đ&acirc;u</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tai-nghe/tai-nghe-bluetooth-airpods-pro-apple-mwp22\" target=\"_blank\" title=\"Tham khảo AirPods Pro tại Điện Máy XANH\">AirPods Pro</a>&nbsp;sở hữu thiết kế nhỏ gọn, sang trọng v&agrave; tinh tế, được thiết kế theo dạng&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo tai nghe tại Điện Máy XANH\">tai nghe</a>&nbsp;In-ear thay v&igrave; Earbuds như&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2\" target=\"_blank\" title=\"Tham khảo AirPods 2 tại Điện Máy XANH\">AirPods 2</a>, cho ph&eacute;p tối đa h&oacute;a khả năng&nbsp;<strong>chống ồn, kh&oacute; rớt khi đeo v&agrave; kh&ocirc;ng bị đau tai</strong>.</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.dienmayxanh.com/kinh-nghiem-hay/tim-hieu-ve-tai-nghe-in-ear-tai-nghe-earbuds-chung-1219762\" target=\"_blank\" title=\"Tìm hiểu về tai nghe In-ear, tai nghe Earbuds. Chúng có gì khác nhau?\">T&igrave;m hiểu về tai nghe In-ear, tai nghe Earbuds. Ch&uacute;ng c&oacute; g&igrave; kh&aacute;c nhau?</a></p>\r\n\r\n<p><img alt=\"Thiết kế nhỏ gọn, tinh tế - Tai nghe Bluetooth Airpods Pro Apple\" height=\"500\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22.jpg\" title=\"Thiết kế nhỏ gọn, tinh tế - Tai nghe Bluetooth Airpods Pro Apple\" width=\"800\" /></p>\r\n\r\n<h3>Kết nối nhanh v&agrave; ổn định với c&ocirc;ng nghệ Bletooth 5.0</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth kinh doanh tại Điện Máy XANH\">Tai nghe Bluetooth</a>&nbsp;AirPods Pro với&nbsp;<strong>c&ocirc;ng nghệ Bletooth 5.0</strong>&nbsp;cho&nbsp;tốc độ kết nối ấn tượng, với c&aacute;c thiết bị của Apple như: iPhone, iPad, MacBook,... chỉ&nbsp;<strong>khoảng&nbsp;1 - 2 gi&acirc;y</strong>&nbsp;sau khi mở nắp.</p>\r\n\r\n<p><img alt=\"Kết nối nhanh chóng - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang2.jpg\" title=\"Kết nối nhanh chóng - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3>Linh hoạt lựa chọn với 3 k&iacute;ch cỡ đệm tai k&egrave;m theo</h3>\r\n\r\n<p>Do chuyển sang dạng In-ear, n&ecirc;n&nbsp;&nbsp;AirPods Pro c&oacute;&nbsp;<strong>k&egrave;m theo miếng đệm tai cao su với 3 k&iacute;ch cỡ kh&aacute;c nhau</strong>, đem lại trải nghiệm ho&agrave;n hảo hơn cho người d&ugrave;ng v&agrave;&nbsp;gi&uacute;p tai nghe b&aacute;m chắc v&agrave;o tai người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"Đi kèm theo 3 cặp đệm tai - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang3.jpg\" title=\"Đi kèm theo 3 cặp đệm tai - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3>&Acirc;m thanh chất lượng, sống động c&ugrave;ng c&ocirc;ng nghệ Adaptive EQ tự&nbsp;điều chỉnh &acirc;m thanh th&ocirc;ng minh</h3>\r\n\r\n<p>AirPods Pro được đầu tư mạnh mẽ ở chất lượng &acirc;m thanh,&nbsp;&acirc;m&nbsp;bass s&acirc;u hơn, chắc khoẻ hơn v&agrave; chi tiết hơn, c&ugrave;ng c&ocirc;ng nghệ Adaptive EQ, cho ph&eacute;p<strong>&nbsp;tự động điều chỉnh tần số &acirc;m thanh&nbsp;</strong>200 lần mỗi gi&acirc;y&nbsp;để tối ưu h&oacute;a &acirc;m thanh cho cấu tr&uacute;c tai, mang lại trải nghiệm &acirc;m thanh tốt nhất.</p>\r\n\r\n<p><img alt=\"Công nghệ Adaptive EQ tùy chỉnh âm tùy theo người dùng - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trangfixx4.jpg\" title=\"Công nghệ Adaptive EQ tùy chỉnh âm tùy theo người dùng - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3><a href=\"https://www.dienmayxanh.com/tai-nghe-khong-day\" target=\"_blank\" title=\"Tham khảo thêm tai nghe True Wireless tại Điện Máy XANH\">Tai nghe True Wireless</a>&nbsp;AirPods Pro trang bị 2 chế độ: Transparency Mode (xuy&ecirc;n &acirc;m) v&agrave; Noise Cancellation Mode (chống ồn chủ động)</h3>\r\n\r\n<p><strong>- Chống ồn chủ động:</strong>&nbsp;Trong chế độ n&agrave;y, tai nghe sẽ loại bỏ tạp &acirc;m xung quanh v&agrave;&nbsp;tiếng ồn ngo&agrave;i m&ocirc;i trường, gi&uacute;p bạn đắm ch&igrave;m v&agrave;o bản nhạc m&agrave; kh&ocirc;ng bị ảnh hưởng bởi tiếng ồn xung quanh.</p>\r\n\r\n<p><strong>- Chế độ xuy&ecirc;n &acirc;m:&nbsp;</strong>Với chế độ n&agrave;y, AirPods Pro gi&uacute;p bạn vừa c&oacute; thể nghe nhạc vừa nghe thấy những &acirc;m thanh xung quanh, gi&uacute;p bạn dễ d&agrave;ng hơn khi tr&ograve; chuyện với mọi người trong khi vẫn đeo tai nghe.</p>\r\n\r\n<p><img alt=\"Trang bị chống ồn và chế độ xuyên âm - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-6.jpg\" title=\"Trang bị chống ồn và chế độ xuyên âm - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3>Điều khiển dễ d&agrave;ng, nhanh ch&oacute;ng với cảm ứng lực hiện đại</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tai-nghe-apple-bluetooth\" target=\"_blank\" title=\"Tham khảo thêm tai nghe Apple tại Điện Máy XANH\">Tai nghe Bluetooth Apple</a>&nbsp;AirPods Pro&nbsp;sử dụng cảm biến lực để điều khiển trực tiếp, kh&ocirc;ng cần thao t&aacute;c nhiều tr&ecirc;n điện thoại, cụ thể như sau:</p>\r\n\r\n<p><strong>- B&oacute;p 1 lần:</strong>&nbsp;Ph&aacute;t/tạm dừng nhạc hoặc trả lời cuộc gọi.</p>\r\n\r\n<p><strong>- B&oacute;p 2 lần:</strong>&nbsp;Chuyển b&agrave;i kế tiếp.</p>\r\n\r\n<p><strong>- B&oacute;p 3 lần:</strong>&nbsp;Quay lại b&agrave;i trước.</p>\r\n\r\n<p><strong>- Nhấn giữ:</strong>&nbsp;Chuyển đổi giữa chế độ chống ồn chủ động v&agrave; chế độ xuy&ecirc;n &acirc;m.</p>\r\n\r\n<p>Ngo&agrave;i ra, tai nghe&nbsp;AirPods Pro c&ograve;n c&oacute; t&iacute;nh năng &ldquo;<strong>Hey Siri</strong>&rdquo; gi&uacute;p kiểm so&aacute;t &acirc;m lượng, cuộc gọi, nghe nhạc v&agrave; nhiều hơn thế nữa chỉ bằng giọng n&oacute;i (chỉ hỗ trợ giọng n&oacute;i Tiếng Anh).</p>\r\n\r\n<p><img alt=\"Cảm ứng lực dễ dàng thao tác, hiện đại - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-7.jpg\" title=\"Cảm ứng lực dễ dàng thao tác, hiện đại - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3>T&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y chuẩn Qi cho hộp sạc</h3>\r\n\r\n<p>Chuẩn&nbsp;<a href=\"https://www.dienmayxanh.com/cap-dien-thoai-sac-khong-day\" target=\"_blank\" title=\"Tham khảo sạc không dây tại Điện Máy XANH\">sạc kh&ocirc;ng d&acirc;y</a>&nbsp;Qi được trang bị tr&ecirc;n&nbsp;hộp sạc AirPods Pro&nbsp;tương th&iacute;ch hầu hết c&aacute;c loại đế sạc kh&ocirc;ng d&acirc;y hiện nay.</p>\r\n\r\n<p><img alt=\"Hỗ trợ sạc không dây chuẩn Qi - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang7.jpg\" title=\"Hỗ trợ sạc không dây chuẩn Qi - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, tr&ecirc;n hộp sạc c&ograve;n được trang bị một cổng&nbsp;<a href=\"https://www.dienmayxanh.com/cap-dien-thoai-cap-iphone-lightning\" target=\"_blank\" title=\"Tham khảo cáp lightning tại Điện Máy XANH\">Lightning</a>&nbsp;v&agrave;&nbsp;<a href=\"https://www.dienmayxanh.com/cap-dien-thoai-type-c-lightning\" target=\"_blank\" title=\"Tham khảo USB-C To Lightning tại Điện Máy XANH\">c&aacute;p USB-C To Lightning</a>&nbsp;k&egrave;m theo bộ sản phẩm giống như c&aacute;p sạc đi k&egrave;m với&nbsp;iPhone 11 Pro/11 Pro Max.</p>\r\n\r\n<p><img alt=\"Hỗ trợ sạc qua cổng Lightning - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang8.jpg\" title=\"Hỗ trợ sạc qua cổng Lightning - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3>Thời lượng pin cho ph&eacute;p nghe nhạc đến 4.5 giờ, đ&agrave;m thoại đến 3.5 giờ</h3>\r\n\r\n<p>Khi sử dụng t&iacute;nh năng khử tiếng ồn chủ động, thời gian nghe nhạc l&agrave; 4.5 giờ v&agrave; thời gian đ&agrave;m thoại l&agrave; 3.5 giờ li&ecirc;n tục, c&ograve;n<strong>&nbsp;</strong>nếu tắt chống ồn, c&oacute; thể nghe nhạc đến 5 giờ v&agrave; thời gian đ&agrave;m thoại đến 4.5 giờ.</p>\r\n\r\n<p>Khi d&ugrave;ng tai nghe AirPods Pro với hộp sạc th&igrave; người d&ugrave;ng sẽ c&oacute; thời gian&nbsp;<strong>24 giờ nghe nhạc v&agrave; 18 giờ đ&agrave;m thoại</strong>&nbsp;li&ecirc;n tục khi được sạc đầy.</p>\r\n\r\n<p><img alt=\"Có thể nghe nhạc liên tục trong 4.5 giờ và 3.5 giờ đàm thoại - Tai nghe Bluetooth Airpods Pro Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/213711/tai-nghe-bluetooth-airpods-pro-apple-mwp22-trang9.jpg\" title=\"Có thể nghe nhạc liên tục trong 4.5 giờ và 3.5 giờ đàm thoại - Tai nghe Bluetooth Airpods Pro Apple\" /></p>\r\n\r\n<h3>Tiết kiệm thời gian với khả năng sạc nhanh</h3>\r\n\r\n<p>Với t&iacute;nh năng&nbsp;<strong>sạc 5 ph&uacute;t được 1 giờ nghe nhạc hoặc đ&agrave;m thoại</strong>, AirPods Pro gi&uacute;p cho bạn tiết kiệm thời gian v&agrave; an t&acirc;m hơn khi c&oacute; việc gấp.</p>', '5990000', '5000000', 'tai-nghe-bluetooth-airpods-pro-apple-mwp22-1-org86.jpg', 'apple,tai-nghe,airpod', 1, NULL, NULL),
(32, 3, 3, 'AirPods 2 Apple MV7N2', '200', 0, 'Tai nghe AirPods 2 Apple MV7N2', '<h3><a href=\"https://www.dienmayxanh.com/tai-nghe/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang\" target=\"_blank\" title=\"Xem chi tiết sản phẩm Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng\" type=\"Xem chi tiết sản phẩm Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng\">Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng</a>&nbsp;vẫn giữ cho m&igrave;nh thiết kế hiện đại, cao cấp, hợp thời trang.</h3>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng thiết kế hiện đại, cao cấp\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org1.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng thiết kế hiện đại, cao cấp\" /></p>\r\n\r\n<h3><a href=\"https://www.dienmayxanh.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo thêm tai nghe kinh doanh tại Điện Máy XANH\" type=\"Xem các sản phẩm tai nghe đang kinh doanh tại Thegioididong.com \">Tai nghe</a>&nbsp;AirPods 2 trang bị chip H1 gi&uacute;p tai nghe kết nối ổn định, nhanh ch&oacute;ng v&agrave; tiết kiệm pin hơn</h3>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/tin-tuc/chip-apple-h1-la-gi-no-giup-airpods-moi-loi-hai-hon-o-diem-nao--1165255\" target=\"_blank\" title=\"Tham khảo bài viết: Chip Apple H1 là gì? Nó giúp AirPods mới lợi hại hơn ở điểm nào?\">Chip Apple H1 l&agrave; g&igrave;? N&oacute; gi&uacute;p AirPods mới lợi hại hơn ở điểm n&agrave;o?</a><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng hỗ trợ chip H1 xử lý tốt hơn \" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org3.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng hỗ trợ chip H1 xử lý tốt hơn \" /></p>\r\n\r\n<h3>Hỗ trợ k&iacute;ch hoạt Siri bằng giọng n&oacute;i &quot;Hey, Siri&quot; v&agrave; thao t&aacute;c cảm ứng tiện lợi</h3>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo thêm tai nghe bluetooth tại Điện Máy XANH\" type=\"Tai nghe Buetooth giá rẻ đang được kinh doanh tại Thế Giới Di Động\">tai nghe Bluetooth</a>&nbsp;AirPods 2 c&ograve;n hỗ trợ nhận cuộc gọi, c&oacute; thể nghe hoặc tạm dừng đoạn nhạc đang ph&aacute;t tr&ecirc;n điện thoại bằng những thao t&aacute;c chạm cảm ứng tr&ecirc;n tai nghe, tiện lợi cho người d&ugrave;ng khi di chuyển.</p>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kích hoạt Siri bằng giọng nói \" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-2.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kích hoạt Siri bằng giọng nói \" /></p>\r\n\r\n<h3>Kết nối được hầu hết với c&aacute;c thiết bị Apple v&agrave; cả với c&aacute;c thiết bị hệ điều h&agrave;nh Android, Windows</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tai-nghe-apple\" target=\"_blank\" title=\"Tham khảo tai nghe Apple kinh doanh tại Điện Máy XANH\">Tai nghe Apple</a>&nbsp;AirPods 2 sử dụng chuẩn kết nối Bluetooth được tối ưu cho mọi thiết bị của h&atilde;ng như iPhone, iPad, iMac, Macbook, Appe Watch v&agrave; cả những thiết bị&nbsp;<a href=\"https://www.dienmayxanh.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Điện Máy XANH\">điện thoại</a>,&nbsp;<a href=\"https://www.dienmayxanh.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng tại Điện Máy XANH\">m&aacute;y t&iacute;nh bảng</a>&nbsp;chạy Android hay&nbsp;<a href=\"https://www.dienmayxanh.com/laptop\" target=\"_blank\" title=\"Tham khảo laptop kinh doanh tại Điện Máy XANH\">laptop</a>, m&aacute;y t&iacute;nh Windows.</p>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kết nối với hầu hết các thiết bị Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-23.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng kết nối với hầu hết các thiết bị Apple\" /></p>\r\n\r\n<h3>Trang bị c&ocirc;ng nghệ khử tiếng ồn cuộc gọi, đảm bảo chất lượng cuộc đ&agrave;m thoại tốt nhất</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tai-nghe-apple-bluetooth\" target=\"_blank\" title=\"Tai nghe Buetooth Apple chính hãng tại Điện Máy XANH\" type=\"Tai nghe Buetooth Apple chính hãng tại Điện Máy XANH\">Tai nghe Bluetoot Apple</a>&nbsp;AirPods 2 trang bị t&iacute;nh năng khử ồn để tạo điểm nhấn kh&aacute;c biệt so với chiếc AirPods thế hệ đầu, nhằm cho ra chất &acirc;m tuyệt vời v&agrave; cải thiện khoảng c&aacute;ch kết nối giữa c&aacute;c thiết bị.</p>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng khử tiếng ồn\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-org-25.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng khử tiếng ồn\" /></p>\r\n\r\n<h3>Nghe nhạc l&ecirc;n đến 3 giờ cho mỗi một lần sạc đầy tai nghe v&agrave; c&oacute; thể sử dụng hơn 24 giờ khi sạc nhiều lần với hộp sạc</h3>\r\n\r\n<p>Đặc biệt, sạc đầy nhanh chỉ trong 15 ph&uacute;t sẽ c&oacute; thể nghe sạc đến 3 giờ (ở mức &acirc;m lượng 50%) nhờ c&ocirc;ng nghệ sạc nhanh t&iacute;ch hợp tr&ecirc;n&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe-khong-da\" target=\"_blank\" title=\"Tham khảo thêm tai nghe True Wireless tại Điện Máy XANH\">tai nghe True Wireless</a>.</p>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng sử dụng pin dài lâu\" src=\"https://cdn.tgdd.vn/Products/Images/54/200785/tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-15.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Apple MV7N2 Trắng sử dụng pin dài lâu\" /></p>', '3990000', '3450000', 'tai-nghe-bluetooth-airpods-2-apple-mv7n2-trang-1-1-org79.jpg', 'airpod,apple,tai-nghe', 1, NULL, NULL),
(33, 3, 3, 'AirPods 2 sạc không dây Apple MRXJ2', '200', 0, 'Tai nghe AirPods 2 sạc không dây Apple MRXJ2', '<h3>Thiết kế nhỏ gọn, ấn tượng người nh&igrave;n</h3>\r\n\r\n<p><a href=\"https://www.dienmayxanh.com/tai-nghe/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2\" target=\"_blank\" title=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng chính hãng, giá rẻ, bán tại Điện Máy Xanh\" type=\"Xem chi tiết sản phẩm Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng\">Tai nghe Bluetooth AirPods 2 Wireless charge</a>&nbsp;sở hữu thiết kế thời trang v&agrave; nhỏ gọn, trọng lượng cũng rất nhẹ&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tai nghe Bluetooth chính hãng, giá rẻ, bán tại Điện Máy XANH\" type=\"Các sản phẩm tai nghe Bluetooth đang được kinh doanh tại Thế Giới Di Động\">tai nghe Bluetooth</a>&nbsp;c&ograve;n được phủ l&ecirc;n một lớp chất liệu mới ở đầu mỗi tai nghe để gi&uacute;p tai nghe b&aacute;m tr&ecirc;n tai người đeo hơn, tr&aacute;nh bị rơi rớt hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<figure class=\"easyimage easyimage-full\"><img alt=\"\" src=\"data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsQAAA7EAZUrDhsAAAANSURBVBhXYzh8+PB/AAffA0nNPuCLAAAAAElFTkSuQmCC\" width=\"1\" />\r\n<figcaption></figcaption>\r\n</figure>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Truy cập nhanh v&agrave;o Siri bằng c&aacute;ch n&oacute;i &quot;Hey Siri&quot;</h3>\r\n\r\n<p>Với sự trở lại của AirPods 2 lần&nbsp;n&agrave;y th&igrave;&nbsp;khả năng chống ồn, xử l&yacute; &acirc;m thanh b&ecirc;n ngo&agrave;i tốt hơn so với tai nghe AirPods đời đầu v&agrave; bạn c&oacute; thể kết nối với&nbsp;nhanh v&agrave;o Siri bằng c&aacute;ch n&oacute;i&nbsp;&ldquo;Hey Siri&rdquo; sau đ&oacute; thực hiện y&ecirc;u cầu của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng dễ dàng kết nối với Siri\" src=\"https://cdn.tgdd.vn/Products/Images/54/200779/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-8-1.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng dễ dàng kết nối với Siri\" /></p>\r\n\r\n<h3>Dễ d&agrave;ng thao t&aacute;c bằng cảm ứng để&nbsp;nhận cuộc gọi, nghe hoặc tạm dừng đoạn nhạc, tiện lợi khi kh&ocirc;ng cần thao t&aacute;c nhiều tr&ecirc;n&nbsp;<a href=\"https://www.dienmayxanh.com/dtdd\" target=\"_blank\" title=\"Điện thoại chính hãng, giá rẻ, bán tại Điện Máy Xanh\">điện thoại</a>, cho bạn rảnh tay hơn</h3>\r\n\r\n<h3><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng thao tác dễ dàng\" src=\"https://cdn.tgdd.vn/Products/Images/54/200779/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-9-1.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng thao tác dễ dàng\" /></h3>\r\n\r\n<h3>C&oacute; thể sạc pin cho&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe\" target=\"_blank\" title=\"Tai nghe chính hãng, giá rẻ, bán tại Điện Máy XANH\" type=\"Tai nghe chính hãng, giá rẻ, bán tại Điện Máy Xanh\">tai nghe</a>&nbsp;dễ d&agrave;ng qua cổng sạc&nbsp;<a href=\"https://www.dienmayxanh.com/cap-dien-thoai-cap-iphone-lightning\" target=\"_blank\" title=\"Cáp Lighning chính hãng, giá rẻ, bán tại Điện Máy XANH\" type=\"Cáp Lighning chính hãng, giá rẻ, bán tại Điện Máy Xanh\">Lightning</a>&nbsp;hoặc c&oacute; thể sạc kh&ocirc;ng d&acirc;y nhờ được trang bị chuẩn sạc Qi hiện đại</h3>\r\n\r\n<h3><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng được sạc pin thông qua cổng Lightning\" src=\"https://cdn.tgdd.vn/Products/Images/54/200779/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-7-1.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng được sạc pin thông qua cổng Lightning\" /><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thể sạc không dây\" src=\"https://cdn.tgdd.vn/Products/Images/54/200779/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-6-1.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thể sạc không dây\" /></h3>\r\n\r\n<h3>Bạn c&oacute; thể d&ugrave;ng&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe-nhet-trong\" target=\"_blank\" title=\"Tai nghe nhét trong chính hãng, giá rẻ, bán tại Điện Máy XANH\">tai nghe nh&eacute;t trong</a>&nbsp;để nghe nhạc l&ecirc;n đến 5 giờ với &acirc;m lượng l&agrave; 50% cho mỗi một lần sạc đầy</h3>\r\n\r\n<p><img alt=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thời lượng nghe nhạc lớn\" src=\"https://cdn.tgdd.vn/Products/Images/54/200779/tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-3-2.jpg\" title=\"Tai nghe Bluetooth AirPods 2 Wireless charge Apple MRXJ2 Trắng có thời lượng nghe nhạc lớn\" /></p>\r\n\r\n<h3>Khi sử dụng song song với hộp sạc c&oacute; thể d&ugrave;ng&nbsp;<a href=\"https://www.dienmayxanh.com/tai-nghe-khong-day\" target=\"_blank\" title=\"Tham khảo thêm tai nghe không dây tại Điện Máy XANH \">tai nghe kh&ocirc;ng d&acirc;y</a>&nbsp;được l&ecirc;n đến 24 giờ m&agrave; kh&ocirc;ng lo hết pin</h3>', '4990000', '4300000', 'tai-nghe-bluetooth-airpods-2-wireless-charge-mrxj2-1-1-org64.jpg', 'airpod,apple,tai-nghe', 1, NULL, NULL),
(34, 3, 3, 'Tai nghe nhét trong EarPods Lightning', '200', 0, 'Tai nghe nhét trong EarPods Lightning Apple MMTN2', '<h3><a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-earpods-cong-lightning-apple-mmtn2\" target=\"_blank\" title=\"Tai nghe nhét trong cổng Lightning EarPods Apple MMTN2 kinh doanh tại Thế Giới Di Động\">Tai nghe EarPods cổng Lightning Apple MMTN2</a>&nbsp;thiết kế trẻ trung với m&agrave;u trắng sang trọng, tinh tế</h3>\r\n\r\n<p><img alt=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Thiết kế hiện đại, hợp thời trang\" src=\"https://cdn.tgdd.vn/Products/Images/54/88053/tai-nghe-earpods-cong-lightning-apple-mmtn2-6.jpg\" title=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Thiết kế hiện đại, hợp thời trang\" /></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-co-day\" target=\"_blank\" title=\"Tham khảo tai nghe có dây tại Thế Giới Di Động\">Tai nghe c&oacute; d&acirc;y</a>&nbsp;d&agrave;i 1.2 m, dễ d&agrave;ng kết nối v&agrave; nghe nhạc với&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;bỏ trong t&uacute;i hoặc balo</h3>\r\n\r\n<p><img alt=\"Tai nghe nhét trong EarPods Lightning Apple MMTN2 có chiều dài dây 1.2 m\" src=\"https://cdn.tgdd.vn/Products/Images/54/88053/tai-nghe-earpods-cong-lightning-apple-mmtn2-fix.jpg\" title=\"Tai nghe nhét trong EarPods Lightning Apple MMTN2 có chiều dài dây 1.2 m\" /></p>\r\n\r\n<h3>Thiết kế đầu&nbsp;<a href=\"http://www.thegioididong.com/tai-nghe-nhet-trong\" target=\"_blank\" title=\"Tai nghe nhét trong giá rẻ tại Thế Giới Di Động\">tai nghe&nbsp;nh&eacute;t trong</a>&nbsp;vừa vặn với tai, tạo cảm gi&aacute;c &ecirc;m &aacute;i, dễ chịu khi đeo</h3>\r\n\r\n<p><img alt=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Thiết kế vừa vặn với tai\" src=\"https://cdn.tgdd.vn/Products/Images/54/88053/tai-nghe-earpods-cong-lightning-apple-mmtn2-7.jpg\" title=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Thiết kế vừa vặn với tai\" /></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-apple\" target=\"_blank\" title=\"Tham khảo tai nghe Apple kinh doanh tại Thế Giới Di Động\">Tai nghe Apple</a>&nbsp;cho chất &acirc;m trong trẻo, &acirc;m bass tốt, độ chi tiết cao</h3>\r\n\r\n<p><img alt=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Đường truyền âm thanh ổn định, chi tiết\" src=\"https://cdn.tgdd.vn/Products/Images/54/88053/tai-nghe-earpods-cong-lightning-apple-mmtn2-9.jpg\" title=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Đường truyền âm thanh ổn định, chi tiết\" /></p>\r\n\r\n<h3>Tương th&iacute;ch với&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Máy tính bảng các thương hiệu kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Điện thoại di động kinh doanh tại Thế Giới Di Động\">điện thoại</a>&nbsp;Apple (<a href=\"http://www.thegioididong.com/dtdd-apple-iphone\" target=\"_blank\" title=\"Tham khảo iPhone kinh doanh tại Thế Giới Di Động\">iPhone</a>&nbsp;5,&nbsp;<a href=\"http://www.thegioididong.com/may-tinh-bang-apple-ipad\" target=\"_blank\" title=\"Tham khảo iPad kinh doanh tại Thế Giới Di Động\">iPad</a>&nbsp;4 trở l&ecirc;n)</h3>\r\n\r\n<p><img alt=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Tai nghe tương thích với máy tính bảng, điện thoại Apple\" src=\"https://cdn.tgdd.vn/Products/Images/54/88053/tai-nghe-earpods-cong-lightning-apple-mmtn2-10.jpg\" title=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Tai nghe tương thích với máy tính bảng, điện thoại Apple\" /></p>\r\n\r\n<h3>T&iacute;ch hợp c&aacute;c ph&iacute;m chức năng tiện &iacute;ch như: Tăng/giảm &acirc;m lượng, dừng/ph&aacute;t nhạc, nghe/nhận cuộc gọi</h3>\r\n\r\n<h3><img alt=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Tích hợp phím bấm trên tai nghe\" src=\"https://cdn.tgdd.vn/Products/Images/54/88053/tai-nghe-earpods-cong-lightning-apple-mmtn2-12.jpg\" title=\"Tai nghe EarPods cổng Lightning Apple MMTN2 - Tích hợp phím bấm trên tai nghe\" /></h3>', '790000', '640000', 'tai-nghe-earpods-cong-lightning-apple-mmtn2-org-151.jpg', 'tai-nghe,apple,tai-nghe-day', 1, NULL, NULL),
(35, 4, 4, 'Samsung Galaxy A71', '200', 0, 'Samsung Galaxy A71', '<h2><strong>Samsung A71 &ndash; Smartphone&nbsp;tầm trung của Samsung</strong></h2>\r\n\r\n<p><em><strong>Samsung Galaxy A71</strong>&nbsp;sẽ l&agrave; điện thoại gi&aacute; cả phải chăng của Samsung với mục đ&iacute;ch tiếp cận đối tượng rộng hơn. Samsung A71 l&agrave; sản phẩm thuộc series&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-a.html\" target=\"_blank\" title=\"Điện thoại SamSung Galaxy A chính hãng\">Samsung Galaxy A</a>&nbsp;với m&agrave;n h&igrave;nh đục lỗ, cấu h&igrave;nh mạnh mẽ, cụm bốn camera sau chất lượng cao v&agrave; c&ugrave;ng nhiều c&ocirc;ng nghệ thời thượng.</em></p>\r\n\r\n<p>Sắp tới đ&acirc;y, Samsung sẽ cho giới thiệu bộ ba flagship thuộc d&ograve;ng Galaxy S mới bao gồm S21, S21 Plus v&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s21-ultra.html\">Samsung S21 Ultra</a>, ph&acirc;n kh&uacute;c smartphone cao cấp&nbsp;hứa hẹn sẽ v&ocirc; c&ugrave;ng hấp dẫn.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh đục lỗ 6,7 inch, Full HD với tấm nền&nbsp;</strong><strong>Super AMOLED</strong></h3>\r\n\r\n<p>Galaxy A71 sở hữu m&agrave;n h&igrave;nh&nbsp;Super AMOLED&nbsp;Plus 6,7 inch. C&oacute; độ ph&acirc;n giải Full HD 1080 x 2400 pixel, 393ppi. M&aacute;y cũng được trang bị đầu đọc dấu v&acirc;n tay dưới m&agrave;n h&igrave;nh giống như nhiều mẫu smartphone của Samsung đ&atilde; ra mắt gần đ&acirc;y với tốc độ nhận diện rất nhanh. C&ugrave;ng với đ&oacute; l&agrave; chức năng mở kh&oacute;a bằng nhận diện khu&ocirc;n mặt.</p>\r\n\r\n<p><img alt=\"Màn hình Super AMOLED 6,7 inch Full HD với tỉ lệ khung hình 20:9\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a71-6.jpg\" /></p>\r\n\r\n<p>Samsung Galaxy A71 được trang bị m&agrave;n h&igrave;nh Infinity-O cho tỉ lệ m&agrave;n h&igrave;nh đạt được tối đa, gi&uacute;p tăng kh&ocirc;ng gian sử dụng cho người d&ugrave;ng. Với tỉ lệ khung h&igrave;nh 20:9 cho người d&ugrave;ng những trải nghiệm h&igrave;nh ảnh sống động v&agrave; sắc n&eacute;t khi xem phim hoặc chơi game. M&aacute;y cũng được trang bị&nbsp;k&iacute;nh cường lực Gorilla Glass&nbsp;3 cho khả năng chịu lực cũng như chống va đập tốt hơn.&nbsp;<strong>Samsung A71</strong>&nbsp;sẽ được b&aacute;n ra với 4 phi&ecirc;n bản m&agrave;u sắc: đen, trắng, hồng, xanh.</p>\r\n\r\n<p><img alt=\"Galaxy A71 sẽ được bán với 4 phiên bản màu sắc\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a71-2.jpg\" /></p>\r\n\r\n<h3><strong>Bộ bốn camera sau với cảm biến ch&iacute;nh 64MP</strong></h3>\r\n\r\n<p><em><strong>Galaxy A71</strong></em>&nbsp;sở hữu camera selfie c&oacute; độ ph&acirc;n giải khủng 32MP c&ugrave;ng đ&egrave;n LED Flash tự động trợ s&aacute;ng hỗ trợ selfie trong điều kiện thiếu s&aacute;ng. Với nhiều t&iacute;nh năng hữu &iacute;ch như:&nbsp;Sticker hay&nbsp;AR Emoji, hỗ trợ tuyệt vời cho nhu cầu selfie của người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"Camera trước của Samsung Galaxy A71 có độ phân giải khủng 32MP\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a71-4.jpg\" /></p>\r\n\r\n<p>Samsung A71 được trang bị bộ bốn camera ở mặt sau với camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 64MP v&agrave; camera g&oacute;c si&ecirc;u rộng c&oacute; độ ph&acirc;n giải 12MP, ống k&iacute;nh cảm biến độ s&acirc;u 5MP v&agrave; cảm biến Macro 5MP. Với c&aacute;c t&iacute;nh năng như Zoom kỹ thuật số, chạm để lấy n&eacute;t, Samsung Galaxy A71 sẽ mang đến cho bạn những bức ảnh chuy&ecirc;n nghiệp nhất với chất lượng cao.</p>\r\n\r\n<p><img alt=\"Bộ bốn camera sau với camera chính 64MP\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a71-3.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, Galaxy A71 c&ograve;n cho khả năng quay video chất lượng cao với Super Steady Video.</p>', '7700000', '7000000', 'a71_bac_347.jpg', 'samsung,dien-thoai,galaxy', 1, NULL, NULL),
(36, 4, 4, 'Samsung Galaxy Note 20 Ultra', '200', 0, 'Samsung Galaxy Note 20 Ultra', '<h2>Samsung Note 20 Ultra: Thiết kế sang trọng v&agrave; nhiều c&ocirc;ng nghệ cực tốt</h2>\r\n\r\n<p>Samsung l&agrave; g&atilde; khổng lồ c&ocirc;ng nghệ cực kỳ nổi tiếng đến từ đất nước H&agrave;n Quốc, mỗi chiếc điện thoại của h&atilde;ng&nbsp;đều mang thiết kế hiện đại, sang trọng đi k&egrave;m với đa dạng c&ocirc;ng nghệ cực kỳ nổi bật.&nbsp;<strong>Note 20 Ultra</strong>&nbsp;l&agrave; một trong những chiếc smartphone nổi tiếng v&agrave; đang được nhiều người quan t&acirc;m, đ&oacute;n nhận. Samsung hứa hẹn sẽ chiều l&ograve;ng kh&aacute;ch h&agrave;ng với thiết kế lộng lẫy, c&ugrave;ng với v&ocirc; v&agrave;ng c&ocirc;ng nghệ, chip xử l&yacute; mới mẻ, thật hiện đại. Ngo&agrave;i ra, bạn cũng c&oacute; thể tham khảo th&ecirc;m Note 20 Ultra 5G.</p>\r\n\r\n<p>V&agrave; nhiều khả năng cuối năm nay, Samsung sẽ cho ra mắt bộ ba smartphone thuộc d&ograve;ng S mới bao gồm Galaxy S21, S21 Plus v&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s21-ultra.html\">Samsung S21 Ultra</a>, sớm hơn so với mọi năm nhằm cạnh tranh với d&ograve;ng iPhone 12 series của Apple.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh AMOLED cực sống động k&egrave;m với hệ điều h&agrave;nh Android, One UI 2.1</strong></h3>\r\n\r\n<p>Điều đầu ti&ecirc;n m&agrave; chắc hẳn nhiều người sẽ th&iacute;ch th&uacute; ở chiếc điện thoại Samsung Galaxy Note 20 Ultra đ&oacute; ch&iacute;nh l&agrave; sự trang bị m&agrave;n h&igrave;nh AMOLED. Với Dynamic AMOLED được trang bị l&ecirc;n đến 16M colors, k&iacute;ch thước m&agrave;n h&igrave;nh l&ecirc;n đến 6.9 inches v&agrave; độ ph&acirc;n giải Quad-HD+. Tạo ra mọi m&agrave;u sắc ch&acirc;n thật nhất, sống động v&agrave; sắc n&eacute;t cho người d&ugrave;ng trải nghiệm th&iacute;ch th&uacute;.&nbsp;</p>\r\n\r\n<p><img alt=\"Màn hình AMOLED cực sống động kèm với hệ điều hành Android, One UI 2.1\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/galaxy-note-20-ultra3.jpg\" /></p>\r\n\r\n<p>M&aacute;y được c&agrave;i sẵn hệ điều h&agrave;nh Android. Đ&acirc;y dường như l&agrave; hệ điều h&agrave;nh th&ocirc;ng dụng v&agrave; được nhiều người sử dụng quen tay nhất. Đi k&egrave;m với đ&oacute; l&agrave; skin One UI 2.1 dựa tr&ecirc;n Android 10, tạo n&ecirc;n nhiều chức năng, c&ocirc;ng cụ hiện đại c&oacute; tr&ecirc;n chiếc&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-note.html\" target=\"_blank\">Samsung Galaxy Note</a>&nbsp;n&agrave;y.&nbsp;</p>\r\n\r\n<h3><strong>Trang bị chip Exynos 990 cực kỳ hiện đại c&ugrave;ng với cấu h&igrave;nh CPU nổi bật</strong></h3>\r\n\r\n<p>Điều khiến mọi người bất ngờ nhất tr&ecirc;n&nbsp;<strong>Samsung&nbsp;Note 20 Ultra</strong>&nbsp;đ&oacute; l&agrave; chip Exynos 990, m&agrave; Samsung đ&atilde; nghi&ecirc;n cứu v&agrave; &aacute;p dụng ngay tr&ecirc;n điện thoại n&agrave;y. Đ&acirc;y dường như l&agrave; con chip mạnh mẽ v&agrave; hiện đại bậc nhất, khiến người d&ugrave;ng ho&agrave;n to&agrave;n h&agrave;i l&ograve;ng. B&ecirc;n cạnh đ&oacute; l&agrave; kh&ocirc;ng c&ograve;n sự hổ thẹn đến từ ph&iacute;a Samsung.</p>\r\n\r\n<p><img alt=\"Trang bị chip Exynos 992 cực kỳ hiện đại cùng với cấu hình CPU nổi bật\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/galaxy-note-20-ultra-1.jpg\" /></p>\r\n\r\n<p>Cấu h&igrave;nh CPU của chiếc điện thoại Samsung Galaxy Note 20 Ultra cũng ho&agrave;n to&agrave;n nổi bật, khiến người d&ugrave;ng th&iacute;ch th&uacute;. Với trang bị CPU 2x 2.84 GHz Dual-core (Cortex-A78), 2x 2.84 GHz (Cortex-X1), 4x 1.8 GHz Quad-core (Cortex-A55) v&agrave; Mali-G78 GPU hứa hẹn sẽ mang lại sự mượt m&agrave; cực ổn định trong l&uacute;c chơi game, giải tr&iacute;.&nbsp;</p>\r\n\r\n<h3><strong>Trang bị 3 camera sau cuốn h&uacute;t c&ugrave;ng với camera trước selfie cực kỳ sắc n&eacute;t</strong></h3>\r\n\r\n<p>Đương nhi&ecirc;n tr&ecirc;n Note 20 Ultra chắc chắn sẽ kh&ocirc;ng thể thiếu ba camera bắt kịp xu hướng. Với sự cuốn h&uacute;t từ camera cảm biến ch&iacute;nh lấy n&eacute;t bằng laser độ ph&acirc;n giải l&ecirc;n đến 108MP. Tiếp theo l&agrave; camera si&ecirc;u rộng 12MP v&agrave; camera telephoto 12MP. Sự trang bị cực kỳ đầy đủ n&agrave;y hứa hẹn gi&uacute;p cho người d&ugrave;ng thỏa m&atilde;n hết niềm đam m&ecirc; chụp ảnh của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"Trang bị 3 camera sau cuốn hút cùng với camera trước selfie cực kỳ sắc nét\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/_samsung-galaxy-note-20-ultra-3.jpg\" /></p>\r\n\r\n<p>Tiếp theo ở mặt trước l&agrave; camera selfie cực kỳ sắc n&eacute;t. Với sự trang bị camera trước d&ugrave;ng để selfie c&oacute; độ ph&acirc;n giải cực kỳ tốt l&ecirc;n đến 40MP, hứa hẹn tạo n&ecirc;n những khung h&igrave;nh cực ho&agrave;n hảo. Bạn sẽ thoải m&aacute;i với cả t&iacute;nh năng camera sau v&agrave; trước đều chất lượng, mang c&ocirc;ng nghệ hiện đại bắt nhịp xu thế.</p>\r\n\r\n<h3><strong>Khả năng quay video cực kỳ ổn định c&ugrave;ng với bộ nhớ dung lượng cực khủng</strong></h3>\r\n\r\n<p>Ngo&agrave;i khả năng tạo ra những khung h&igrave;nh chất lượng tuyệt mỹ th&igrave;&nbsp;<strong>Note 20 Ultra</strong>&nbsp;c&ograve;n khiến người d&ugrave;ng kinh ngạc với chất lượng quay video. Video được tạo ra từ camera trước c&oacute; l&ecirc;n đến 4K, tạo ra sự sắc n&eacute;t trong những video th&uacute; vị. Đối với camera sau, khả năng quay video của n&oacute; l&ecirc;n đến 8K c&ugrave;ng với v&ocirc; v&agrave;ng c&ocirc;ng nghệ hiện đại. Thật sự bạn sẽ cho&aacute;ng ngợp với độ trang bị ho&agrave;n hảo n&agrave;y.&nbsp;</p>\r\n\r\n<p><img alt=\"Khả năng quay video cực kỳ ổn định cùng với bộ nhớ dung lượng cực khủng\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/_samsung-galaxy-note-20-ultra-4.jpg\" /></p>', '21690000', '19900000', 'black_final73.jpg', 'samsung,galaxy,dien-thoai', 1, NULL, NULL),
(37, 4, 4, 'Samsung Galaxy A12', '200', 0, 'Samsung Galaxy A12', '<h2><strong>Điện thoại Samsung Galaxy A12 &ndash; Smartphone gi&aacute; rẻ cấu h&igrave;nh tốt v&agrave; pin tr&acirc;u</strong></h2>\r\n\r\n<p><strong>Samsung Galaxy A12</strong>&nbsp;l&agrave; một chiếc smartphone gi&aacute; rẻ nhưng sở hữu cấu h&igrave;nh ổn định c&ugrave;ng với vi&ecirc;n pin 5000mAh cho thời lượng suốt ng&agrave;y d&agrave;i. B&ecirc;n cạnh đ&oacute; điện thoại Samsung Galaxy A12 cũng sở hữu thiết kế thời trang v&agrave; ph&ugrave; hợp với xu hướng.</p>\r\n\r\n<h3><strong>Thiết kế đơn giản, 4 phi&ecirc;n bản m&agrave;u, v&acirc;n tay cạnh b&ecirc;n</strong></h3>\r\n\r\n<p>Samsung Galaxy A12 c&oacute; thiết kế đơn giản với bốn g&oacute;c cạnh được bo tr&ograve;n mềm mại c&ugrave;ng với hai cạnh b&ecirc;n được v&aacute;t cong nhẹ nh&agrave;ng tạo cảm gi&aacute;c cầm thoải m&aacute;i cho người d&ugrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; smartphone c&ograve;n được cho ra mắt với nhiều phi&ecirc;n bản m&agrave;u Đen, Trắng, Xanh thời trang để bạn c&oacute; thể lựa chọn m&agrave;u sắc y&ecirc;u th&iacute;ch.</p>\r\n\r\n<p><img alt=\"Thiết kế đơn giản, 4 phiên bản màu và cảm biến vân tay tích hợp ở mặt lưng\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-1_1.jpg\" /></p>\r\n\r\n<p>Samsung Galaxy A12 mặc d&ugrave; c&oacute; thiết kế nguy&ecirc;n khối v&agrave; chỉ sử dụng chất liệu nhựa nhưng lại được ho&agrave;n thiện v&ocirc; c&ugrave;ng tỉ mỉ v&agrave; ch&iacute;nh x&aacute;c n&ecirc;n vẫn mang đến cảm gi&aacute;c cao cấp khi chạm.&nbsp;M&aacute;y được hỗ trợ v&acirc;n tay cạnh b&ecirc;n gi&uacute;p người d&ugrave;ng mở kh&oacute;a điện thoại nhanh ch&oacute;ng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh giọt nước&nbsp; 6.5 inch v&agrave; tấm nền TFT LCD t&aacute;i tạo m&agrave;u tốt</strong></h3>\r\n\r\n<p>Mặc d&ugrave; l&agrave; smartphone gi&aacute; rẻ nhưng Samsung Galaxy A12 vẫn sở hữu thiết kế m&agrave;n h&igrave;nh bắt kịp với xu hướng với m&agrave;n h&igrave;nh giọt nước v&agrave; được bo tr&ograve;n theo kiểu d&aacute;ng của smartphone. M&agrave;n h&igrave;nh 6.5 inch tr&agrave;n viền cho người d&ugrave;ng g&oacute;c nh&igrave;n rộng, thoải m&aacute;i tận hưởng kh&ocirc;ng gian hiển thị.</p>\r\n\r\n<p><img alt=\"Màn hình đục lỗ 6.4 inch và tấm nền TFT LCD tái tạo màu tốt\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-2_1.jpg\" /></p>\r\n\r\n<p>Sử dụng tấm nền TFT LCD thay v&igrave; c&aacute;c loại m&agrave;n h&igrave;nh LCD th&ocirc;ng thường mang đến khả năng t&aacute;i tạo m&agrave;u tốt hơn, cho h&igrave;nh ảnh hiển thị c&oacute; m&agrave;u sắc ch&acirc;n thực v&agrave; sống động.</p>\r\n\r\n<p>Smartphone c&ograve;n c&oacute; độ ph&acirc;n giải HD+ mặc d&ugrave; chưa thực sự qu&aacute; xuất sắc nhưng tr&ecirc;n một thiết bị gi&aacute; rẻ th&igrave; m&agrave;n h&igrave;nh Samsung Galaxy A12 đ&atilde; đạt chất lượng rất tốt.</p>\r\n\r\n<h3><strong>Hệ thống 4 camera sau với ống k&iacute;nh g&oacute;c si&ecirc;u rộng v&agrave; camera trước 8MP</strong></h3>\r\n\r\n<p>Samsung Galaxy A12 cũng mang đến cho người d&ugrave;ng khả năng chụp ảnh ấn tượng khi trang bị 4 camera sau với camera ch&iacute;nh 48MP g&oacute;c rộng, camera g&oacute;c si&ecirc;u rộng 5MP v&agrave; camera x&oacute;a ph&ocirc;ng 2MP c&ugrave;ng camera cận cảnh 2MP.</p>\r\n\r\n<p>M&aacute;y hỗ trợ đầy đủ c&aacute;c t&iacute;nh năng chụp ảnh th&ocirc;ng minh như lấy n&eacute;t tự động, chống rung,&hellip;đồng thời hỗ trợ quay phim với chất lượng 1080p sắc n&eacute;t để bạn c&oacute; thể quay lại mọi khoảnh khắc đ&aacute;ng nhớ, chụp lại c&aacute;c t&agrave;i liệu quan trọng một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><img alt=\"Hệ thống 3 camera sau với ống kính góc siêu rộng và camera trước 8MP\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-3_1.jpg\" /></p>\r\n\r\n<p>Ngo&agrave;i ra Samsung Galaxy A12 c&ograve;n sở hữu camera trước 8MP f/2.0 gi&uacute;p bạn thực hiện video call chất lượng cao, chụp selfie sắc n&eacute;t v&agrave; xinh đẹp.</p>\r\n\r\n<h3><strong>Chipset Helio G35 v&agrave; RAM 4GB cho sức mạnh ấn tượng</strong></h3>\r\n\r\n<p>Sức mạnh của&nbsp;<strong>Samsung A12</strong>&nbsp;kh&ocirc;ng hề thua k&eacute;m bất kỳ d&ograve;ng smartphone tầm trung n&agrave;o hiện nay nhờ chipset Helio G35 v&agrave; RAM 4GB cho tốc độ xử l&yacute; nhanh ch&oacute;ng mọi thao t&aacute;c t&aacute;c vụ, chạy đa nhiệm mượt m&agrave;, dễ d&agrave;ng chuyển đổi qua lại giữa c&aacute;c đa nhiệm, chơi game tốt.</p>\r\n\r\n<p><img alt=\"Chipset Snapdragon 450 và RAM 3GB cho sức mạnh ấn tượng\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-4_1.jpg\" /></p>\r\n\r\n<h3><strong>Pin dung lượng 5000mAh, hỗ trợ sạc nhanh 15W</strong></h3>\r\n\r\n<p>Samsung Galaxy A12 đ&aacute;p ứng cho người d&ugrave;ng thời gian sử dụng l&acirc;u d&agrave;i nhờ được trang bị vi&ecirc;n pin dung lượng 5000mAh bền bỉ. Bạn c&oacute; thể thoải m&aacute;i sử dụng smartphone m&agrave; kh&ocirc;ng lo hết pin li&ecirc;n tục suốt ng&agrave;y với thời lượng bền bỉ.</p>\r\n\r\n<p><img alt=\"Pin dung lượng 4000mAh cho thời lượng bền bỉ\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-a12-5_1.jpg\" /></p>', '4000000', '3600000', 'samsung-galaxy-a12_2_9.jpg', 'samsung,galaxy,dien-thoai', 1, NULL, NULL),
(38, 4, 4, 'Samsung Galaxy Note 10+', '200', 0, 'Samsung Galaxy Note 10+', '<h2><strong>Samsung Note 10 Plus &ndash; M&agrave;n h&igrave;nh lớn cho trải nghiệm tuyệt đỉnh, S-Pen tiện dụng</strong></h2>\r\n\r\n<p><em>L&agrave; phi&ecirc;n bản n&acirc;ng cấp đ&aacute;ng gi&aacute; ra đời c&ugrave;ng thời điểm với Samsung Galaxy Note 10,&nbsp;<strong>Samsung Galaxy Note 10 Plus&nbsp;</strong>l&agrave; sự lựa chọn tuyệt vời cho người d&ugrave;ng đam m&ecirc; c&ocirc;ng nghệ c&oacute; nhu cầu sở hữu một chiếc điện thoại ho&agrave;n hảo về mọi mặt. Với Note 10 Plus, Samsung<strong>&nbsp;</strong>đ&atilde; thật sự mang đến một chiếc điện thoại c&oacute; sức mạnh vượt trội, đ&aacute;p ứng to&agrave;n diện c&aacute;c nhu cầu về l&agrave;m việc v&agrave; giải tr&iacute; của người d&ugrave;ng. Ngo&agrave;i ra, bạn c&oacute; thể tham khảo th&ecirc;m&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-note-20.html\" target=\"_blank\">Galaxy Note 20</a>&nbsp;sắp được ra mắt.</em></p>\r\n\r\n<h3><strong>Samsung Note 10 Plus c&oacute; thiết kế vu&ocirc;ng vắn, chất liệu kim loại kết hợp thủy tinh cao cấp</strong></h3>\r\n\r\n<p>Galaxy Note 10 Plus tạo n&ecirc;n ấn tượng đặc biệt từ ban đầu bởi vẻ ngo&agrave;i đẳng cấp. Kế thừa trọn vẹn tinh hoa thiết kế của d&ograve;ng sản phẩm&nbsp;<a href=\"https://cellphones.com.vn/mobile/samsung/galaxy-note.html\" target=\"_blank\" title=\"Điện thoại SamSung Galaxy Note chính hãng\">Galaxy Note</a>&nbsp;nh&agrave; Samsung, Note 10 Plus c&oacute; ngoại h&igrave;nh vu&ocirc;ng vứt đầy mạnh mẽ v&agrave; tinh tế. Viền m&agrave;n h&igrave;nh cực kỳ mỏng c&ugrave;ng với độ d&agrave;y mảnh mai kh&ocirc;ng k&eacute;m, gi&uacute;p&nbsp;<strong>Note 10 Plus</strong>&nbsp;d&ugrave;ng c&oacute; k&iacute;ch thước lớn nhưng vẫn giữ được sự thanh tho&aacute;t, sang trọng.</p>\r\n\r\n<p><img alt=\"Thiết kế vuông vứt hoàn hảo với chất liệu kim loại kết hợp thủy tinh cao cấp\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-Galaxy-Note-10-Plus-1.jpg\" /></p>\r\n\r\n<p>Chất liệu được Samsung lựa chọn để l&agrave;m n&ecirc;n Note 10 Plus tạo ra sự tin tưởng về độ an to&agrave;n lẫn thẩm mỹ ngay từ lần chạm đầu ti&ecirc;n. Phi&ecirc;n bản n&acirc;ng cấp của Samsung Galaxy Note 10 sở hữu khung m&aacute;y ho&agrave;n to&agrave;n từ kim loại, mặt lưng c&oacute; sự kết hợp với thủy tinh cao cấp với khả năng chuyển m&agrave;u ấn tượng tạo n&ecirc;n c&aacute;c phi&ecirc;n bản m&agrave;u sắc v&ocirc; c&ugrave;ng bắt mắt v&agrave; sang trọng.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh AMOLED&nbsp;Infinity-O 6.8 inch si&ecirc;u ấn tượng</strong></h3>\r\n\r\n<p>Samsung Galaxy Note 10 Plus sở hữu m&agrave;n h&igrave;nh được xem l&agrave; lớn nhất thị trường hiện tại với th&ocirc;ng số ch&iacute;nh thức được c&ocirc;ng bố l&ecirc;n đến 6.8 inch. M&agrave;n h&igrave;nh n&agrave;y sử dụng c&ocirc;ng nghệ AMOLED Infinity-O với độ ph&acirc;n giải 3.040 x 1.440 pixels cho ph&eacute;p điện thoại hiển thị những khung m&agrave;u cực kỳ ấn tượng, ch&acirc;n thực v&agrave; sắc n&eacute;t với g&oacute;c nh&igrave;n gần như to&agrave;n vẹn nhất.</p>\r\n\r\n<p><img alt=\"Màn hình AMOLED Infinity-O 6.8 inch siêu ấn tượng\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-Galaxy-Note-10-Plus-2.jpg\" /></p>\r\n\r\n<p>Hướng đến một chiếc điện th&acirc;n thiết với người d&ugrave;ng, Samsung Galaxy Note 10 Plus sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh&nbsp;Dynamic AMOLED&nbsp;ti&ecirc;n tiến cho ra m&agrave;n h&igrave;nh &iacute;t &aacute;nh s&aacute;ng xanh hơn nhằm gi&uacute;p mắt thoải m&aacute;i. C&ugrave;ng với đ&oacute;, m&agrave;n h&igrave;nh hỗ trợ cả nội dung HDR10+ v&igrave; thế c&aacute;c chương tr&igrave;nh phim ảnh, game v&agrave; c&aacute;c nội dung kh&aacute;c được hiển thị rất đẹp.</p>\r\n\r\n<h3><strong>Galaxy Note 10 Plus c&oacute; hiệu năng vượt trội c&ugrave;ng vi xử l&yacute; Exynos 9825</strong></h3>\r\n\r\n<p><strong>Samsung Note 10 Plus</strong>&nbsp;c&oacute; hiệu năng v&ocirc; c&ugrave;ng mạnh mẽ, xứng tầm chiếc flagship cao cấp nhất năm 2019 của Samsung. Cụ thể, Note 10 Plus được trang bị vi xử l&yacute; Exynos 9825 8 nh&acirc;n 64-bit kết hợp với 12 GB RAM v&agrave; 256 GB bộ nhớ trong.</p>\r\n\r\n<p><img alt=\"Hiệu năng vượt trội cùng vi xử lý Exynos 9825\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-Galaxy-Note-10-Plus-3.jpg\" /></p>\r\n\r\n<p>Exynos 9825 l&agrave; d&ograve;ng chip mạnh mẽ nhất tới từ Samsung. Chip Exynos 9825 l&agrave; phi&ecirc;n bản n&acirc;ng cấp của Exynos 9820 8 nm, Exynos 9825 được x&acirc;y dựng tr&ecirc;n quy tr&igrave;nh khắc tia cực t&iacute;m EUV 7 nm. Con chip n&agrave;y cho ph&eacute;p Samsung Galaxy Note 10 Plus c&oacute; khả năng xử l&yacute; mọi t&aacute;c vụ một c&aacute;ch nhanh ch&oacute;ng, khả năng choi c&aacute;c game &ldquo;nặng&rdquo; v&agrave; c&aacute;c thao t&aacute;c đa nhiệm cực mượt m&agrave;.</p>\r\n\r\n<h3><strong>Camera tốt nhất thế giới theo đ&aacute;nh gi&aacute; của chuy&ecirc;n trang DxOmark</strong></h3>\r\n\r\n<p>Camera l&agrave; điểm nhấn nổi bật của&nbsp;<em><strong>Galaxy Note 10 Plus</strong></em>. Điện thoại sở hữu camera ch&iacute;nh gồm 3 ống k&iacute;nh, trong đ&oacute;, camera ch&iacute;nh 12 MP với khẩu độ c&oacute; thể thay đổi từ F/1.5 &ndash; F/2.4, hỗ trợ chống rung quang học OIS v&agrave; tự động lấy n&eacute;t dual-pixel. C&ugrave;ng với đ&oacute;, 2 ống k&iacute;nh c&ograve;n lại l&agrave; camera g&oacute;c si&ecirc;u rộng 16 MP khẩu độ F/2.2 c&ugrave;ng ống k&iacute;nh tele 12 MP khẩu độ F/2.1. Ngo&agrave;i ra, Note 10 Plus cũng c&oacute; th&ecirc;m 1 cảm biến 3D ToF, được xem l&agrave; một n&acirc;ng cấp so với Galaxy Note 10.</p>\r\n\r\n<p><img alt=\"Camera tốt nhất thế giới theo đánh giá của chuyên trang DxOmark\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/Samsung-Galaxy-Note-10-Plus-4.jpg\" /></p>\r\n\r\n<p>Camera trước của Samsung Galaxy Note 10 Plus dạng camera đơn. Ống k&iacute;nh n&agrave;y c&oacute; độ ph&acirc;n giải 10 MP được Samsung ho&agrave;n thiện tốt v&agrave; trang bị bị nhiều c&ocirc;ng nghệ chụp ảnh hiện đại, gi&uacute;p người d&ugrave;ng c&oacute; những tấm ảnh selfie xuất sắc. Theo chuy&ecirc;n trang DxOmark, hệ thống camera của Note 10 Plus được đ&aacute;nh giả l&agrave; tốt nhất thế giới ở thời điểm hiện tại.</p>\r\n\r\n<h3><strong>B&uacute;t S-Pen si&ecirc;u tiện &iacute;ch cho trải nghiệm đến từ tương lai</strong></h3>\r\n\r\n<p>B&uacute;t S-Pen &ndash; Linh hồn của d&ograve;ng Galaxy Note được n&acirc;ng cấp ho&agrave;n hảo ở phi&ecirc;n bản đi k&egrave;m với&nbsp;<em>Note 10 Plus</em>. Về cơ bản, S-Pen tr&ecirc;n Samsung Note 10 Plus vẫn thừa hưởng nhiều t&iacute;nh năng hơn b&uacute;t cảm ứng stylus th&ocirc;ng thường, chiếc b&uacute;t n&agrave;y c&oacute; khả năng gi&uacute;p người d&ugrave;ng ghi ch&uacute; nhanh bằng c&aacute;ch viết tr&ecirc;n m&agrave;n h&igrave;nh kh&oacute;a, đến chụp ảnh từ xa với S-Pen.</p>', '16300000', '15100000', 'note_10_plus_xanh99.jpg', 'samsung,note,dien-thoai', 1, NULL, NULL);
INSERT INTO `tbl_product` (`product_id`, `category_id`, `brand_id`, `product_name`, `product_quantity`, `product_sold`, `product_desc`, `product_content`, `product_price`, `price_cost`, `product_image`, `product_tags`, `product_status`, `created_at`, `updated_at`) VALUES
(39, 4, 4, 'Samsung Galaxy Note 10 Lite', '200', 0, 'Note 10 Lite', '<h2>Samsung Note 10 Lite - Phi&ecirc;n bản nhỏ gọn, đa năng của si&ecirc;u ph&acirc;̉m Note 10</h2>\r\n\r\n<p><em><em><strong>Galaxy Note 10 Lite</strong>&nbsp;(ra mắt c&ugrave;ng với Galaxy S10 Lite) l&agrave; phi&ecirc;n bản nhỏ gọn hơn, gi&aacute; rẻ hơn bộ đ&ocirc;i si&ecirc;u phẩm&nbsp;Note 10&nbsp;/&nbsp;<em><a href=\"https://cellphones.com.vn/samsung-galaxy-note-10-plus.html\" target=\"_blank\">Note 10+</a>&nbsp;</em>nhưng v&acirc;̃n giữ nguy&ecirc;n những tinh hoa của người ti&ecirc;̀n nhi&ecirc;̣m nhưng với mức gi&aacute; hấp dẫn hơn. Mặc d&ugrave; điện thoại Samsung Note 20 sắp ra mắt nhưng đ&acirc;y vẫn l&agrave; sự lựa chọn hợp l&yacute; ở thời điểm hiện tại.&nbsp;</em></em></p>\r\n\r\n<h3>Samsung Note 10 Lite c&oacute; cấu h&igrave;nh như thế n&agrave;o</h3>\r\n\r\n<p>Trong thu&acirc;̣t ngữ c&ocirc;ng ngh&ecirc;̣, chữ &quot;lite&quot; đại di&ecirc;̣n cho &quot;light&quot;, nghĩa l&agrave; phi&ecirc;n bản nhẹ hơn, được thu nhỏ đ&ecirc;̉ v&acirc;̣n h&agrave;nh &ocirc;̉n định m&agrave; kh&ocirc;ng bị gi&acirc;̣t. V&ecirc;̀ cơ bản, b&acirc;́t kỳ thi&ecirc;́t bị n&agrave;o c&oacute; chữ &quot;<strong>Lite</strong>&quot; trong t&ecirc;n sản ph&acirc;̉m đ&ecirc;̀u l&agrave; phi&ecirc;n bản nhỏ hơn, v&acirc;̣n h&agrave;nh nhẹ hơn so với thi&ecirc;́t bị g&ocirc;́c được ra mắt trước đ&oacute;.</p>\r\n\r\n<p><img alt=\"Sự khác biệt giữa Galaxy Note 10 Lite và Note 10 thường\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-note10-lite-3.jpg\" /></p>\r\n\r\n<p><strong>Samsung Note 10 Lite</strong>&nbsp;ch&iacute;nh l&agrave; phi&ecirc;n bản được r&uacute;t gọn v&ecirc;̀ ngoại h&igrave;nh (chi&ecirc;̀u d&agrave;i, r&ocirc;̣ng) l&acirc;̃n c&acirc;́u h&igrave;nh (m&agrave;n h&igrave;nh, dung lượng lưu trữ, dung lượng pin,...). Ngo&agrave;i ra th&igrave; thi&ecirc;́t k&ecirc;́ đặc trưng của d&ograve;ng Note l&acirc;̃n c&aacute;c t&iacute;nh năng như b&uacute;t S-Pen hay bảo m&acirc;̣t sinh trắc học sẽ được giữ nguy&ecirc;n như tr&ecirc;n bản Note 10 g&ocirc;́c. Do đ&oacute; với những ai ưa th&iacute;ch thi&ecirc;́t k&ecirc;́ v&agrave; t&iacute;nh năng của Note 10 nhưng kh&ocirc;ng mu&ocirc;́n smartphone của m&igrave;nh trở n&ecirc;n to lớn v&agrave; c&ocirc;̀ng k&ecirc;̀nh th&igrave; đ&acirc;y sẽ l&agrave; lựa chọn ph&ugrave; hợp nh&acirc;́t.</p>\r\n\r\n<h3>Galaxy Note 10 Lite c&oacute; thi&ecirc;́t k&ecirc;́ vu&ocirc;ng vức đặc trưng của d&ograve;ng Note</h3>\r\n\r\n<p>Nh&igrave;n chung, phi&ecirc;n bản&nbsp;<strong>Galaxy Note 10 Lite</strong>&nbsp;sẽ c&oacute; sự thay đ&ocirc;̉i v&ecirc;̀ ngoại h&igrave;nh. K&iacute;ch thước t&ocirc;̉ng th&ecirc;̉ của thi&ecirc;́t bị sẽ được giảm xu&ocirc;́ng đ&ecirc;̉ tạo cảm gi&aacute;c nhẹ hơn, vừa tay c&acirc;̀m hơn. Ngo&agrave;i ra th&igrave; Samsung Galaxy Note v&acirc;̃n sẽ được k&ecirc;́ thừa thi&ecirc;́t k&ecirc;́ nguy&ecirc;n kh&ocirc;́i sang trọng với k&iacute;nh cường lực&nbsp;Gorilla Glass 6&nbsp;phủ hai mặt v&agrave; mang d&aacute;ng vẻ quy&ecirc;̉n s&ocirc;̉ đặc trưng m&agrave; &quot;người anh&quot; Galaxy Note 10 c&oacute; trước đ&oacute;.</p>\r\n\r\n<p><img alt=\"Thiết kế vuông vức, tạo hình quyển sổ đặc trưng của dòng Note\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-note10-lite-1.jpg\" /></p>\r\n\r\n<p><em><strong>Samsung Galaxy Note 10 Lite</strong></em>&nbsp;được cho l&agrave; sẽ ra mắt với hai m&agrave;u sắc: đỏ v&agrave; đen v&agrave; kh&ocirc;ng c&oacute; phi&ecirc;n bản m&agrave;u bạc &aacute;nh kim b&oacute;ng b&acirc;̉y như Galaxy Note 10. Vi&ecirc;̣c Samsung chỉ cho ra mắt hai phi&ecirc;n bản m&agrave;u c&oacute; th&ecirc;̉ sẽ gi&uacute;p người d&ugrave;ng d&ecirc;̃ d&agrave;ng lựa chọn hơn so với ba phi&ecirc;n bản m&agrave;u của Note 10.</p>\r\n\r\n<p><img alt=\"Nhiều phiên bản màu sắc\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-note10-lite-2.jpg\" /></p>\r\n\r\n<h3>Note 10 Lite c&oacute; m&agrave;n h&igrave;nh&nbsp;Infinity-O&nbsp;6.7 inch với t&acirc;́m n&ecirc;̀n&nbsp;Super AMOLED&nbsp;cho h&igrave;nh ảnh sắc n&eacute;t</h3>\r\n\r\n<p>M&ocirc;̣t y&ecirc;́u t&ocirc;́ c&acirc;́u h&igrave;nh nữa được r&uacute;t gọn tr&ecirc;n&nbsp;<strong><em>Note 10 Lite</em></strong>&nbsp;ch&iacute;nh l&agrave; thi&ecirc;́t k&ecirc;́ m&agrave;n h&igrave;nh cong. Đ&ecirc;̉ c&oacute; th&ecirc;̉ hạ gi&aacute; th&agrave;nh sản ph&acirc;̉m v&agrave; l&agrave;m cho d&ograve;ng Note trở n&ecirc;n đa dạng v&ecirc;̀ m&acirc;̃u m&atilde; hơn, Samsung sẽ quy&ecirc;́t định loại bỏ m&agrave;n h&igrave;nh cong v&agrave; mang v&ecirc;̀ m&agrave;n h&igrave;nh phẳng truy&ecirc;̀n th&ocirc;́ng cho chiếc điện thoại n&agrave;y. Với những người d&ugrave;ng cảm th&acirc;́y b&acirc;́t ti&ecirc;̣n với cạnh bo cong hai b&ecirc;n của Note 10, th&igrave; m&agrave;n h&igrave;nh tr&ecirc;n phi&ecirc;n bản Lite sẽ l&agrave;m trải nghi&ecirc;̣m sử dụng thoải m&aacute;i hơn.</p>\r\n\r\n<p><img alt=\"Màn hình phẳng cùng tấm nền Super AMOLED cho hình ảnh sắc nét\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-note10-lite-7.jpg\" /></p>\r\n\r\n<p><strong>Note 10 Lite</strong>&nbsp;được trang bị t&acirc;́m n&ecirc;̀n m&agrave;u Super AMOLED đặc trưng, mang lại m&agrave;u sắc trung thực v&agrave; hi&ecirc;̉n thị đẹp mắt kh&ocirc;ng thua k&eacute;m g&igrave; 2 người tiền nhiệm.</p>\r\n\r\n<h3>Samsung Note 10 Lite chạy con chip Exynos 9810 c&ugrave;ng 6GB RAM gi&uacute;p v&acirc;̣n h&agrave;nh mượt m&agrave;</h3>\r\n\r\n<p>Về cấu h&igrave;nh phần cứng&nbsp;<strong>Galaxy Note 10 Lite</strong>&nbsp;sẽ được trang bị chip&nbsp;Exynos 9810&nbsp;của Samsung. Th&ocirc;ng tin v&ecirc;̀ chip c&acirc;́u h&igrave;nh chưa được x&aacute;c nh&acirc;̣n nhưng nhi&ecirc;̀u khả năng v&acirc;̃n sẽ l&agrave; GPU Mali-G76 MP12 hoặc Adreno 640 tương tự như Note 10 g&ocirc;́c.</p>\r\n\r\n<p><img alt=\"Vi xử lý Exynos 9810 cùng 6GB RAM giúp vận hành mượt mà\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-note10-lite-6.jpg\" /></p>\r\n\r\n<p>Samsung&nbsp;<strong>Galaxy Note 10 Lite</strong>&nbsp;cũng sẽ được giảm dung lượng bộ nhớ trong xu&ocirc;́ng 128GB thay cho 256GB như Note 10. V&agrave; với 128GB bộ nhớ trong, smartphone chắc chắn sẽ c&acirc;̀n đ&ecirc;́n 6GB RAM đ&ecirc;̉ c&oacute; th&ecirc;̉ v&acirc;̣n h&agrave;nh mượt m&agrave; v&agrave; xử l&yacute; dữ li&ecirc;̣u nhanh ch&oacute;ng. M&aacute;y sẽ hoạt động tr&ecirc;n h&ecirc;̣ đi&ecirc;̀u h&agrave;nh&nbsp;Android 10&nbsp;với giao diện One UI 2.0 mới nhất hiện nay.&nbsp;Note 10 Lite được trang bị vi&ecirc;n pin 4500 mAh, hỗ trợ sạc nhanh 25W.</p>', '10500000', '9800000', '202642777.webp', 'samsung,note,dien-thoai', 1, NULL, NULL),
(40, 4, 4, 'Samsung Galaxy S20+ (Plus)', '200', 0, 'Samsung Galaxy S20+ (Plus)', '<h2><strong>Samsung S20 Plus - Flagship m&agrave;n h&igrave;nh lớn, cấu h&igrave;nh cao</strong></h2>\r\n\r\n<p><strong>Galaxy S20 Plus</strong>&nbsp;được biết l&agrave; phi&ecirc;n bản c&oacute; cấu h&igrave;nh mạnh hơn của Samsung S20 v&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s20-ultra.html\" target=\"_blank\" title=\"samsung galaxy s20 ultra\">Samsung S20 Ultra</a>.&nbsp;<em>Samsung S20 Plus</em>&nbsp;c&oacute; k&iacute;nh thước m&agrave;n h&igrave;nh lớn hơn v&agrave; cấu h&igrave;nh kh&aacute; tương đồng với người anh em Galaxy S20.</p>\r\n\r\n<p>Theo c&aacute;c tin đồn gần đ&acirc;y th&igrave; nhiều khả năng sắp tới đ&acirc;y, Samsung sẽ cho ra mắt bộ ba smartphone thuộc d&ograve;ng S mới bao gồm Galaxy S21, S21 Plus v&agrave; cao cấp nhất l&agrave;&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s21-ultra.html\" target=\"_blank\">điện thoại Samsung S21 Ultra</a>, sớm hơn so với mọi năm nhằm cạnh tranh với d&ograve;ng iPhone 12 series của Apple.</p>\r\n\r\n<h3><strong>Thiết kế Samsung S20 Plus với m&agrave;n h&igrave;nh đục lỗ, mặt lưng k&iacute;nh b&oacute;ng bẩy</strong></h3>\r\n\r\n<p><strong>Samsung Galaxy S20 Plus</strong>&nbsp;sở hữu thiết kế mặt trước tương tự c&aacute;c sản phẩm điện thoại Samsung Galaxy S20 Plus kh&aacute;c. Bạn vẫn c&oacute; m&agrave;n h&igrave;nh kh&ocirc;ng viền với hai cạnh b&ecirc;n cong tr&agrave;n. Tuy nhi&ecirc;n vị tr&iacute; đặt camera trước đ&atilde; chuyển ra trung t&acirc;m, điều n&agrave;y kh&aacute; giống Galaxy Note 10. Phần lỗ camera n&agrave;y cũng được l&agrave;m nhỏ hơn, kết hợp với phần viền si&ecirc;u mỏng n&ecirc;n&nbsp;<em>Samsung S20&nbsp;Plus</em>&nbsp;cho diện t&iacute;ch hiển thị gần như tuyệt đối. Gi&uacute;p tăng kh&ocirc;ng gian trải nghiệm cho người d&ugrave;ng.</p>\r\n\r\n<p><img alt=\"samsung-galaxy-s20-plus\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-plus-5.jpg\" /></p>\r\n\r\n<p>Mặt sau&nbsp;<strong>Galaxy S20 Plus</strong>&nbsp;g&acirc;y ấn tượng bởi modul camera h&igrave;nh chữ nhật, kh&aacute; giống với Samsung Galaxy A51 v&agrave; Galaxy A71 từng được ra mắt trước đ&oacute;. Samsung cũng đ&atilde; loại bỏ n&uacute;t Bixby tr&ecirc;n thiết kế Galaxy S20 Plus c&ugrave;ng jack tai nghe 3.5mm. N&uacute;t nguồn, tăng giảm &acirc;m lượng được thiết kế b&ecirc;n phải thiết bị.</p>\r\n\r\n<p>Hai cạnh b&ecirc;n của Samsung Galaxy S20 Plus vẫn sở hữu thiết kế quen thuộc với c&aacute;c g&oacute;c được cắt nhẹ cho khả năng cầm nắm tốt.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh Samsung S20 Plus c&oacute; k&iacute;ch thước 6.7 inch Super Amoled với tần số qu&eacute;t 120Hz</strong></h3>\r\n\r\n<p><strong>Samsung S20 Plus</strong>&nbsp;được trang bị m&agrave;n h&igrave;nh k&iacute;ch thước lớn l&ecirc;n đến 6.7 inch c&ugrave;ng tấm nền Super Amoled, độ ph&acirc;n giải Quad-HD+. C&ugrave;ng với m&agrave;n h&igrave;nh 120Hz cho độ nhạy tốt. B&ecirc;n cạnh đ&oacute;, Samsung cũng trang bị cho&nbsp;<em>Galaxy S20 Plus</em>&nbsp;tấm k&iacute;nh cường lực Gorilla 6.</p>\r\n\r\n<p><img alt=\"samsung-galaxy-s20-plus-cellpphones\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-plus-1.JPG\" /></p>\r\n\r\n<p>Ngo&agrave;i khả năng hiển thị tốt,&nbsp;<strong>S20 Plus</strong>&nbsp;c&ograve;n sở hữu c&ocirc;ng nghệ v&acirc;n tay &acirc;m m&agrave;n h&igrave;nh mới nhất của Samsung. C&ocirc;ng nghệ mở kh&oacute;a sinh trắc học n&agrave;y với thời gian xử l&yacute; nhanh ch&oacute;ng c&ugrave;ng sự bảo mật cao.</p>\r\n\r\n<h3><strong>Samsung S20 Plus trang bị cụm 4 camera sau, camera ch&iacute;nh 12MP&nbsp;</strong></h3>\r\n\r\n<p>Tương tự người anh em Galaxy S20,&nbsp;<strong><em>Samsung S20 Plus</em></strong>&nbsp;sở hữu cụm 4 camera tr&ecirc;n, đ&egrave;n flash LED tr&ecirc;n modul h&igrave;nh chữ nhật. Trong đ&oacute;, cảm biến ch&iacute;nh c&oacute; độ ph&acirc;n giải l&ecirc;n tới 12MP c&ugrave;ng khả năng chụp g&oacute;c rộng. C&ugrave;ng với đ&oacute; l&agrave; ống k&iacute;nh si&ecirc;u rộng 12MP, ống k&iacute;nh tele 64MP, cảm biến ToF 0.3MP. C&aacute;c cảm biến được xắp xếp đối xứng.</p>\r\n\r\n<p><img alt=\"samsung-s20-plus\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-plus-4.jpg\" /></p>\r\n\r\n<p>Camera tr&ecirc;n&nbsp;<strong>Samsung Galaxy S20 Plus</strong>&nbsp;c&oacute; khả năng hợp 9 pixel th&agrave;nh một gi&uacute;p cải thiện ảnh chụp trong điều kiện thiếu s&aacute;ng. Ngo&agrave;i ra&nbsp;<em>Galaxy S20 Plus</em>, &nbsp;cũng được trang bị những t&iacute;nh năng chụp th&ocirc;ng minh kh&aacute;c như: lấy n&eacute;t tự động bằng laser, chụp li&ecirc;n tục, kiểm so&aacute;t ISO, nhận diện khu&ocirc;n mặt,...</p>\r\n\r\n<p><img alt=\"galaxy-s20-plus\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-plus-3.jpg\" /></p>\r\n\r\n<p><strong>Samsung Galaxy S20 Plus</strong>&nbsp;sở hữu camera trước với độ ph&acirc;n giải 10MP, nằm trong phần nốt ruồi. Cảm biến 10MP cho khả năng chụp selfie ấn tưởng cho ra những bức ảnh nịnh mắt, độ chi tiết cao. Ngo&agrave;i khả năng chụp ảnh, Galaxy S20 Plus c&ograve;n cho khả năng quay video 8K.</p>\r\n\r\n<h3><strong>Hiệu năng mạnh mẽ với chip Exynos 990 c&ugrave;ng 8GB RAM</strong></h3>\r\n\r\n<p><strong>Samsung S20 Plus</strong>&nbsp;được trang bị chip Exynos 990 tương tự như điện thoại Galaxy S20 FE vừa mới ra mắt. Con chip do Samsung sản xuất v&agrave; tối ưu h&oacute;a ri&ecirc;ng cho điện thoại Samsung. Nhờ vậy,&nbsp;<em>Galaxy S20 Plus</em>&nbsp;cho hiệu năng xử l&yacute; tốt, thời gian load ứng dụng nhanh. C&ugrave;ng với đ&oacute; l&agrave; 8GB RAM mang lại khả năng đa nhiệm tốt. Kh&ocirc;ng gặp hiện tượng load lại khi mở nhiều ứng dụng.</p>\r\n\r\n<p><img alt=\"samsung-s20-plus-cellpphones\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-plus-2.JPG\" /></p>', '16500000', '15000000', '637170945536714482_ss-s20-plus-den-140.png', 'samsung,galaxy,ultra,dien-thoai', 1, NULL, NULL),
(41, 4, 4, 'Samsung Galaxy S20 Ultra', '200', 0, 'Samsung Galaxy S20 Ultra', '<h2><strong>Điện thoại Samsung S20 Ultra &ndash; Flagship thiết kế độc đ&aacute;o, cấu h&igrave;nh cao</strong></h2>\r\n\r\n<p><em><strong>Samsung Galaxy S20 Ultra</strong>&nbsp;l&agrave; flagship mới của d&ograve;ng Galaxy S sẽ được Samsung giới thiệu v&agrave;o đầu năm 2020. Đ&acirc;y l&agrave; phi&ecirc;n bản cao cấp nhất b&ecirc;n cạnh phi&ecirc;n bản thường v&agrave; bản Plus. Điện thoại sẽ được trang bị những t&iacute;nh năng tuyệt vời, dung lượng pin lớn, m&agrave;n h&igrave;nh được trang bị tần số qu&eacute;t 120Hz, camera ch&iacute;nh c&oacute; độ ph&acirc;n giải 108mp sẽ l&agrave; những t&iacute;nh năng nổi bật nhất.</em></p>\r\n\r\n<p><em>Ngo&agrave;i ra, bạn c&oacute; thể tham khảo th&ecirc;m th&ocirc;ng tin&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s21-ultra.html\" target=\"_blank\">điện thoại Samsung Galaxy S21 Ultra</a>&nbsp;mới nhất, sắp được l&ecirc;n kệ trong thời gian sắp tới.</em></p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh k&iacute;ch thước 6.9 inch với tần số qu&eacute;t 120Hz</strong></h3>\r\n\r\n<p>Về thiết kế, Samsung S20 Ultra c&oacute; thiết kế tương tự&nbsp;<a href=\"https://cellphones.com.vn/samsung-galaxy-s20.html\" target=\"_blank\">samsung S20</a>&nbsp;v&agrave; S20 Plus. Mặt trước m&aacute;y được trang bị m&agrave;n h&igrave;nh cong 6.9 inch với tấm nền&nbsp;AMOLED<a href=\"https://www.lifewire.com/super-amoled-4151073\" rel=\"nofollow\" target=\"_blank\" title=\"Tấm nền Super AMOLED\">&nbsp;</a>v&agrave; độ ph&acirc;n giải FullHD. C&aacute;c cạnh m&aacute;y được v&aacute;t nhẹ cho khả năng cầm nắm tốt. Phần camera trước của S20 Ultra sẽ được l&agrave;m theo dạng đục lỗ thường thấy tr&ecirc;n c&aacute;c mẫu smartphone Samsung gần đ&acirc;y. Ngo&agrave;i ra, m&agrave;n h&igrave;nh c&oacute; tần số qu&eacute;t l&ecirc;n đến 120Hz. Đ&acirc;y cũng l&agrave; mẫu smartphone đầu ti&ecirc;n được Samsung trang bị m&agrave;n h&igrave;nh c&oacute; tần số qu&eacute;t cao như vậy. Với mức tần số qu&eacute;t n&agrave;y, m&aacute;y sẽ cho độ nhạy cảm ứng cao hơn, c&aacute;c thao t&aacute;c vuốt chạm sẽ mượt m&agrave; v&agrave; ch&acirc;n thực hơn, độ trễ giữa c&aacute;c thao t&aacute;c l&agrave; rất &iacute;t. Ngo&agrave;i ra, bạn sẽ dễ d&agrave;ng cảm nhận sự thay đổi n&agrave;y khi chơi c&aacute;c game y&ecirc;u cầu đồ họa cao.</p>\r\n\r\n<p><img alt=\"Màn hình 6.9 inch với tần số quét 120Hz\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-ultra-1.JPG\" /></p>\r\n\r\n<p>Mặt sau của m&aacute;y được l&agrave;m bằng k&iacute;nh sang trọng. M&aacute;y cũng được trang bị c&ocirc;ng nghệ cảm biến v&acirc;n tay &acirc;m m&agrave;n h&igrave;nh.Cụm 4 camera v&agrave; đ&egrave;n led sẽ nằm b&ecirc;n ở g&oacute;c b&ecirc;n tr&aacute;i thay v&igrave; nằm ngang như tr&ecirc;n Samsung Galaxy S10 năm nay. Ngo&agrave;i ra, Galaxy S20 Ultra chỉ giữ lại cổng&nbsp;USB Type-C&nbsp;v&agrave; cụm loa ngo&agrave;i ở cạnh dưới của m&aacute;y. Cụm ph&iacute;m tăng giảm &acirc;m lượng v&agrave; ph&iacute;m trợ l&yacute; ảo Bixby được thiết kế b&ecirc;n tay phải thay v&igrave; b&ecirc;n tr&aacute;i như tr&ecirc;n Galaxy Note 10. M&aacute;y sẽ bị loại bỏ jack tai nghe 3.5mm.</p>\r\n\r\n<h3><strong>Camera ch&iacute;nh c&oacute; độ ph&acirc;n giải đến 108MP v&agrave; zoom quang 5X</strong></h3>\r\n\r\n<p><strong>Samsung S20 Ultra</strong>&nbsp;được trang bị cụm 4 camera sau lần lượt l&agrave; camera g&oacute;c rộng 108MP, camera g&oacute;c si&ecirc;u rộng 12MP, ống k&iacute;nh tele 48MP v&agrave; ToF 0.3MP. Điện thoại mang đến khả năng zoom quang học tới 10 lần. Nhờ đ&oacute; bạn c&oacute; thể ghi lại mọi khoảnh khắc. Trong điều kiện thiếu s&aacute;ng, m&aacute;y cũng cho ra chất lượng ảnh tốt. Với cơ chế chụp nhiều ảnh c&ugrave;ng l&uacute;c sau đ&oacute; hợp nhất, gi&uacute;p giảm độ mờ, nhiễu.</p>\r\n\r\n<p><img alt=\"cụm 4 camera sau\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-ultra-3_1.jpg\" /></p>\r\n\r\n<p>Camera selfie tr&ecirc;n Samsung Galaxy S20 Ultra l&agrave; ống k&iacute;nh 40MP mang lại cho bạn những bức ảnh selfie tuyệt vời nhất. B&ecirc;n cạnh đ&oacute;, m&aacute;y c&ograve;n khả năng quay video 8k cho những h&igrave;nh ảnh, chuyển động vẫn giữ được độ ph&acirc;n giải si&ecirc;u sắc n&eacute;t.</p>\r\n\r\n<p><img alt=\"camera trước 40MP\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-ultra-4.JPG\" /></p>\r\n\r\n<h3><strong>Hiệu năng ấn tượng với 12GB RAM c&ugrave;ng chip Exynos 990</strong></h3>\r\n\r\n<p><strong>Galaxy S20 Ultra</strong>&nbsp;sẽ được trang bị cấu h&igrave;nh mạnh nhất. M&aacute;y sẽ sử dụng chip Exynos 990. Đ&acirc;y l&agrave; con chip do SamSung sản xuất c&oacute; thể xứ l&yacute; mượt m&agrave; c&aacute;c game nặng v&agrave; mới nhất hiện nay. K&egrave;m theo đ&oacute; l&agrave; khả năng đ&aacute;p ứng đồ họa cao. M&aacute;y cũng được trang bị dung lượng RAM l&ecirc;n đến 12GB cho ph&eacute;p xử l&yacute; c&aacute;c t&aacute;c vụ đa nhiệm m&agrave; kh&ocirc;ng phải tải lại ứng dụng. Đ&acirc;y sẽ l&agrave; mức dung lượng RAM cao nhất tr&ecirc;n c&aacute;c d&ograve;ng điện thoại Samsung hiện nay.</p>\r\n\r\n<p><img alt=\"Phần cứng mạnh mẽ đi kèm hệ điều hành Android 10\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-ultra-2.jpg\" /></p>\r\n\r\n<p>V&agrave; với phi&ecirc;n bản dung lượng bộ nhớ trong cao nhất l&agrave; 512GB v&agrave; khe mở rộng thẻ nhớ cho ph&eacute;p bạn lưu trữ thoải m&aacute;i h&igrave;nh ảnh, video v&agrave; t&agrave;i liệu. Đi k&egrave;m với cấu h&igrave;nh phần cứng mạnh m&atilde; n&agrave;y l&agrave; hệ hiệu h&agrave;nh mới nhất Android 10 Q.&nbsp;</p>\r\n\r\n<h3><strong>Dung lượng pin được n&acirc;ng cấp đ&aacute;ng kể 5000mAh</strong></h3>\r\n\r\n<p>Điện thoại Samsung S20 Ultra<strong>&nbsp;</strong>được trang bị vi&ecirc;n pin Li-ion kh&ocirc;ng thể th&aacute;o rời với dung lượng lớn 5.00 mAh. Với mức dung lượng pin n&agrave;y m&aacute;y ho&agrave;n to&agrave;n c&oacute; thể đ&aacute;p ứng mọi nhu cầu của bạn trong suốt một ng&agrave;y.&nbsp;&nbsp;</p>\r\n\r\n<p><img alt=\"Dung lượng pin được nâng cấp đáng kể 4.500mAh\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/mobile/samsung/samsung-galaxy-s20-ultra-5.jpg\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, Samsung S20 Ultra c&ograve;n được trang bị c&ocirc;ng nghệ sạc nhanh 45W v&agrave; 15W khi sạc kh&ocirc;ng d&acirc;y</p>', '21190000', '19900000', '637170935875912528_ss-s20-ultra-den-133.png', 'samsung,galaxy,dien-thoai,ultra', 1, NULL, NULL);

